import React, { useRef, useState, useEffect, useMemo } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Text, Html } from '@react-three/drei';
import * as THREE from 'three';
import { 
  Box, Chip, Paper, Typography, Slider, Button, 
  Grid, Card, CardContent, Switch, FormControlLabel, Divider,
  Stack, LinearProgress, Tooltip, IconButton, Alert, Collapse,
  Badge, AvatarGroup, Avatar, ToggleButtonGroup, ToggleButton
} from '@mui/material';
import { 
  PlayArrow, Pause, Refresh, TrendingUp, Science,
  LocalFireDepartment, Thermostat, Speed, Warning, CheckCircle,
  Visibility, VisibilityOff, Info, ExpandMore, ExpandLess,
  Close, CrisisAlert, BubbleChart, Cloud, WarningAmber,
  BrokenImage // Замена CrackedIcon
} from '@mui/icons-material';

// Интерфейсы типов
interface MIREAColors {
  primary: string;
  primaryDark: string;
  primaryLight: string;
  secondary: string;
  secondaryDark: string;
  accent: string;
  success: string;
  warning: string;
  error: string;
  background: string;
  backgroundLight: string;
  surface: string;
  surfaceLight: string;
  text: string;
  textSecondary: string;
  glass: string;
}

interface DefectType {
  id: string;
  defectType: string; // Изменено с type на defectType
  name: string;
  color: string;
  icon: React.ReactNode;
  severity: number;
  location: 'furnace' | 'mold' | 'glass';
  description: string;
}

interface DefectVisual {
  position: [number, number, number];
  defectType: string;
  severity: number;
  rotation?: [number, number, number];
  scale?: number;
}

interface FurnaceProps {
  temperature: number;
  thermalField: number[][] | null;
  showField: boolean;
  defects: DefectType[];
}

interface MoldProps {
  position: [number, number, number];
  temperature: number;
  defects: DefectType[];
}

// Цветовая палитра РТУ МИРЭА
const MIREA_COLORS: MIREAColors = {
  primary: '#0066CC',
  primaryDark: '#004C99',
  primaryLight: '#3385D6',
  secondary: '#FF6B35',
  secondaryDark: '#CC5529',
  accent: '#00D9FF',
  success: '#00C853',
  warning: '#FFA726',
  error: '#EF5350',
  background: '#0A0E1A',
  backgroundLight: '#141824',
  surface: '#1E2330',
  surfaceLight: '#2A3142',
  text: '#FFFFFF',
  textSecondary: '#B0B8C8',
  glass: 'rgba(255, 255, 255, 0.05)'
};

// Конфигурация типов дефектов
const DEFECT_TYPES: { [key: string]: Omit<DefectType, 'id' | 'defectType' | 'severity' | 'location'> } = {
  crack: {
    name: 'Трещина',
    color: '#EF5350',
    icon: <CrisisAlert />,
    description: 'Линейный разрыв материала'
  },
  bubble: {
    name: 'Пузырь',
    color: '#FFA726',
    icon: <BubbleChart />,
    description: 'Газовый пузырь в массе'
  },
  chip: {
    name: 'Скол',
    color: '#9C27B0',
    icon: <BrokenImage />, // Используем BrokenImage вместо CrackedIcon
    description: 'Отколотый фрагмент края'
  },
  cloudiness: {
    name: 'Помутнение',
    color: '#90A4AE',
    icon: <Cloud />,
    description: 'Локальное помутнение стекла'
  },
  stress: {
    name: 'Напряжение',
    color: '#E91E63',
    icon: <WarningAmber />,
    description: 'Внутренние напряжения'
  }
};

// Компонент: Визуализация трещины
const CrackDefect = ({ position, severity, rotation = [0, 0, 0] }: { 
  position: [number, number, number]; 
  severity: number;
  rotation?: [number, number, number];
}) => {
  const meshRef = useRef<THREE.Mesh>(null);
  const [pulse, setPulse] = useState(0);

  useFrame((state) => {
    if (meshRef.current) {
      // Пульсация в зависимости от серьезности
      const pulseValue = Math.sin(state.clock.elapsedTime * (2 + severity * 2)) * 0.1 * severity;
      meshRef.current.scale.set(1 + pulseValue, 1, 1 + pulseValue);
      
      // Медленное вращение
      meshRef.current.rotation.y += 0.01;
    }
  });

  const length = 0.3 + severity * 0.4;
  const width = 0.02 + severity * 0.03;
  const height = 0.01;

  return (
    <mesh 
      ref={meshRef}
      position={position}
      rotation={rotation}
    >
      <boxGeometry args={[length, height, width]} />
      <meshStandardMaterial
        color={MIREA_COLORS.error}
        emissive={MIREA_COLORS.error}
        emissiveIntensity={0.5 + severity * 0.5}
        transparent
        opacity={0.8 + severity * 0.2}
        roughness={0.2}
        metalness={0.8}
      />
    </mesh>
  );
};

// Компонент: Визуализация пузыря
const BubbleDefect = ({ position, severity }: { 
  position: [number, number, number]; 
  severity: number;
}) => {
  const meshRef = useRef<THREE.Mesh>(null);
  const innerMeshRef = useRef<THREE.Mesh>(null);

  useFrame((state) => {
    if (meshRef.current && innerMeshRef.current) {
      // Плавное подрагивание
      const float = Math.sin(state.clock.elapsedTime * 1.5) * 0.05 * severity;
      meshRef.current.position.y = position[1] + float;
      
      // Вращение внутреннего шарика
      innerMeshRef.current.rotation.x += 0.02;
      innerMeshRef.current.rotation.y += 0.03;
    }
  });

  const size = 0.08 + severity * 0.12;

  return (
    <group position={position}>
      <mesh ref={meshRef}>
        <sphereGeometry args={[size, 16, 16]} />
        <meshPhysicalMaterial
          color={MIREA_COLORS.warning}
          transparent
          opacity={0.6}
          transmission={0.9}
          thickness={0.5}
          roughness={0.1}
          clearcoat={1}
          clearcoatRoughness={0}
        />
      </mesh>
      {/* Внутренний пузырек */}
      <mesh ref={innerMeshRef} position={[0, 0, size * 0.3]}>
        <sphereGeometry args={[size * 0.3, 8, 8]} />
        <meshStandardMaterial
          color="#FFD700"
          emissive="#FFD700"
          emissiveIntensity={0.8}
          transparent
          opacity={0.9}
        />
      </mesh>
    </group>
  );
};

// Компонент: Визуализация скола
const ChipDefect = ({ position, severity, rotation = [0, 0, 0] }: { 
  position: [number, number, number]; 
  severity: number;
  rotation?: [number, number, number];
}) => {
  const meshRef = useRef<THREE.Mesh>(null);
  const particlesRef = useRef<THREE.Group>(null);

  useFrame((state) => {
    if (meshRef.current) {
      // Медленное мерцание
      const glow = 0.5 + 0.3 * Math.sin(state.clock.elapsedTime * 2);
      (meshRef.current.material as THREE.MeshStandardMaterial).emissiveIntensity = glow;
    }
    
    if (particlesRef.current && severity > 0.5) {
      // Частицы осколков
      particlesRef.current.rotation.y += 0.02;
    }
  });

  const size = 0.05 + severity * 0.1;

  return (
    <group position={position} rotation={rotation}>
      <mesh ref={meshRef}>
        <dodecahedronGeometry args={[size, 0]} />
        <meshStandardMaterial
          color="#9C27B0"
          emissive="#9C27B0"
          emissiveIntensity={0.5}
          roughness={0.3}
          metalness={0.7}
        />
      </mesh>
      
      {/* Частицы осколков для серьезных дефектов */}
      {severity > 0.5 && (
        <group ref={particlesRef}>
          {[...Array(3)].map((_, i) => {
            const angle = (i / 3) * Math.PI * 2;
            const radius = size * 2;
            return (
              <mesh
                key={i}
                position={[
                  Math.cos(angle) * radius,
                  Math.sin(angle) * 0.3,
                  Math.sin(angle) * radius
                ]}
              >
                <octahedronGeometry args={[size * 0.3, 0]} />
                <meshStandardMaterial
                  color="#BA68C8"
                  emissive="#BA68C8"
                  emissiveIntensity={0.4}
                  transparent
                  opacity={0.7}
                />
              </mesh>
            );
          })}
        </group>
      )}
    </group>
  );
};

// Компонент: Визуализация помутнения
const CloudinessDefect = ({ position, severity }: { 
  position: [number, number, number]; 
  severity: number;
}) => {
  const meshRef = useRef<THREE.Mesh>(null);

  useFrame((state) => {
    if (meshRef.current) {
      // Очень медленное вращение
      meshRef.current.rotation.y += 0.001;
      
      // Пульсация прозрачности
      const pulse = 0.5 + 0.3 * Math.sin(state.clock.elapsedTime * 0.5);
      (meshRef.current.material as THREE.MeshStandardMaterial).opacity = 0.4 + pulse * 0.3 * severity;
    }
  });

  const size = 0.15 + severity * 0.25;

  return (
    <mesh ref={meshRef} position={position}>
      <sphereGeometry args={[size, 32, 32]} />
      <meshPhysicalMaterial
        color="#90A4AE"
        transparent
        opacity={0.6}
        transmission={0.7}
        thickness={1}
        roughness={0.8}
        clearcoat={0.5}
      />
    </mesh>
  );
};

// Компонент: Визуализация напряжений
const StressDefect = ({ position, severity }: { 
  position: [number, number, number]; 
  severity: number;
}) => {
  const groupRef = useRef<THREE.Group>(null);
  const linesRef = useRef<THREE.Group>(null);

  useFrame((state) => {
    if (groupRef.current) {
      // Пульсация всей группы
      const pulse = 1 + 0.2 * Math.sin(state.clock.elapsedTime * 3) * severity;
      groupRef.current.scale.set(pulse, pulse, pulse);
    }
    
    if (linesRef.current) {
      // Вращение линий напряжений
      linesRef.current.rotation.y += 0.005;
      linesRef.current.rotation.z += 0.003;
    }
  });

  const coreSize = 0.06 + severity * 0.09;
  const lineLength = 0.2 + severity * 0.3;

  return (
    <group ref={groupRef} position={position}>
      {/* Центральное ядро */}
      <mesh>
        <icosahedronGeometry args={[coreSize, 1]} />
        <meshStandardMaterial
          color="#E91E63"
          emissive="#E91E63"
          emissiveIntensity={0.8}
          roughness={0.2}
          metalness={0.8}
        />
      </mesh>
      
      {/* Линии напряжений */}
      <group ref={linesRef}>
        {[...Array(6)].map((_, i) => {
          const angle = (i / 6) * Math.PI * 2;
          return (
            <mesh
              key={i}
              position={[
                Math.cos(angle) * lineLength * 0.5,
                Math.sin(angle) * lineLength * 0.5,
                0
              ]}
              rotation={[0, 0, angle]}
            >
              <cylinderGeometry args={[0.008, 0.004, lineLength, 8]} />
              <meshStandardMaterial
                color="#F48FB1"
                emissive="#F48FB1"
                emissiveIntensity={0.6}
                transparent
                opacity={0.8}
              />
            </mesh>
          );
        })}
      </group>
    </group>
  );
};

// Компонент: Дефекты на печи
const FurnaceDefects = ({ defects }: { defects: DefectType[] }) => {
  const furnacePositions: DefectVisual[] = useMemo(() => {
    const positions: DefectVisual[] = [];
    
    // Трещины - на дверце и углах
    defects.filter(d => d.defectType === 'crack').forEach((defect, i) => {
      positions.push({
        position: [0, 0.5 + Math.random() * 0.5, 1.6] as [number, number, number],
        defectType: 'crack',
        severity: defect.severity,
        rotation: [0, 0, Math.random() * Math.PI] as [number, number, number]
      });
    });
    
    // Пузыри - внутри печи (видно через окно)
    defects.filter(d => d.defectType === 'bubble').forEach((defect, i) => {
      positions.push({
        position: [
          (Math.random() - 0.5) * 1.5,
          0.5 + Math.random() * 1,
          0.5 + Math.random()
        ] as [number, number, number],
        defectType: 'bubble',
        severity: defect.severity
      });
    });
    
    // Помутнение - на стекле окна
    defects.filter(d => d.defectType === 'cloudiness').forEach((defect, i) => {
      positions.push({
        position: [
          (Math.random() - 0.5) * 0.8,
          0.5 + (Math.random() - 0.5) * 0.3,
          1.65
        ] as [number, number, number],
        defectType: 'cloudiness',
        severity: defect.severity
      });
    });
    
    // Напряжения - на корпусе
    defects.filter(d => d.defectType === 'stress').forEach((defect, i) => {
      positions.push({
        position: [
          (Math.random() - 0.5) * 3.5,
          (Math.random() - 0.5) * 2.5,
          (Math.random() - 0.5) * 2.5
        ] as [number, number, number],
        defectType: 'stress',
        severity: defect.severity
      });
    });
    
    return positions;
  }, [defects]);

  return (
    <group>
      {furnacePositions.map((defect, i) => {
        switch(defect.defectType) {
          case 'crack':
            return <CrackDefect key={i} {...defect} />;
          case 'bubble':
            return <BubbleDefect key={i} {...defect} />;
          case 'cloudiness':
            return <CloudinessDefect key={i} {...defect} />;
          case 'stress':
            return <StressDefect key={i} {...defect} />;
          default:
            return null;
        }
      })}
    </group>
  );
};

// Компонент: Дефекты на форме
const MoldDefects = ({ position, defects }: { 
  position: [number, number, number]; 
  defects: DefectType[];
}) => {
  const moldPositions: DefectVisual[] = useMemo(() => {
    const positions: DefectVisual[] = [];
    const radius = 0.6;
    const height = 0.8;
    
    defects.forEach((defect, i) => {
      const angle = (i / defects.length) * Math.PI * 2;
      const yPos = (Math.random() - 0.5) * height;
      
      switch(defect.defectType) {
        case 'crack':
          positions.push({
            position: [
              position[0] + Math.cos(angle) * radius * 0.8,
              position[1] + yPos + 0.5,
              position[2] + Math.sin(angle) * radius * 0.8
            ] as [number, number, number],
            defectType: 'crack',
            severity: defect.severity,
            rotation: [0, angle, 0] as [number, number, number]
          });
          break;
          
        case 'chip':
          positions.push({
            position: [
              position[0] + Math.cos(angle) * radius * 0.9,
              position[1] + yPos + 0.6,
              position[2] + Math.sin(angle) * radius * 0.9
            ] as [number, number, number],
            defectType: 'chip',
            severity: defect.severity
          });
          break;
          
        case 'bubble':
          positions.push({
            position: [
              position[0] + (Math.random() - 0.5) * radius * 0.5,
              position[1] + 0.5 + Math.random() * 0.3,
              position[2] + (Math.random() - 0.5) * radius * 0.5
            ] as [number, number, number],
            defectType: 'bubble',
            severity: defect.severity
          });
          break;
          
        case 'stress':
          positions.push({
            position: [
              position[0] + Math.cos(angle) * radius * 0.7,
              position[1] + 0.5,
              position[2] + Math.sin(angle) * radius * 0.7
            ] as [number, number, number],
            defectType: 'stress',
            severity: defect.severity
          });
          break;
      }
    });
    
    return positions;
  }, [defects, position]);

  return (
    <group>
      {moldPositions.map((defect, i) => {
        switch(defect.defectType) {
          case 'crack':
            return <CrackDefect key={i} {...defect} />;
          case 'chip':
            return <ChipDefect key={i} {...defect} />;
          case 'bubble':
            return <BubbleDefect key={i} {...defect} />;
          case 'stress':
            return <StressDefect key={i} {...defect} />;
          default:
            return null;
        }
      })}
    </group>
  );
};

// Компонент: Легенда дефектов (3D-оверлей)
const DefectsLegend = ({ defects, position }: { 
  defects: DefectType[]; 
  position: [number, number, number];
}) => {
  if (defects.length === 0) return null;

  return (
    <Html position={position} center>
      <Box sx={{
        background: 'rgba(30, 35, 48, 0.95)',
        backdropFilter: 'blur(10px)',
        borderRadius: 2,
        p: 1.5,
        border: '1px solid rgba(0, 102, 204, 0.3)',
        minWidth: 180,
        boxShadow: '0 4px 20px rgba(0, 0, 0, 0.5)'
      }}>
        <Typography variant="caption" sx={{ 
          color: MIREA_COLORS.text, 
          fontWeight: 700, 
          mb: 1, 
          display: 'block' 
        }}>
          ⚠️ Обнаружены дефекты:
        </Typography>
        
        <Stack spacing={0.5}>
          {defects.map((defect, i) => (
            <Box 
              key={i} 
              sx={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: 1,
                p: 0.5,
                borderRadius: 1,
                bgcolor: `${defect.color}20`,
                borderLeft: `3px solid ${defect.color}`
              }}
            >
              <Box sx={{ color: defect.color }}>
                {defect.icon}
              </Box>
              <Box sx={{ flex: 1 }}>
                <Typography variant="caption" sx={{ color: MIREA_COLORS.text, fontWeight: 500 }}>
                  {defect.name}
                </Typography>
                <Typography variant="caption" sx={{ 
                  color: MIREA_COLORS.textSecondary, 
                  display: 'block',
                  fontSize: '0.7em'
                }}>
                  Серьезность: {(defect.severity * 100).toFixed(0)}%
                </Typography>
              </Box>
            </Box>
          ))}
        </Stack>
      </Box>
    </Html>
  );
};

// Компонент: Печь с температурной визуализацией
const EnhancedFurnace = ({ temperature, thermalField, showField, defects }: FurnaceProps) => {
  const furnaceRef = useRef<THREE.Mesh>(null);
  const doorRef = useRef<THREE.Mesh>(null);
  const [glowIntensity, setGlowIntensity] = useState(0);
  
  useEffect(() => {
    const intensity = Math.min(1, Math.max(0, (temperature - 1400) / 300));
    setGlowIntensity(intensity);
  }, [temperature]);
  
  useFrame((state) => {
    if (furnaceRef.current && glowIntensity > 0.7) {
      const pulse = Math.sin(state.clock.elapsedTime * 2) * 0.03;
      furnaceRef.current.scale.set(1 + pulse, 1, 1 + pulse);
    }
  });
  
  const furnaceDefects = defects.filter(d => 
    d.location === 'furnace' || ['bubble', 'cloudiness', 'stress'].includes(d.defectType)
  );

  return (
    <group position={[-6, 1.5, 0]}>
      {/* Основной корпус печи */}
      <mesh ref={furnaceRef} position={[0, 0, 0]} castShadow receiveShadow>
        <boxGeometry args={[4, 3, 3]} />
        <meshStandardMaterial 
          color={MIREA_COLORS.secondary}
          emissive={MIREA_COLORS.secondary}
          emissiveIntensity={glowIntensity * 0.7}
          metalness={0.3}
          roughness={0.6}
        />
      </mesh>
      
      {/* Дверца печи */}
      <mesh ref={doorRef} position={[0, 0, 1.6]} castShadow>
        <boxGeometry args={[2.5, 2, 0.15]} />
        <meshStandardMaterial 
          color={MIREA_COLORS.error}
          emissive={MIREA_COLORS.error}
          emissiveIntensity={glowIntensity * 0.9}
          metalness={0.5}
          roughness={0.3}
        />
      </mesh>
      
      {/* Окно печи */}
      <mesh position={[0, 0.5, 1.65]}>
        <boxGeometry args={[1, 0.8, 0.05]} />
        <meshStandardMaterial 
          color="#FFD700"
          emissive="#FFD700"
          emissiveIntensity={glowIntensity}
          transparent
          opacity={0.9}
        />
      </mesh>
      
      {/* Горелки */}
      {[...Array(3)].map((_, i) => (
        <group key={i} position={[-1.5 + i * 1.5, -1.2, 0]}>
          <mesh rotation={[Math.PI / 2, 0, 0]}>
            <cylinderGeometry args={[0.15, 0.15, 0.8]} />
            <meshStandardMaterial color="#333333" metalness={0.8} roughness={0.2} />
          </mesh>
          {/* Flame effect */}
          <mesh position={[0, 0, 0.6]} rotation={[Math.PI, 0, 0]}>
            <coneGeometry args={[0.2, 0.6, 8]} />
            <meshStandardMaterial 
              color="#FF4500"
              emissive="#FF4500"
              emissiveIntensity={glowIntensity * 1.2}
              transparent
              opacity={0.8}
            />
          </mesh>
        </group>
      ))}
      
      {/* Temperature label */}
      <Text
        position={[0, 2.5, 0]}
        color={MIREA_COLORS.accent}
        fontSize={0.4}
        fontWeight={700}
        anchorX="center"
        anchorY="middle"
      >
        {temperature.toFixed(0)}°C
      </Text>
      
      {/* Status indicator */}
      <mesh position={[-1.8, 1.3, 1.6]}>
        <sphereGeometry args={[0.15]} />
        <meshStandardMaterial 
          color={temperature > 1600 ? MIREA_COLORS.error : MIREA_COLORS.success}
          emissive={temperature > 1600 ? MIREA_COLORS.error : MIREA_COLORS.success}
          emissiveIntensity={0.8}
        />
      </mesh>
      
      {/* Дефекты на печи */}
      <FurnaceDefects defects={furnaceDefects} />
      
      {/* Легенда дефектов печи */}
      {furnaceDefects.length > 0 && (
        <DefectsLegend 
          defects={furnaceDefects} 
          position={[0, 2.2, 2.5] as [number, number, number]} 
        />
      )}
    </group>
  );
};

// Компонент: Форма для стекла
const Mold = ({ position, temperature, defects }: MoldProps) => {
  const moldRef = useRef<THREE.Mesh>(null);
  const [heatGlow, setHeatGlow] = useState(0);
  
  useEffect(() => {
    const glow = temperature > 350 ? 0.4 : 0.1;
    setHeatGlow(glow);
  }, [temperature]);
  
  const moldDefects = defects.filter(d => 
    d.location === 'mold' || ['crack', 'chip'].includes(d.defectType)
  );

  return (
    <group position={position}>
      {/* Форма */}
      <mesh ref={moldRef} castShadow>
        <cylinderGeometry args={[0.6, 0.6, 0.8]} />
        <meshStandardMaterial 
          color={temperature > 350 ? MIREA_COLORS.error : MIREA_COLORS.warning}
          emissive={temperature > 350 ? MIREA_COLORS.error : MIREA_COLORS.warning}
          emissiveIntensity={heatGlow}
          metalness={0.5}
          roughness={0.5}
        />
      </mesh>
      
      {/* Температурный индикатор */}
      <Text
        position={[0, 1.5, 0]}
        color={temperature > 350 ? MIREA_COLORS.error : MIREA_COLORS.textSecondary}
        fontSize={0.25}
        anchorX="center"
      >
        {temperature.toFixed(0)}°C
      </Text>
      
      {/* Дефекты на форме */}
      <MoldDefects position={position} defects={moldDefects} />
      
      {/* Легенда дефектов формы */}
      {moldDefects.length > 0 && (
        <DefectsLegend 
          defects={moldDefects} 
          position={[0, 2, 0] as [number, number, number]} 
        />
      )}
    </group>
  );
};

// Компонент: Формовочная линия с анимацией
const FormingLine = ({ speed, moldTemp, glass, defects }: { 
  speed: number; 
  moldTemp: number;
  glass: boolean;
  defects: DefectType[];
}) => {
  const beltRef = useRef<THREE.Mesh>(null);
  const glassRef = useRef<THREE.Mesh>(null);
  const [beltOffset, setBeltOffset] = useState(0);
  
  const moldPositions: [number, number, number][] = [
    [-4, 0.5, 0],
    [-1.5, 0.5, 0],
    [1, 0.5, 0],
    [3.5, 0.5, 0]
  ];
  
  useFrame((state) => {
    if (beltRef.current) {
      const delta = state.clock.getDelta();
      setBeltOffset((prev) => (prev + speed * delta * 0.05) % 10);
      beltRef.current.position.x = -4 + beltOffset;
    }
    
    // Анимация стекла
    if (glassRef.current && glass) {
      const pulse = Math.sin(state.clock.elapsedTime * 3) * 0.02;
      glassRef.current.scale.set(1, 1 + pulse, 1);
    }
  });
  
  return (
    <group position={[4, 0.5, 0]}>
      {/* Основание конвейера */}
      <mesh position={[0, -0.3, 0]} castShadow receiveShadow>
        <boxGeometry args={[10, 0.4, 2]} />
        <meshStandardMaterial 
          color={MIREA_COLORS.surfaceLight}
          metalness={0.7}
          roughness={0.3}
        />
      </mesh>
      
      {/* Движущаяся лента */}
      <mesh 
        ref={beltRef}
        position={[0, 0, 0]} 
        castShadow
      >
        <boxGeometry args={[1.5, 0.05, 1.8]} />
        <meshStandardMaterial 
          color={MIREA_COLORS.primary}
          metalness={0.6}
          roughness={0.4}
        />
      </mesh>
      
      {/* Формы для стекла */}
      {moldPositions.map((pos, i) => (
        <Mold
          key={i}
          position={pos}
          temperature={moldTemp}
          defects={defects.filter(d => d.location === 'mold')}
        />
      ))}
      
      {/* Glass piece */}
      {glass && (
        <mesh 
          ref={glassRef}
          position={[-1.5, 0.8, 0]}
          castShadow
        >
          <boxGeometry args={[1, 0.3, 1]} />
          <meshPhysicalMaterial 
            color={MIREA_COLORS.accent}
            transparent
            opacity={0.7}
            metalness={0.1}
            roughness={0.1}
            transmission={0.9}
            thickness={0.5}
          />
        </mesh>
      )}
      
      {/* Speed indicator */}
      <Text
        position={[0, -1, 0]}
        color={MIREA_COLORS.accent}
        fontSize={0.35}
        fontWeight={700}
        anchorX="center"
      >
        {speed.toFixed(0)} м/мин
      </Text>
    </group>
  );
};

// Компонент: Освещение и фон
const SceneLighting = () => {
  return (
    <>
      <ambientLight intensity={0.2} />
      <pointLight 
        position={[10, 10, 10]} 
        intensity={1.5} 
        castShadow 
        shadow-mapSize-width={2048}
        shadow-mapSize-height={2048}
      />
      <pointLight 
        position={[-10, 5, -10]} 
        intensity={0.8} 
        color={MIREA_COLORS.primary} 
      />
      <spotLight
        position={[0, 15, 0]}
        angle={0.4}
        penumbra={1}
        intensity={2}
        castShadow
        color={MIREA_COLORS.accent}
      />
      
      {/* Grid floor */}
      <mesh 
        rotation={[-Math.PI / 2, 0, 0]} 
        position={[0, -0.5, 0]} 
        receiveShadow
      >
        <planeGeometry args={[40, 40]} />
        <meshStandardMaterial 
          color={MIREA_COLORS.background}
          metalness={0.3}
          roughness={0.7}
        />
      </mesh>
      
      {/* Grid lines */}
      <gridHelper 
        args={[40, 40, MIREA_COLORS.primary, MIREA_COLORS.surfaceLight]} 
        position={[0, -0.49, 0]}
      />
    </>
  );
};

// Главный компонент
