      severity: 0.7, 
      location: 'mold', 
      ...DEFECT_TYPES.crack 
    },
    { 
      id: '2', 
      defectType: 'bubble', 
      severity: 0.4, 
      location: 'furnace', 
      ...DEFECT_TYPES.bubble 
    },
    { 
      id: '3', 
      defectType: 'chip', 
      severity: 0.5, 
      location: 'mold', 
      ...DEFECT_TYPES.chip 
    },
    { 
      id: '4', 
      defectType: 'cloudiness', 
      severity: 0.3, 
      location: 'furnace', 
      ...DEFECT_TYPES.cloudiness 
    },
    { 
      id: '5', 
