                  icon={<CheckCircle />}
                  label="В режиме демонстрации"
                  color="success"
                  size="small"
                  sx={{ 
                    fontWeight: 700,
                    background: MIREA_COLORS.success,
                    color: MIREA_COLORS.text
                  }}
                />
                
                <FormControlLabel
                  control={
                    <Switch 
                      checked={showDefects} 
