            </Stack>
          </Paper>
        </Grid>
        
        {/* 3D Visualization */}
        <Grid item xs={12} md={6}>
          <Paper
            elevation={8}
            sx={{
              height: '700px',
              position: 'relative',
              overflow: 'hidden',
              background: MIREA_COLORS.background,
              border: `2px solid ${MIREA_COLORS.primary}60`,
              borderRadius: 2,
              boxShadow: `0 8px 32px ${MIREA_COLORS.primary}30`
            }}
          >
            <Canvas
              shadows
              camera={{ position: [12, 8, 12], fov: 60 }}
            >
