              />
              
              <FormingLine 
                speed={beltSpeed}
                moldTemp={moldTemp}
                glass={true}
                defects={showDefects ? filteredDefects.filter(d => d.location === 'mold') : []}
              />
              
              {/* Factory building outline */}
              <mesh position={[0, 4, -5]} receiveShadow>
                <boxGeometry args={[25, 8, 1]} />
                <meshStandardMaterial 
                  color={MIREA_COLORS.surfaceLight}
                  metalness={0.5}
                  roughness={0.5}
                  transparent
                  opacity={0.3}
                />
              </mesh>
              
              {/* Title */}
              <Text
                position={[0, 7, 0]}
                color={MIREA_COLORS.primary}
