                />
              </Box>
              
              <AvatarGroup max={4} sx={{ justifyContent: 'flex-end', mt: 1 }}>
                {defects.map((defect) => (
                  <Avatar 
                    key={defect.id}
                    sx={{ 
                      bgcolor: defect.color, 
                      width: 28, 
                      height: 28,
                      border: `2px solid ${MIREA_COLORS.surface}`
                    }}
                  >
                    {defect.icon}
                  </Avatar>
                ))}
              </AvatarGroup>
            </Box>
          </Paper>
        </Grid>
        
        {/* Контролы параметров */}
        <Grid item xs={12} md={3}>
          <Paper sx={{ 
            p: 2, 
            height: '700px',
            background: `linear-gradient(180deg, ${MIREA_COLORS.surface}F2 0%, ${MIREA_COLORS.surfaceLight}F2 100%)`,
            backdropFilter: 'blur(20px)',
            border: `1px solid ${MIREA_COLORS.primary}40`,
            borderRadius: 2,
            overflow: 'auto'
          }}>
            <Typography variant="h6" sx={{ 
              color: MIREA_COLORS.text, 
