import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Stack,
  Card,
  CardContent,
  Tabs,
  Tab,
  CircularProgress,
  Alert
} from '@mui/material';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  ScatterChart,
  Scatter,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Cell,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar
} from 'recharts';
import { useTheme } from '@mui/material/styles';
import {
  TrendingUp,
  BarChart as BarChartIcon,
  ScatterPlot,
  BubbleChart
} from '@mui/icons-material';

const API_BASE_URL = 'http://localhost:8000';

interface DefectTrendData {
  timestamp: string;
  total_defects: number;
  crack: number;
  bubble: number;
  chip: number;
  stain: number;
  cloudiness: number;
  deformation: number;
}

interface EfficiencyData {
  timestamp: string;
  production_rate: number;
  quality_rate: number;
  efficiency_score: number;
}

interface CorrelationData {
  [parameter: string]: {
    [defect: string]: number;
  };
}

const AnalyticsDashboard: React.FC = () => {
  const theme = useTheme();

  const [activeTab, setActiveTab] = useState(0);
  const [timeRange, setTimeRange] = useState('24h');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Data states
  const [defectTrends, setDefectTrends] = useState<DefectTrendData[]>([]);
  const [efficiencyData, setEfficiencyData] = useState<EfficiencyData[]>([]);
  const [correlations, setCorrelations] = useState<CorrelationData>({});

  // Fetch defect trends
  const fetchDefectTrends = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `${API_BASE_URL}/api/analytics/defect-trends?timerange=${timeRange}&grouping=hourly`
      );

      if (!response.ok) {
        throw new Error('Failed to fetch defect trends');
      }

      const data = await response.json();
      setDefectTrends(data.data_points || []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      console.error('Error fetching defect trends:', err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch production efficiency
  const fetchEfficiencyData = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `${API_BASE_URL}/api/analytics/production-efficiency?timerange=${timeRange}`
      );

      if (!response.ok) {
        throw new Error('Failed to fetch efficiency data');
      }

      const data = await response.json();
      setEfficiencyData(data.data_points || []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      console.error('Error fetching efficiency data:', err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch parameter correlations
  const fetchCorrelations = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE_URL}/api/analytics/parameter-correlations`);

      if (!response.ok) {
        throw new Error('Failed to fetch correlations');
      }

      const data = await response.json();
      setCorrelations(data.correlations || {});
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      console.error('Error fetching correlations:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDefectTrends();
    fetchEfficiencyData();
    fetchCorrelations();
  }, [timeRange]);

  // Prepare correlation matrix data for visualization
  const getCorrelationMatrixData = () => {
    const parameters = Object.keys(correlations);
    const defects = ['crack', 'bubble', 'chip', 'stain', 'cloudiness', 'deformation'];

    return parameters.flatMap(param => 
      defects.map(defect => ({
        parameter: param.replace(/_/g, ' '),
        defect: defect,
        correlation: correlations[param]?.[defect] || 0
      }))
    );
  };

  const getCorrelationColor = (value: number) => {
    if (value > 0.6) return theme.palette.error.main;
    if (value > 0.3) return theme.palette.warning.main;
    if (value > 0) return theme.palette.info.main;
    if (value > -0.3) return theme.palette.success.light;
    return theme.palette.success.main;
  };

  // Prepare radar chart data for defect distribution
  const getDefectDistributionRadar = () => {
    if (defectTrends.length === 0) return [];

    const latest = defectTrends[defectTrends.length - 1];
    const total = latest.total_defects || 1;

    return [
      { defect: 'Трещины', value: (latest.crack / total) * 100 },
      { defect: 'Пузыри', value: (latest.bubble / total) * 100 },
      { defect: 'Сколы', value: (latest.chip / total) * 100 },
      { defect: 'Пятна', value: (latest.stain / total) * 100 },
      { defect: 'Помутнения', value: (latest.cloudiness / total) * 100 },
      { defect: 'Деформации', value: (latest.deformation / total) * 100 }
    ];
  };

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Stack direction="row" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" fontWeight="bold">
          Расширенная аналитика
        </Typography>

        <FormControl sx={{ minWidth: 150 }}>
          <InputLabel>Временной диапазон</InputLabel>
          <Select
            value={timeRange}
            label="Временной диапазон"
            onChange={(e) => setTimeRange(e.target.value)}
          >
            <MenuItem value="24h">24 часа</MenuItem>
            <MenuItem value="7d">7 дней</MenuItem>
            <MenuItem value="30d">30 дней</MenuItem>
          </Select>
        </FormControl>
      </Stack>

      {/* Error Alert */}
      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError(null)}>
          {error}
        </Alert>
      )}

      {/* Tabs */}
      <Paper sx={{ mb: 3 }}>
        <Tabs value={activeTab} onChange={(_, newValue) => setActiveTab(newValue)}>
          <Tab icon={<TrendingUp />} label="Тренды дефектов" />
          <Tab icon={<ScatterPlot />} label="Эффективность производства" />
          <Tab icon={<BubbleChart />} label="Корреляции параметров" />
          <Tab icon={<BarChartIcon />} label="Распределение дефектов" />
        </Tabs>
      </Paper>

      {/* Loading Indicator */}
      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
          <CircularProgress />
        </Box>
      )}

      {/* Tab Panels */}
      {!loading && (
        <>
          {/* Tab 1: Defect Trends */}
          {activeTab === 0 && (
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Динамика дефектов по времени
                  </Typography>
                  <ResponsiveContainer width="100%" height={400}>
                    <LineChart data={defectTrends}>
                      <CartesianGrid strokeDasharray="3 3" stroke={theme.palette.divider} />
                      <XAxis
                        dataKey="timestamp"
                        tickFormatter={(value) => new Date(value).toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' })}
                        stroke={theme.palette.text.secondary}
                      />
                      <YAxis stroke={theme.palette.text.secondary} />
                      <Tooltip
                        contentStyle={{
                          backgroundColor: theme.palette.background.paper,
                          border: `1px solid ${theme.palette.divider}`
                        }}
                        labelFormatter={(value) => new Date(value).toLocaleString('ru-RU')}
                      />
                      <Legend />
                      <Line type="monotone" dataKey="crack" stroke={theme.palette.error.main} strokeWidth={2} name="Трещины" />
                      <Line type="monotone" dataKey="bubble" stroke={theme.palette.warning.main} strokeWidth={2} name="Пузыри" />
                      <Line type="monotone" dataKey="chip" stroke={theme.palette.info.main} strokeWidth={2} name="Сколы" />
                      <Line type="monotone" dataKey="stain" stroke={theme.palette.success.main} strokeWidth={2} name="Пятна" />
                      <Line type="monotone" dataKey="cloudiness" stroke={theme.palette.primary.main} strokeWidth={2} name="Помутнения" />
                      <Line type="monotone" dataKey="deformation" stroke={theme.palette.secondary.main} strokeWidth={2} name="Деформации" />
                    </LineChart>
                  </ResponsiveContainer>
                </Paper>
              </Grid>

              <Grid item xs={12}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Суммарное количество дефектов
                  </Typography>
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={defectTrends.slice(-20)}>
                      <CartesianGrid strokeDasharray="3 3" stroke={theme.palette.divider} />
                      <XAxis
                        dataKey="timestamp"
                        tickFormatter={(value) => new Date(value).toLocaleTimeString('ru-RU', { hour: '2-digit' })}
                        stroke={theme.palette.text.secondary}
                      />
                      <YAxis stroke={theme.palette.text.secondary} />
                      <Tooltip />
                      <Bar dataKey="total_defects" fill={theme.palette.primary.main} name="Всего дефектов" />
                    </BarChart>
                  </ResponsiveContainer>
                </Paper>
              </Grid>
            </Grid>
          )}

          {/* Tab 2: Production Efficiency */}
          {activeTab === 1 && (
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Производительность vs Качество
                  </Typography>
                  <ResponsiveContainer width="100%" height={400}>
                    <ScatterChart>
                      <CartesianGrid strokeDasharray="3 3" stroke={theme.palette.divider} />
                      <XAxis
                        type="number"
                        dataKey="production_rate"
                        name="Производительность"
                        unit=" ед/ч"
                        stroke={theme.palette.text.secondary}
                      />
                      <YAxis
                        type="number"
                        dataKey="quality_rate"
                        name="Качество"
                        unit="%"
                        stroke={theme.palette.text.secondary}
                      />
                      <Tooltip cursor={{ strokeDasharray: '3 3' }} />
                      <Scatter
                        name="Точки данных"
                        data={efficiencyData}
                        fill={theme.palette.primary.main}
                      />
                    </ScatterChart>
                  </ResponsiveContainer>
                </Paper>
              </Grid>

              <Grid item xs={12}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Показатель эффективности по времени
                  </Typography>
                  <ResponsiveContainer width="100%" height={300}>
                    <LineChart data={efficiencyData}>
                      <CartesianGrid strokeDasharray="3 3" stroke={theme.palette.divider} />
                      <XAxis
                        dataKey="timestamp"
                        tickFormatter={(value) => new Date(value).toLocaleTimeString('ru-RU', { hour: '2-digit' })}
                        stroke={theme.palette.text.secondary}
                      />
                      <YAxis stroke={theme.palette.text.secondary} />
                      <Tooltip />
                      <Line
                        type="monotone"
                        dataKey="efficiency_score"
                        stroke={theme.palette.success.main}
                        strokeWidth={3}
                        name="Эффективность"
                      />
                    </LineChart>
                  </ResponsiveContainer>
                </Paper>
              </Grid>
            </Grid>
          )}

          {/* Tab 3: Parameter Correlations */}
          {activeTab === 2 && (
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Корреляция параметров процесса с типами дефектов
              </Typography>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                Положительные значения (красный/оранжевый): увеличение параметра повышает вероятность дефекта
                <br />
                Отрицательные значения (зеленый): увеличение параметра снижает вероятность дефекта
              </Typography>

              <Grid container spacing={2} sx={{ mt: 2 }}>
                {Object.entries(correlations).map(([param, defectCorrs]) => (
                  <Grid item xs={12} md={6} lg={4} key={param}>
                    <Card>
                      <CardContent>
                        <Typography variant="subtitle1" fontWeight="bold" gutterBottom>
                          {param.replace(/_/g, ' ').toUpperCase()}
                        </Typography>
                        <Stack spacing={1}>
                          {Object.entries(defectCorrs).map(([defect, correlation]) => (
                            <Box
                              key={defect}
                              sx={{
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                p: 1,
                                borderRadius: 1,
                                backgroundColor: `${getCorrelationColor(correlation)}20`
                              }}
                            >
                              <Typography variant="body2">{defect}</Typography>
                              <Typography
                                variant="body2"
                                fontWeight="bold"
                                sx={{ color: getCorrelationColor(correlation) }}
                              >
                                {correlation > 0 ? '+' : ''}{correlation.toFixed(2)}
                              </Typography>
                            </Box>
                          ))}
                        </Stack>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Paper>
          )}

          {/* Tab 4: Defect Distribution */}
          {activeTab === 3 && (
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Радиальное распределение дефектов
                  </Typography>
                  <ResponsiveContainer width="100%" height={400}>
                    <RadarChart data={getDefectDistributionRadar()}>
                      <PolarGrid stroke={theme.palette.divider} />
                      <PolarAngleAxis
                        dataKey="defect"
                        stroke={theme.palette.text.secondary}
                      />
                      <PolarRadiusAxis stroke={theme.palette.text.secondary} />
                      <Radar
                        name="Процент"
                        dataKey="value"
                        stroke={theme.palette.primary.main}
                        fill={theme.palette.primary.main}
                        fillOpacity={0.6}
                      />
                      <Tooltip />
                    </RadarChart>
                  </ResponsiveContainer>
                </Paper>
              </Grid>

              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Столбчатая диаграмма по типам дефектов
                  </Typography>
                  <ResponsiveContainer width="100%" height={400}>
                    <BarChart data={getDefectDistributionRadar()}>
                      <CartesianGrid strokeDasharray="3 3" stroke={theme.palette.divider} />
                      <XAxis dataKey="defect" stroke={theme.palette.text.secondary} />
                      <YAxis stroke={theme.palette.text.secondary} />
                      <Tooltip />
                      <Bar dataKey="value" fill={theme.palette.primary.main}>
                        {getDefectDistributionRadar().map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={[
                            theme.palette.error.main,
                            theme.palette.warning.main,
                            theme.palette.info.main,
                            theme.palette.success.main,
                            theme.palette.primary.main,
                            theme.palette.secondary.main
                          ][index]} />
                        ))}
                      </Bar>
                    </BarChart>
                  </ResponsiveContainer>
                </Paper>
              </Grid>
            </Grid>
          )}
        </>
      )}
    </Box>
  );
};

export default AnalyticsDashboard;
