import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Grid,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Stack,
  Card,
  CardContent,
  Tabs,
  Tab,
  CircularProgress,
  Alert,
  Chip,
  IconButton,
  Tooltip as MuiTooltip,
  alpha,
  useTheme,
  Divider
} from '@mui/material';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  ScatterChart,
  Scatter,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Cell,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  AreaChart,
  Area,
  ZAxis,
  Brush
} from 'recharts';
import {
  TrendingUp,
  BarChart as BarChartIcon,
  ScatterPlot,
  BubbleChart,
  Download,
  FilterList,
  Refresh,
  InfoOutlined
} from '@mui/icons-material';

const API_BASE_URL = 'http://localhost:8000';

interface DefectTrendData {
  timestamp: string;
  total_defects: number;
  crack: number;
  bubble: number;
  chip: number;
  stain: number;
  cloudiness: number;
  deformation: number;
}

interface EfficiencyData {
  timestamp: string;
  production_rate: number;
  quality_rate: number;
  efficiency_score: number;
}

interface CorrelationData {
  [parameter: string]: {
    [defect: string]: number;
  };
}

// Цветовая палитра РТУ МИРЭА
const RTU_COLORS = {
  primary: '#1E3A8A',
  secondary: '#3B82F6',
  accent: '#10B981',
  warning: '#F59E0B',
  error: '#EF4444',
  info: '#8B5CF6',
  background: '#F8FAFC',
  surface: '#FFFFFF',
  textPrimary: '#1E293B',
  textSecondary: '#64748B'
};

const DEFECT_COLORS = {
  crack: RTU_COLORS.error,
  bubble: RTU_COLORS.warning,
  chip: RTU_COLORS.accent,
  stain: RTU_COLORS.info,
  cloudiness: RTU_COLORS.primary,
  deformation: RTU_COLORS.secondary
};

// Вспомогательные функции для работы с цветами
const withOpacity = (color: string, opacity: number) => {
  const hex = color.replace('#', '');
  const r = parseInt(hex.substring(0, 2), 16);
  const g = parseInt(hex.substring(2, 4), 16);
  const b = parseInt(hex.substring(4, 6), 16);
  return `rgba(${r}, ${g}, ${b}, ${opacity})`;
};

const CustomTooltip = ({ active, payload, label }: any) => {
  if (active && payload && payload.length) {
    return (
      <Paper
        elevation={3}
        sx={{
          p: 2,
          backgroundColor: RTU_COLORS.surface,
          border: `1px solid ${withOpacity(RTU_COLORS.primary, 0.2)}`,
          borderRadius: 2,
          minWidth: 200
        }}
      >
        <Typography variant="subtitle2" fontWeight="bold" color={RTU_COLORS.textPrimary} gutterBottom>
          {new Date(label).toLocaleString('ru-RU', {
            day: '2-digit',
            month: 'short',
            hour: '2-digit',
            minute: '2-digit'
          })}
        </Typography>
        <Divider sx={{ my: 1 }} />
        {payload.map((entry: any, index: number) => (
          <Box key={index} sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 0.5 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: entry.color }} />
              <Typography variant="body2" color={RTU_COLORS.textSecondary}>
                {entry.name}
              </Typography>
            </Box>
            <Typography variant="body2" fontWeight="bold" color={RTU_COLORS.textPrimary}>
              {typeof entry.value === 'number' ? entry.value.toFixed(2) : entry.value}
            </Typography>
          </Box>
        ))}
      </Paper>
    );
  }
  return null;
};

const AnalyticsDashboard: React.FC = () => {
  const theme = useTheme();

  const [activeTab, setActiveTab] = useState(0);
  const [timeRange, setTimeRange] = useState('24h');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedDefects, setSelectedDefects] = useState<string[]>([]);

  const [defectTrends, setDefectTrends] = useState<DefectTrendData[]>([]);
  const [efficiencyData, setEfficiencyData] = useState<EfficiencyData[]>([]);
  const [correlations, setCorrelations] = useState<CorrelationData>({});

  const fetchDefectTrends = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `${API_BASE_URL}/api/analytics/defect-trends?timerange=${timeRange}&grouping=hourly`
      );

      if (!response.ok) {
        throw new Error('Failed to fetch defect trends');
      }

      const data = await response.json();
      setDefectTrends(data.data_points || []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      console.error('Error fetching defect trends:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchEfficiencyData = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `${API_BASE_URL}/api/analytics/production-efficiency?timerange=${timeRange}`
      );

      if (!response.ok) {
        throw new Error('Failed to fetch efficiency data');
      }

      const data = await response.json();
      setEfficiencyData(data.data_points || []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      console.error('Error fetching efficiency data:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchCorrelations = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE_URL}/api/analytics/parameter-correlations`);

      if (!response.ok) {
        throw new Error('Failed to fetch correlations');
      }

      const data = await response.json();
      setCorrelations(data.correlations || {});
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      console.error('Error fetching correlations:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDefectTrends();
    fetchEfficiencyData();
    fetchCorrelations();
  }, [timeRange]);

  const getCorrelationMatrixData = () => {
    const parameters = Object.keys(correlations);
    const defects = ['crack', 'bubble', 'chip', 'stain', 'cloudiness', 'deformation'];

    return parameters.flatMap(param => 
      defects.map(defect => ({
        parameter: param.replace(/_/g, ' '),
        defect: defect,
        correlation: correlations[param]?.[defect] || 0
      }))
    );
  };

  const getCorrelationColor = (value: number) => {
    if (value > 0.7) return RTU_COLORS.error;
    if (value > 0.4) return RTU_COLORS.warning;
    if (value > 0.1) return RTU_COLORS.accent;
    if (value > -0.2) return RTU_COLORS.secondary;
    if (value > -0.5) return RTU_COLORS.primary;
    return RTU_COLORS.info;
  };

  const getDefectDistributionRadar = () => {
    if (defectTrends.length === 0) return [];

    const latest = defectTrends[defectTrends.length - 1];
    const total = latest.total_defects || 1;

    return [
      { defect: 'Трещины', value: (latest.crack / total) * 100, fullMark: 100 },
      { defect: 'Пузыри', value: (latest.bubble / total) * 100, fullMark: 100 },
      { defect: 'Сколы', value: (latest.chip / total) * 100, fullMark: 100 },
      { defect: 'Пятна', value: (latest.stain / total) * 100, fullMark: 100 },
      { defect: 'Помутнения', value: (latest.cloudiness / total) * 100, fullMark: 100 },
      { defect: 'Деформации', value: (latest.deformation / total) * 100, fullMark: 100 }
    ];
  };

  const getDefectSummary = () => {
    if (defectTrends.length === 0) return null;
    const latest = defectTrends[defectTrends.length - 1];
    return {
      total: latest.total_defects,
      byType: [
        { name: 'Трещины', value: latest.crack, color: DEFECT_COLORS.crack },
        { name: 'Пузыри', value: latest.bubble, color: DEFECT_COLORS.bubble },
        { name: 'Сколы', value: latest.chip, color: DEFECT_COLORS.chip },
        { name: 'Пятна', value: latest.stain, color: DEFECT_COLORS.stain },
        { name: 'Помутнения', value: latest.cloudiness, color: DEFECT_COLORS.cloudiness },
        { name: 'Деформации', value: latest.deformation, color: DEFECT_COLORS.deformation }
      ]
    };
  };

  const handleRefresh = () => {
    fetchDefectTrends();
    fetchEfficiencyData();
    fetchCorrelations();
  };

  const handleExportData = () => {
    console.log('Export data');
  };

  return (
    <Box sx={{ 
      p: 3, 
      backgroundColor: RTU_COLORS.background,
      minHeight: '100vh'
    }}>
      <Paper elevation={0} sx={{ 
        p: 3, 
        mb: 3, 
        backgroundColor: RTU_COLORS.surface,
        borderRadius: 3,
        border: `1px solid ${withOpacity(RTU_COLORS.primary, 0.1)}`
      }}>
        <Stack direction="row" justifyContent="space-between" alignItems="center">
          <Box>
            <Typography variant="h4" fontWeight="bold" color={RTU_COLORS.textPrimary}>
              Аналитика производства
            </Typography>
            <Typography variant="body2" color={RTU_COLORS.textSecondary}>
              РТУ МИРЭА • Система мониторинга качества
            </Typography>
          </Box>

          <Stack direction="row" spacing={2} alignItems="center">
            <FormControl sx={{ minWidth: 180 }}>
              <InputLabel sx={{ color: RTU_COLORS.textSecondary }}>Временной диапазон</InputLabel>
              <Select
                value={timeRange}
                label="Временной диапазон"
                onChange={(e) => setTimeRange(e.target.value)}
                sx={{
                  backgroundColor: RTU_COLORS.background,
                  borderRadius: 2,
                  '& .MuiOutlinedInput-notchedOutline': {
                    borderColor: withOpacity(RTU_COLORS.primary, 0.3)
                  }
                }}
              >
                <MenuItem value="24h">Последние 24 часа</MenuItem>
                <MenuItem value="7d">Последние 7 дней</MenuItem>
                <MenuItem value="30d">Последние 30 дней</MenuItem>
              </Select>
            </FormControl>

            <IconButton onClick={handleRefresh} sx={{ color: RTU_COLORS.primary }}>
              <Refresh />
            </IconButton>

            <IconButton onClick={handleExportData} sx={{ color: RTU_COLORS.primary }}>
              <Download />
            </IconButton>
          </Stack>
        </Stack>
      </Paper>

      {error && (
        <Alert 
          severity="error" 
          sx={{ 
            mb: 2, 
            borderRadius: 2,
            backgroundColor: withOpacity(RTU_COLORS.error, 0.15),
            border: `1px solid ${withOpacity(RTU_COLORS.error, 0.3)}`
          }} 
          onClose={() => setError(null)}
        >
          {error}
        </Alert>
      )}

      {!loading && defectTrends.length > 0 && (
        <Grid container spacing={2} sx={{ mb: 3 }}>
          <Grid item xs={12} md={3}>
            <Card sx={{ 
              backgroundColor: RTU_COLORS.surface,
              borderRadius: 3,
              border: `1px solid ${withOpacity(RTU_COLORS.primary, 0.1)}`,
              height: '100%'
            }}>
              <CardContent>
                <Stack direction="row" justifyContent="space-between" alignItems="flex-start">
                  <Box>
                    <Typography variant="body2" color={RTU_COLORS.textSecondary}>
                      Всего дефектов
                    </Typography>
                    <Typography variant="h3" fontWeight="bold" color={RTU_COLORS.textPrimary}>
                      {getDefectSummary()?.total || 0}
                    </Typography>
                  </Box>
                  <Box sx={{ 
                    p: 1, 
                    borderRadius: 2, 
                    backgroundColor: withOpacity(RTU_COLORS.primary, 0.1)
                  }}>
                    <TrendingUp sx={{ color: RTU_COLORS.primary }} />
                  </Box>
                </Stack>
                <Typography variant="caption" color={RTU_COLORS.textSecondary} sx={{ mt: 1 }}>
                  За последние 24 часа
                </Typography>
              </CardContent>
            </Card>
          </Grid>

          <Grid item xs={12} md={3}>
            <Card sx={{ 
              backgroundColor: RTU_COLORS.surface,
              borderRadius: 3,
              border: `1px solid ${withOpacity(RTU_COLORS.accent, 0.1)}`,
              height: '100%'
            }}>
              <CardContent>
                <Stack direction="row" justifyContent="space-between" alignItems="flex-start">
                  <Box>
                    <Typography variant="body2" color={RTU_COLORS.textSecondary}>
                      Эффективность
                    </Typography>
                    <Typography variant="h3" fontWeight="bold" color={RTU_COLORS.accent}>
                      {efficiencyData.length > 0 
                        ? `${efficiencyData[efficiencyData.length - 1].efficiency_score.toFixed(1)}%` 
                        : '0%'}
                    </Typography>
                  </Box>
                  <Box sx={{ 
                    p: 1, 
                    borderRadius: 2, 
                    backgroundColor: withOpacity(RTU_COLORS.accent, 0.1)
                  }}>
                    <BarChartIcon sx={{ color: RTU_COLORS.accent }} />
                  </Box>
                </Stack>
