import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Slider } from '@/components/ui/slider';
import { Badge } from '@/components/ui/badge';

const API_BASE = 'http://localhost:8000';

const COLORS = {
  primary: '#0066FF',
  success: '#00E676',
  warning: '#FFD700',
  error: '#FF1744',
  info: '#00E5FF',
  text: '#FFFFFF',
  textSecondary: '#B8C5D6'
};

// Canvas Graph Visualization Component
const GraphCanvas = ({ data, loading }) => {
  const canvasRef = useRef(null);
  const [zoom, setZoom] = useState(1);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });

  const getNodeColor = (type) => {
    const colors = {
      defect: COLORS.error,
      parameter: COLORS.primary,
      equipment: COLORS.info,
      cause: COLORS.warning,
      recommendation: COLORS.success
    };
    return colors[type] || COLORS.primary;
  };

  useEffect(() => {
    if (!data || !canvasRef.current || loading) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const dpr = window.devicePixelRatio || 1;
    const rect = canvas.getBoundingClientRect();
    
    canvas.width = rect.width * dpr;
    canvas.height = rect.height * dpr;
    ctx.scale(dpr, dpr);

    const width = rect.width;
    const height = rect.height;
    const centerX = width / 2;
    const centerY = height / 2;

    ctx.clearRect(0, 0, width, height);

    if (!data.nodes || data.nodes.length === 0) {
      ctx.fillStyle = COLORS.textSecondary;
      ctx.font = '16px system-ui, sans-serif';
      ctx.textAlign = 'center';
      ctx.fillText('ÐÐµÑ‚ Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð´Ð»Ñ Ð¾Ñ‚Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ñ', centerX, centerY);
      return;
    }

    // Calculate node positions
    const radius = Math.min(width, height) * 0.3 * zoom;
    const nodePositions = new Map();
    
    data.nodes.forEach((node, i) => {
      const angle = (i / data.nodes.length) * Math.PI * 2 - Math.PI / 2;
      const x = centerX + offset.x + Math.cos(angle) * radius;
      const y = centerY + offset.y + Math.sin(angle) * radius;
      nodePositions.set(node.id, { x, y });
    });

    // Draw edges
    ctx.lineWidth = 2;
    if (data.edges) {
      data.edges.forEach(edge => {
        const source = nodePositions.get(edge.source);
        const target = nodePositions.get(edge.target);
        
        if (source && target) {
          const gradient = ctx.createLinearGradient(source.x, source.y, target.x, target.y);
          gradient.addColorStop(0, COLORS.primary + '80');
          gradient.addColorStop(1, COLORS.info + '80');
          
          ctx.strokeStyle = gradient;
          ctx.globalAlpha = 0.6;
          ctx.beginPath();
          ctx.moveTo(source.x, source.y);
          ctx.lineTo(target.x, target.y);
          ctx.stroke();
          ctx.globalAlpha = 1;

          // Draw arrow
          const angle = Math.atan2(target.y - source.y, target.x - source.x);
          const arrowSize = 10;
          ctx.fillStyle = COLORS.info + '99';
          ctx.beginPath();
          ctx.moveTo(target.x, target.y);
          ctx.lineTo(
            target.x - arrowSize * Math.cos(angle - Math.PI / 6),
            target.y - arrowSize * Math.sin(angle - Math.PI / 6)
          );
          ctx.lineTo(
            target.x - arrowSize * Math.cos(angle + Math.PI / 6),
            target.y - arrowSize * Math.sin(angle + Math.PI / 6)
          );
          ctx.closePath();
          ctx.fill();
        }
      });
    }

    // Draw nodes
    data.nodes.forEach((node) => {
      const pos = nodePositions.get(node.id);
      if (!pos) return;

      const nodeSize = 25 + (node.confidence || 0.5) * 20;
      const color = getNodeColor(node.nodeType || node.type);

      // Shadow
      ctx.shadowColor = color + '99';
      ctx.shadowBlur = 15;

      // Node circle
      const gradient = ctx.createRadialGradient(pos.x, pos.y, 0, pos.x, pos.y, nodeSize);
      gradient.addColorStop(0, color);
      gradient.addColorStop(1, color + '99');
      ctx.fillStyle = gradient;
      ctx.beginPath();
      ctx.arc(pos.x, pos.y, nodeSize, 0, Math.PI * 2);
      ctx.fill();

      ctx.shadowBlur = 0;

      // Border
      ctx.strokeStyle = COLORS.text;
      ctx.lineWidth = 2;
      ctx.stroke();

      // Label
      ctx.fillStyle = COLORS.text;
      ctx.font = 'bold 11px system-ui, sans-serif';
      ctx.textAlign = 'center';
      ctx.textBaseline = 'middle';
      
      const label = (node.label || node.name || 'Node').length > 12 
        ? (node.label || node.name).substring(0, 12) + '...' 
        : (node.label || node.name);
      ctx.fillText(label, pos.x, pos.y + nodeSize + 12);

      // Confidence
      if (node.confidence !== undefined) {
        ctx.fillStyle = COLORS.success + 'DD';
        ctx.font = 'bold 9px system-ui, sans-serif';
        ctx.fillText(`${(node.confidence * 100).toFixed(0)}%`, pos.x, pos.y);
      }
    });
  }, [data, zoom, offset, loading]);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX - offset.x, y: e.clientY - offset.y });
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    setOffset({
      x: e.clientX - dragStart.x,
      y: e.clientY - dragStart.y
    });
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  return (
    <div className="relative w-full h-[500px]">
      {loading && (
        <div className="absolute inset-0 flex items-center justify-center bg-black/50 z-10 rounded-lg">
          <div className="text-2xl animate-pulse" style={{ color: COLORS.primary }}>
            Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð³Ñ€Ð°Ñ„Ð°...
          </div>
        </div>
      )}
      
      <canvas
        ref={canvasRef}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        className="w-full h-full rounded-lg cursor-move"
        style={{ background: 'rgba(0, 8, 20, 0.6)' }}
      />

      {/* Controls */}
      <div className="absolute bottom-4 right-4 flex flex-col gap-2 bg-black/80 rounded-lg p-2 backdrop-blur-sm">
        <button
          onClick={() => setZoom(prev => Math.min(prev + 0.2, 3))}
          className="w-10 h-10 text-white hover:bg-white/20 rounded transition-colors text-xl"
          title="Ð£Ð²ÐµÐ»Ð¸Ñ‡Ð¸Ñ‚ÑŒ"
        >
          +
        </button>
        <button
          onClick={() => setZoom(prev => Math.max(prev - 0.2, 0.5))}
          className="w-10 h-10 text-white hover:bg-white/20 rounded transition-colors text-xl"
          title="Ð£Ð¼ÐµÐ½ÑŒÑˆÐ¸Ñ‚ÑŒ"
        >
          âˆ’
        </button>
        <button
          onClick={() => { setZoom(1); setOffset({ x: 0, y: 0 }); }}
          className="w-10 h-10 text-white hover:bg-white/20 rounded transition-colors text-lg"
          title="Ð¡Ð±Ñ€Ð¾ÑÐ¸Ñ‚ÑŒ"
        >
          âŸ²
        </button>
      </div>

      {/* Legend */}
      <div className="absolute top-4 left-4 bg-black/80 rounded-lg p-4 backdrop-blur-sm">
        <div className="text-xs font-bold text-gray-400 mb-2">Ð›Ð•Ð“Ð•ÐÐ”Ð</div>
        {[
          { type: 'defect', label: 'Ð”ÐµÑ„ÐµÐºÑ‚Ñ‹', color: COLORS.error },
          { type: 'parameter', label: 'ÐŸÐ°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ñ‹', color: COLORS.primary },
          { type: 'equipment', label: 'ÐžÐ±Ð¾Ñ€ÑƒÐ´Ð¾Ð²Ð°Ð½Ð¸Ðµ', color: COLORS.info },
          { type: 'cause', label: 'ÐŸÑ€Ð¸Ñ‡Ð¸Ð½Ñ‹', color: COLORS.warning },
          { type: 'recommendation', label: 'Ð ÐµÐºÐ¾Ð¼ÐµÐ½Ð´Ð°Ñ†Ð¸Ð¸', color: COLORS.success }
        ].map(item => (
          <div key={item.type} className="flex items-center gap-2 mb-1">
            <div
              className="w-3 h-3 rounded-full"
              style={{ background: item.color }}
            />
            <span className="text-xs text-gray-400">{item.label}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

// Main Component
