const generateTemperatureField = (centerTemp, length, width) => {
  const tempField = [];
  for (let i = 0; i < length; i++) {
    const row = [];
    for (let j = 0; j < width; j++) {
      // Температурный градиент - горячее в центре
      const distance = Math.sqrt(Math.pow(i - length / 2, 2) + Math.pow(j - width / 2, 2));
      const temp = centerTemp - distance * 15;
      row.push(temp);
    }
    tempField.push(row);
  }
  return tempField;
};

