// @ts-nocheck
import React, { useRef, useState, useEffect } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls, Text, Box as DreiBox, Sphere, Cylinder, Plane, Line, Html } from '@react-three/drei';
import * as THREE from 'three';
import { 
  Box, Chip, Paper, Typography, Slider, Button, ButtonGroup, 
  Grid, Card, CardContent, Switch, FormControlLabel, Divider,
  TextField, Stack, LinearProgress, Tooltip, IconButton,
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
  Tabs, Tab, Badge, Avatar, List, ListItem, ListItemText,
  Accordion, AccordionSummary, AccordionDetails
} from '@mui/material';
import { 
  PlayArrow, Pause, Refresh, CompareArrows, TrendingUp, Science,
  Warning, Error, CheckCircle, Thermostat, Speed, Opacity,
  Timeline, BubbleChart, Landscape, Settings, DataArray,
  ExpandMore, WaterDrop, Report, HeatPump, Factory,
  LineAxis, ScatterPlot, ShowChart, Animation, LiveTv
} from '@mui/icons-material';
import { systemApi, knowledgeGraphApi } from '../services/api';
import { useWebSocketStream } from '../hooks/useWebSocketStream';

// Цветовая палитра РТУ МИРЭА
const MIREA_COLORS = {
  primary: '#0066CC',
  secondary: '#FF6B35',
  success: '#00C853',
  warning: '#FFA726',
  error: '#EF5350',
  info: '#29B6F6',
  background: '#0A1929',
  backgroundLight: '#1A2332',
  text: '#FFFFFF',
  glassHot: '#FF3300',
  glassWarm: '#FF9900',
  glassCool: '#0099FF',
  glassCold: '#0066CC'
};

// Генератор температуры для печи
const generateTemperatureField = (centerTemp: number, length: number, width: number) => {
  const tempField = [];
  for (let i = 0; i < length; i++) {
    const row = [];
    for (let j = 0; j < width; j++) {
      // Температурный градиент - горячее в центре
      const distance = Math.sqrt(
        Math.pow(i - length/2, 2) + Math.pow(j - width/2, 2)
      );
      const temp = centerTemp - distance * 15;
      row.push(temp);
    }
    tempField.push(row);
  }
  return tempField;
};

// Компонент печи с температурными зонами
const EnhancedFurnace = ({ temperature, meltLevel = 0.8 }) => {
  const furnaceRef = useRef<THREE.Group>(null);
  const [heatWave, setHeatWave] = useState(0);
  const [temperatureField, setTemperatureField] = useState(() => 
    generateTemperatureField(temperature, 20, 8)
  );

  useEffect(() => {
    // Обновляем температурное поле при изменении температуры
    setTemperatureField(generateTemperatureField(temperature, 20, 8));
  }, [temperature]);

  useFrame((state) => {
    if (furnaceRef.current) {
      // Плавная пульсация нагрева
      setHeatWave(Math.sin(state.clock.elapsedTime * 2) * 0.5 + 0.5);
      
      // Легкое мерцание от тепловых волн
      furnaceRef.current.children.forEach((child, i) => {
        if (child.material && child.material.emissive) {
          const intensity = 0.3 + heatWave * 0.2 * (i % 3);
          child.material.emissiveIntensity = intensity;
        }
      });
    }
  });

  const getGlassColor = (temp: number) => {
    if (temp > 1600) return new THREE.Color(MIREA_COLORS.glassHot);
    if (temp > 1400) return new THREE.Color(MIREA_COLORS.warning);
    if (temp > 1200) return new THREE.Color(MIREA_COLORS.glassWarm);
    return new THREE.Color(MIREA_COLORS.glassCool);
  };

  return (
    <group ref={furnaceRef} position={[-8, 1, 0]}>
      {/* Корпус печи */}
      <DreiBox position={[0, 2, 0]} args={[6, 4, 4]} castShadow receiveShadow>
        <meshStandardMaterial 
          color={MIREA_COLORS.backgroundLight}
          metalness={0.8}
          roughness={0.4}
          emissive={MIREA_COLORS.secondary}
          emissiveIntensity={0.05}
        />
      </DreiBox>

      {/* Тепловые зоны внутри печи */}
      {temperatureField.map((row, i) => 
        row.map((temp, j) => (
          <DreiBox
            key={`${i}-${j}`}
            position={[
              -2.5 + (i / temperatureField.length) * 5,
              1.5,
              -1.5 + (j / row.length) * 3
            ]}
            args={[0.2, 0.1, 0.2]}
          >
            <meshStandardMaterial
              color={getGlassColor(temp)}
              emissive={getGlassColor(temp)}
              emissiveIntensity={0.1 + (temp - 1200) / 800 * 0.3}
              transparent
              opacity={0.7}
            />
          </DreiBox>
        ))
      )}

      {/* Уровень расплава */}
      <DreiBox
        position={[0, 0.5 + meltLevel * 1.5, 0]}
        args={[5.8, meltLevel * 3, 3.8]}
        castShadow
      >
        <meshStandardMaterial
          color={getGlassColor(temperature)}
          emissive={getGlassColor(temperature)}
          emissiveIntensity={0.4}
          transparent
          opacity={0.9}
          roughness={0.1}
        />
      </DreiBox>

      {/* Горелки */}
      {Array.from({ length: 3 }).map((_, i) => (
        <Cylinder
          key={i}
          position={[-2.5 + i * 2.5, 4.5, 1.8]}
          args={[0.3, 0.3, 0.5]}
          castShadow
        >
          <meshStandardMaterial
            color={MIREA_COLORS.error}
            emissive={MIREA_COLORS.error}
            emissiveIntensity={0.8}
          />
        </Cylinder>
      ))}

      {/* Тепловые волны */}
      <Sphere position={[0, 3, 0]} args={[3.2]} visible={heatWave > 0.6}>
        <meshBasicMaterial
          color={MIREA_COLORS.warning}
          transparent
          opacity={heatWave * 0.3}
          side={THREE.BackSide}
        />
      </Sphere>

      {/* Индикатор температуры */}
      <Html position={[0, 5, 0]} center>
        <Card sx={{ 
          bgcolor: 'rgba(0, 0, 0, 0.8)', 
          backdropFilter: 'blur(10px)',
          border: `2px solid ${MIREA_COLORS.secondary}` 
        }}>
          <CardContent sx={{ p: 1 }}>
            <Typography variant="h6" sx={{ color: MIREA_COLORS.text }}>
              <Thermostat sx={{ verticalAlign: 'middle', mr: 1 }} />
              {temperature.toFixed(0)}°C
            </Typography>
            <LinearProgress 
              variant="determinate" 
              value={Math.min(100, (temperature - 1000) / 10)}
              sx={{ 
                mt: 1,
                height: 8,
                borderRadius: 4,
                bgcolor: 'rgba(255, 255, 255, 0.1)',
                '& .MuiLinearProgress-bar': {
                  bgcolor: temperature > 1500 ? MIREA_COLORS.error : MIREA_COLORS.warning
                }
              }}
            />
          </CardContent>
        </Card>
      </Html>
    </group>
  );
};

// Улучшенная формовочная линия с физикой стекла
const EnhancedFormingLine = ({ speed, moldTemp, viscosity = 1200 }) => {
  const beltRef = useRef<THREE.Group>(null);
  const moldsRef = useRef<THREE.Group>(null);
  const [beltOffset, setBeltOffset] = useState(0);
  const [glassForms, setGlassForms] = useState<Array<{
    id: number;
    progress: number;
    position: [number, number, number];
    quality: number;
  }>>([]);

  useFrame((state) => {
    if (beltRef.current) {
      // Анимация движения конвейера
      const newOffset = (beltOffset + speed * 0.01) % 10;
      setBeltOffset(newOffset);
      beltRef.current.position.z = newOffset;

      // Обновление позиций стеклянных форм
      setGlassForms(prev => prev.map(form => ({
        ...form,
        position: [form.position[0], form.position[1], (form.position[2] + speed * 0.01) % 10],
        progress: Math.min(1, form.progress + speed * 0.0001)
      })).filter(form => form.progress < 1.2));
    }

    // Добавление новых форм
    if (Math.random() < 0.02 && glassForms.length < 10) {
      setGlassForms(prev => [...prev, {
        id: Date.now(),
        progress: 0,
        position: [-3, 0.5, 0],
        quality: 0.95 - Math.abs(moldTemp - 320) * 0.001
      }]);
    }
  });

  const getGlassFormColor = (progress: number, quality: number) => {
    if (progress < 0.3) return MIREA_COLORS.glassHot;
    if (progress < 0.6) return MIREA_COLORS.glassWarm;
    if (progress < 0.9) return MIREA_COLORS.glassCool;
    return quality > 0.9 ? MIREA_COLORS.success : MIREA_COLORS.warning;
  };

  return (
    <group position={[8, 0.5, 0]}>
      {/* Основание конвейера */}
      <DreiBox position={[0, 0, 0]} args={[8, 0.1, 2]} castShadow receiveShadow>
        <meshStandardMaterial color={MIREA_COLORS.backgroundLight} metalness={0.7} />
      </DreiBox>

      {/* Движущаяся лента */}
      <group ref={beltRef}>
        <DreiBox position={[0, 0.06, 0]} args={[7.8, 0.02, 1.8]} castShadow>
          <meshStandardMaterial 
            color={MIREA_COLORS.info}
            emissive={MIREA_COLORS.info}
            emissiveIntensity={0.1}
          />
        </DreiBox>
      </group>

      {/* Формы для стекла */}
      <group ref={moldsRef}>
        {Array.from({ length: 4 }).map((_, i) => (
          <group key={i} position={[-2.5 + i * 1.5, 0.3, 0]}>
            <Cylinder args={[0.4, 0.4, 0.2]} castShadow>
              <meshStandardMaterial 
                color={moldTemp > 350 ? MIREA_COLORS.error : MIREA_COLORS.info}
                emissive={moldTemp > 350 ? MIREA_COLORS.error : MIREA_COLORS.info}
                emissiveIntensity={0.2}
              />
            </Cylinder>
            <Text
              position={[0, 0.4, 0]}
              color="white"
              fontSize={0.2}
              anchorX="center"
            >
              {moldTemp.toFixed(0)}°C
            </Text>
          </group>
        ))}
      </group>

      {/* Стеклянные формы на конвейере */}
      {glassForms.map((form) => (
        <group key={form.id} position={form.position}>
          <DreiBox
            args={[0.6, 0.3 + form.progress * 0.5, 0.6]}
            castShadow
          >
            <meshStandardMaterial
              color={getGlassFormColor(form.progress, form.quality)}
              emissive={getGlassFormColor(form.progress, form.quality)}
              emissiveIntensity={0.2}
              transparent
              opacity={0.9 - form.progress * 0.3}
            />
          </DreiBox>
          {form.progress > 0.9 && form.quality < 0.9 && (
            <Text
              position={[0, 0.8, 0]}
              color={MIREA_COLORS.error}
              fontSize={0.15}
              anchorX="center"
            >
              !
            </Text>
          )}
        </group>
      ))}

      {/* Индикаторы параметров */}
      <Html position={[0, 2, 0]} center>
        <Stack spacing={1}>
          <Card sx={{ bgcolor: 'rgba(0, 0, 0, 0.8)', backdropFilter: 'blur(10px)' }}>
            <CardContent sx={{ p: 1 }}>
              <Typography variant="body2" sx={{ color: MIREA_COLORS.text }}>
                <Speed sx={{ verticalAlign: 'middle', mr: 1 }} />
                {speed.toFixed(1)} м/мин
              </Typography>
            </CardContent>
          </Card>
          <Card sx={{ bgcolor: 'rgba(0, 0, 0, 0.8)', backdropFilter: 'blur(10px)' }}>
            <CardContent sx={{ p: 1 }}>
              <Typography variant="body2" sx={{ color: MIREA_COLORS.text }}>
                <Opacity sx={{ verticalAlign: 'middle', mr: 1 }} />
                Вязкость: {(viscosity / 1000).toFixed(1)} кПа·с
              </Typography>
            </CardContent>
          </Card>
        </Stack>
      </Html>
    </group>
  );
};

// Продвинутая визуализация дефектов
const AdvancedDefectVisualization = ({ defects, temperature = 1500 }) => {
  const particlesRef = useRef<THREE.Group>(null);
  const [defectParticles, setDefectParticles] = useState<Array<{
    id: number;
    type: string;
    position: [number, number, number];
    velocity: [number, number, number];
    size: number;
    intensity: number;
  }>>([]);

  useEffect(() => {
    // Генерация частиц дефектов на основе данных
    const newParticles: typeof defectParticles = [];
    Object.entries(defects).forEach(([type, severity]) => {
      const count = Math.floor(severity * 10);
      for (let i = 0; i < count; i++) {
        newParticles.push({
          id: Date.now() + i,
          type,
          position: [
            (Math.random() - 0.5) * 15,
            Math.random() * 3,
            (Math.random() - 0.5) * 10
          ],
          velocity: [
            (Math.random() - 0.5) * 0.1,
            Math.random() * 0.05,
            (Math.random() - 0.5) * 0.1
          ],
          size: 0.1 + severity * 0.2,
          intensity: 0.3 + severity * 0.7
        });
      }
    });
    setDefectParticles(newParticles);
  }, [defects]);

  useFrame(() => {
    if (particlesRef.current) {
      // Анимация частиц
      setDefectParticles(prev => prev.map(p => ({
        ...p,
        position: [
          p.position[0] + p.velocity[0],
          Math.max(0.1, p.position[1] + p.velocity[1]),
          p.position[2] + p.velocity[2]
        ],
        velocity: [
          p.velocity[0] * 0.99,
          p.velocity[1] - 0.001,
          p.velocity[2] * 0.99
        ]
      })).filter(p => p.position[1] > 0));
    }
  });

  const getDefectColor = (type: string) => {
    switch (type) {
      case 'crack': return MIREA_COLORS.error;
      case 'bubble': return MIREA_COLORS.warning;
      case 'chip': return MIREA_COLORS.info;
      case 'cloudiness': return '#AAAAAA';
      case 'deformation': return MIREA_COLORS.secondary;
      default: return MIREA_COLORS.text;
    }
  };

  const getDefectIcon = (type: string) => {
    switch (type) {
      case 'crack': return '⚡';
      case 'bubble': return '○';
      case 'chip': return '✖';
      case 'cloudiness': return '☁';
      case 'deformation': return '≈';
      default: return '?';
    }
  };

  return (
    <group ref={particlesRef}>
      {/* Основные частицы дефектов */}
      {defectParticles.map((particle) => (
        <React.Fragment key={particle.id}>
          <Sphere
            position={particle.position}
            args={[particle.size]}
            castShadow
          >
            <meshStandardMaterial
              color={getDefectColor(particle.type)}
              emissive={getDefectColor(particle.type)}
              emissiveIntensity={particle.intensity}
              transparent
              opacity={0.7}
            />
          </Sphere>
          <Text
            position={[particle.position[0], particle.position[1] + particle.size + 0.1, particle.position[2]]}
            color={getDefectColor(particle.type)}
            fontSize={particle.size * 2}
            anchorX="center"
            anchorY="middle"
          >
            {getDefectIcon(particle.type)}
          </Text>
        </React.Fragment>
      ))}

      {/* Тепловые карты дефектов */}
      {temperature > 1500 && Object.keys(defects).length > 0 && (
        <Plane
          rotation={[-Math.PI / 2, 0, 0]}
          position={[0, 0.1, 0]}
          args={[20, 10]}
        >
          <meshBasicMaterial
            color={MIREA_COLORS.error}
            transparent
            opacity={0.1}
            side={THREE.DoubleSide}
          />
        </Plane>
      )}

      {/* Информационная панель дефектов */}
      <Html position={[-15, 4, 0]}>
        <Card sx={{ 
          bgcolor: 'rgba(0, 0, 0, 0.9)', 
          backdropFilter: 'blur(20px)',
          border: '2px solid rgba(255, 0, 0, 0.3)',
          minWidth: 250
        }}>
          <CardContent>
            <Typography variant="h6" sx={{ color: MIREA_COLORS.text, mb: 2, display: 'flex', alignItems: 'center' }}>
              <Warning sx={{ mr: 1, color: MIREA_COLORS.error }} />
              Карта дефектов
            </Typography>
            <List dense>
              {Object.entries(defects).map(([type, severity]) => (
                <ListItem key={type}>
                  <ListItemText
                    primary={
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Typography variant="body2" sx={{ color: getDefectColor(type) }}>
                          {type.toUpperCase()}
                        </Typography>
                        <Typography variant="body2" sx={{ color: MIREA_COLORS.text }}>
                          {(severity * 100).toFixed(1)}%
                        </Typography>
                      </Box>
                    }
                    secondary={
                      <LinearProgress 
                        variant="determinate" 
                        value={severity * 100}
                        sx={{ 
                          mt: 0.5,
                          height: 4,
                          borderRadius: 2,
                          bgcolor: 'rgba(255, 255, 255, 0.1)',
                          '& .MuiLinearProgress-bar': {
                            bgcolor: getDefectColor(type)
                          }
                        }}
                      />
                    }
                  />
                </ListItem>
              ))}
            </List>
          </CardContent>
        </Card>
      </Html>
    </group>
  );
};

// Физическая модель течения стекла
const GlassFlowPhysics = ({ temperature, viscosity, flowRate = 1.0 }) => {
  const flowRef = useRef<THREE.Group>(null);
  const [flowLines, setFlowLines] = useState<Array<{
    points: [number, number, number][];
    age: number;
  }>>([]);

  useFrame((state) => {
    if (flowRef.current && temperature > 1400) {
      // Генерация линий течения
      if (Math.random() < flowRate * 0.1) {
        const newLine = {
          points: [
            [-5, 1 + Math.random() * 0.5, (Math.random() - 0.5) * 2],
            [-3, 1 + Math.random() * 0.5, (Math.random() - 0.5) * 2],
            [-1, 1 + Math.random() * 0.5, (Math.random() - 0.5) * 2],
            [1, 1 + Math.random() * 0.5, (Math.random() - 0.5) * 2]
          ] as [number, number, number][],
          age: 0
        };
        setFlowLines(prev => [...prev.slice(-20), newLine]);
      }

      // Анимация линий течения
      setFlowLines(prev => prev.map(line => ({
        ...line,
        points: line.points.map(([x, y, z]) => [
          x + 0.1 * flowRate,
          y + (Math.random() - 0.5) * 0.02,
          z + (Math.random() - 0.5) * 0.01
        ]) as [number, number, number][],
        age: line.age + 0.01
      })).filter(line => line.age < 1));
    }
  });

  const getFlowColor = () => {
    if (temperature > 1600) return MIREA_COLORS.glassHot;
    if (temperature > 1400) return MIREA_COLORS.warning;
    return MIREA_COLORS.glassWarm;
  };

  return (
    <group ref={flowRef}>
      {flowLines.map((line, i) => (
        <Line
          key={i}
          points={line.points}
          color={getFlowColor()}
          lineWidth={3}
          transparent
          opacity={1 - line.age}
        />
      ))}

      {/* Визуализация вязкости */}
      <Html position={[-8, 3, 0]}>
        <Card sx={{ bgcolor: 'rgba(0, 0, 0, 0.8)', backdropFilter: 'blur(10px)' }}>
          <CardContent sx={{ p: 1 }}>
            <Typography variant="body2" sx={{ color: MIREA_COLORS.text }}>
              <WaterDrop sx={{ verticalAlign: 'middle', mr: 1 }} />
              Вязкость: {(viscosity / 1000).toFixed(2)} кПа·с
            </Typography>
          </CardContent>
        </Card>
      </Html>
    </group>
  );
};

