// Главный компонент Digital Twin
const EnhancedDigitalTwin3D = () => {
  // WebSocket подключение для реальных данных
  const { wsData, isConnected } = useWebSocketStream();
  
  // Состояния реального производства
  const [realState, setRealState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    viscosity: 1200,
    flowRate: 1.0,
    defects: { crack: 0.1, bubble: 0.05, chip: 0.03, cloudiness: 0.02, deformation: 0.01 },
    quality: 95.2
  });
  
  // Состояния прогноза
  const [predictedState, setPredictedState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    viscosity: 1200,
    flowRate: 1.0,
    defects: { crack: 0.12, bubble: 0.06, chip: 0.04, cloudiness: 0.03, deformation: 0.02 },
    quality: 94.8
  });
  
  // What-If состояние
  const [whatIfState, setWhatIfState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    viscosity: 1200,
    flowRate: 1.0
  });
  
  // Результаты What-If анализа
  const [whatIfResults, setWhatIfResults] = useState({
    defects: { crack: 0.1, bubble: 0.05, chip: 0.03, cloudiness: 0.02, deformation: 0.01 },
    quality: 95.2,
    riskLevel: 'LOW',
    recommendations: []
  });
  
  // UI состояния
  const [shadowMode, setShadowMode] = useState(true);
  const [whatIfMode, setWhatIfMode] = useState(false);
  const [showPhysics, setShowPhysics] = useState(true);
  const [isSimulating, setIsSimulating] = useState(false);
  const [activeTab, setActiveTab] = useState(0);
  
  // Обновление реальных данных из WebSocket
  useEffect(() => {
    if (wsData.parameters) {
      const newState = {
        furnaceTemp: wsData.parameters.furnace?.temperature || 1520,
        moldTemp: wsData.parameters.forming?.mold_temp || 320,
        beltSpeed: wsData.parameters.forming?.speed || 150,
        viscosity: calculateViscosity(wsData.parameters.furnace?.temperature || 1520),
        flowRate: 1.0,
        defects: wsData.defectAggregation || { crack: 0.1, bubble: 0.05, chip: 0.03, cloudiness: 0.02, deformation: 0.01 },
        quality: wsData.qualityMetrics?.qualityRate || 95.2
      };
      
      setRealState(newState);
      
      // Симуляция прогноза
      setTimeout(() => {
        setPredictedState({
          ...newState,
          furnaceTemp: newState.furnaceTemp + (Math.random() - 0.5) * 10,
          moldTemp: newState.moldTemp + (Math.random() - 0.5) * 5,
          beltSpeed: newState.beltSpeed + (Math.random() - 0.5) * 3,
          defects: {
            crack: newState.defects.crack * (0.9 + Math.random() * 0.2),
            bubble: newState.defects.bubble * (0.9 + Math.random() * 0.2),
            chip: newState.defects.chip * (0.9 + Math.random() * 0.2),
            cloudiness: newState.defects.cloudiness * (0.9 + Math.random() * 0.2),
            deformation: newState.defects.deformation * (0.9 + Math.random() * 0.2)
          },
          quality: newState.quality * (0.99 + Math.random() * 0.02)
        });
      }, 1000);
    }
  }, [wsData]);
  
  // Функция расчета вязкости по температуре
  const calculateViscosity = (temp: number) => {
    // Упрощенная модель вязкости стекла
    const baseViscosity = 1000000; // Па·с при комнатной температуре
    const activationEnergy = 300000; // Дж/моль
    const gasConstant = 8.314;
    const referenceTemp = 1500; // К
    
    return baseViscosity * Math.exp(activationEnergy / gasConstant * (1/temp - 1/referenceTemp));
  };
  
  // Запуск What-If анализа
  const runWhatIfAnalysis = async () => {
    setIsSimulating(true);
    
    try {
      // Используем Knowledge Graph API для анализа
      const causes = await knowledgeGraphApi.getCausesOfDefect('crack', 0.5);
      
      // Рассчитываем предсказанные дефекты на основе физики
      const tempDev = Math.abs(whatIfState.furnaceTemp - 1520) / 50;
      const speedDev = Math.abs(whatIfState.beltSpeed - 150) / 20;
      const moldDev = Math.abs(whatIfState.moldTemp - 320) / 30;
      
      const newDefects = {
        crack: Math.min(0.2, 0.05 + tempDev * 0.3 + speedDev * 0.2),
        bubble: Math.min(0.15, 0.03 + tempDev * 0.2 + moldDev * 0.3),
        chip: Math.min(0.1, 0.02 + speedDev * 0.4),
        cloudiness: Math.min(0.12, 0.01 + tempDev * 0.4),
        deformation: Math.min(0.18, 0.02 + speedDev * 0.3 + moldDev * 0.3)
      };
      
      // Расчет качества
      const totalDefectScore = Object.values(newDefects).reduce((a, b) => a + b, 0);
      const quality = Math.max(70, 100 - totalDefectScore * 300);
      
      // Определение уровня риска
      let riskLevel = 'LOW';
      if (totalDefectScore > 0.3) riskLevel = 'HIGH';
      else if (totalDefectScore > 0.15) riskLevel = 'MEDIUM';
      
      // Генерация рекомендаций
      const recommendations = [];
      if (whatIfState.furnaceTemp > 1600) recommendations.push('Снизить температуру печи на 20°C');
      if (whatIfState.beltSpeed > 170) recommendations.push('Уменьшить скорость ленты на 10 м/мин');
      if (whatIfState.moldTemp < 300) recommendations.push('Увеличить температуру формы на 20°C');
      if (newDefects.crack > 0.15) recommendations.push('Увеличить время отжига');
      
      setWhatIfResults({
        defects: newDefects,
        quality,
        riskLevel,
        recommendations
      });
      
    } catch (error) {
      console.error('Ошибка What-If анализа:', error);
    } finally {
      setIsSimulating(false);
    }
  };
  
  // Сброс What-If параметров
  const resetWhatIf = () => {
    setWhatIfState({
      furnaceTemp: realState.furnaceTemp,
      moldTemp: realState.moldTemp,
      beltSpeed: realState.beltSpeed,
      viscosity: realState.viscosity,
      flowRate: 1.0
    });
    setWhatIfResults({
      defects: realState.defects,
      quality: realState.quality,
      riskLevel: 'LOW',
      recommendations: []
    });
  };
  
  // Определение отображаемого состояния
  const displayState = whatIfMode ? 
    { ...whatIfState, defects: whatIfResults.defects, quality: whatIfResults.quality } : 
    (shadowMode ? predictedState : realState);
  
  // Получение цвета для уровня риска
  const getRiskColor = (level: string) => {
    switch (level) {
      case 'HIGH': return MIREA_COLORS.error;
      case 'MEDIUM': return MIREA_COLORS.warning;
      case 'LOW': return MIREA_COLORS.success;
      default: return MIREA_COLORS.info;
    }
  };
  
  // Визуализация производственных метрик
  const renderProductionMetrics = () => (
    <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: 2, mb: 3 }}>
      <Card sx={{ bgcolor: 'rgba(255, 107, 53, 0.1)', border: `1px solid ${MIREA_COLORS.secondary}` }}>
        <CardContent>
          <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
            ТЕМПЕРАТУРА ПЕЧИ
          </Typography>
          <Typography variant="h4" sx={{ color: MIREA_COLORS.secondary, fontWeight: 700 }}>
            {displayState.furnaceTemp.toFixed(0)}°C
          </Typography>
          <LinearProgress 
            variant="determinate" 
            value={Math.min(100, (displayState.furnaceTemp - 1000) / 8)}
            sx={{ 
              mt: 1,
              height: 6,
              borderRadius: 3,
              bgcolor: 'rgba(255, 255, 255, 0.1)',
              '& .MuiLinearProgress-bar': {
                bgcolor: displayState.furnaceTemp > 1550 ? MIREA_COLORS.error : MIREA_COLORS.secondary
              }
            }}
          />
        </CardContent>
      </Card>
      
      <Card sx={{ bgcolor: 'rgba(41, 182, 246, 0.1)', border: `1px solid ${MIREA_COLORS.info}` }}>
        <CardContent>
          <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
            КАЧЕСТВО ПРОДУКЦИИ
          </Typography>
          <Typography variant="h4" sx={{ color: MIREA_COLORS.info, fontWeight: 700 }}>
            {displayState.quality.toFixed(1)}%
          </Typography>
          <LinearProgress 
            variant="determinate" 
            value={displayState.quality}
            sx={{ 
              mt: 1,
              height: 6,
              borderRadius: 3,
              bgcolor: 'rgba(255, 255, 255, 0.1)',
              '& .MuiLinearProgress-bar': {
                bgcolor: displayState.quality > 95 ? MIREA_COLORS.success : 
                        displayState.quality > 90 ? MIREA_COLORS.warning : MIREA_COLORS.error
              }
            }}
          />
        </CardContent>
      </Card>
      
      <Card sx={{ bgcolor: 'rgba(0, 200, 83, 0.1)', border: `1px solid ${MIREA_COLORS.success}` }}>
        <CardContent>
          <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
            СКОРОСТЬ ПРОИЗВОДСТВА
          </Typography>
          <Typography variant="h4" sx={{ color: MIREA_COLORS.success, fontWeight: 700 }}>
            {displayState.beltSpeed.toFixed(0)} м/мин
          </Typography>
          <LinearProgress 
            variant="determinate" 
            value={Math.min(100, displayState.beltSpeed / 2)}
            sx={{ 
              mt: 1,
              height: 6,
              borderRadius: 3,
              bgcolor: 'rgba(255, 255, 255, 0.1)',
              '& .MuiLinearProgress-bar': {
                bgcolor: displayState.beltSpeed > 170 ? MIREA_COLORS.error : 
                        displayState.beltSpeed > 160 ? MIREA_COLORS.warning : MIREA_COLORS.success
              }
            }}
          />
        </CardContent>
      </Card>
    </Box>
  );
  
  // Визуализация дефектов
  const renderDefectsDashboard = () => (
    <Card sx={{ bgcolor: 'rgba(0, 0, 0, 0.7)', backdropFilter: 'blur(10px)', mb: 3 }}>
      <CardContent>
        <Typography variant="h6" sx={{ color: MIREA_COLORS.text, mb: 2, display: 'flex', alignItems: 'center' }}>
          <Report sx={{ mr: 1, color: MIREA_COLORS.error }} />
          АНАЛИЗ ДЕФЕКТОВ
        </Typography>
        
        <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(5, 1fr)', gap: 1.5 }}>
          {Object.entries(displayState.defects).map(([type, severity]) => {
            const color = {
              crack: MIREA_COLORS.error,
              bubble: MIREA_COLORS.warning,
              chip: MIREA_COLORS.info,
              cloudiness: '#AAAAAA',
              deformation: MIREA_COLORS.secondary
            }[type];
            
            const icon = {
              crack: '⚡',
              bubble: '○',
              chip: '✖',
              cloudiness: '☁',
              deformation: '≈'
            }[type];
            
            return (
              <Card key={type} sx={{ 
                bgcolor: `rgba(${parseInt(color.slice(1, 3), 16)}, ${parseInt(color.slice(3, 5), 16)}, ${parseInt(color.slice(5, 7), 16)}, 0.1)`,
                border: `1px solid ${color}60`
              }}>
                <CardContent sx={{ p: 2, textAlign: 'center' }}>
                  <Typography variant="h3" sx={{ color, mb: 1 }}>
                    {icon}
                  </Typography>
                  <Typography variant="caption" sx={{ color: MIREA_COLORS.text, display: 'block' }}>
                    {type.toUpperCase()}
                  </Typography>
                  <Typography variant="h5" sx={{ color, fontWeight: 700 }}>
                    {(severity * 100).toFixed(1)}%
                  </Typography>
                  <LinearProgress 
                    variant="determinate" 
                    value={severity * 100}
                    sx={{ 
                      mt: 1,
                      height: 4,
                      borderRadius: 2,
                      bgcolor: 'rgba(255, 255, 255, 0.1)',
                      '& .MuiLinearProgress-bar': { bgcolor: color }
                    }}
                  />
                </CardContent>
              </Card>
            );
          })}
        </Box>
      </CardContent>
    </Card>
  );

  return (
    <Box sx={{ width: '100%', height: '100vh', bgcolor: MIREA_COLORS.background, p: 3 }}>
      {/* Заголовок и статус */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Box>
          <Typography variant="h3" sx={{ 
            color: MIREA_COLORS.text, 
            fontWeight: 900,
            background: `linear-gradient(90deg, ${MIREA_COLORS.primary}, ${MIREA_COLORS.secondary})`,
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            <Factory sx={{ verticalAlign: 'middle', mr: 2 }} />
            Digital Twin • Glass Production
          </Typography>
          <Typography variant="body2" sx={{ color: MIREA_COLORS.text + 'CC', mt: 0.5 }}>
            Интеллектуальная система мониторинга и оптимизации производства стекла
          </Typography>
        </Box>
        
        <Stack direction="row" spacing={2}>
          <Chip
            icon={<LiveTv />}
            label={isConnected ? 'В РЕАЛЬНОМ ВРЕМЕНИ' : 'ОФФЛАЙН'}
            color={isConnected ? 'success' : 'error'}
            sx={{ fontWeight: 700 }}
          />
          <Chip
            icon={<Animation />}
            label="ФИЗИКА ВКЛЮЧЕНА"
            color={showPhysics ? 'primary' : 'default'}
            onClick={() => setShowPhysics(!showPhysics)}
            sx={{ fontWeight: 700, cursor: 'pointer' }}
          />
        </Stack>
      </Box>
      
      {/* Основная панель управления */}
      <Card sx={{ mb: 3, bgcolor: 'rgba(0, 0, 0, 0.6)', backdropFilter: 'blur(20px)' }}>
        <CardContent>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} md={6}>
              <Stack direction="row" spacing={2} alignItems="center">
                <ButtonGroup variant="contained" sx={{ bgcolor: 'rgba(0, 0, 0, 0.3)' }}>
                  <Button
                    startIcon={<CompareArrows />}
                    onClick={() => { setShadowMode(true); setWhatIfMode(false); }}
                    sx={{ 
                      bgcolor: shadowMode ? MIREA_COLORS.primary : 'transparent',
                      '&:hover': { bgcolor: shadowMode ? MIREA_COLORS.primary : 'rgba(255,255,255,0.1)' }
                    }}
                  >
                    Shadow Mode
                  </Button>
                  <Button
                    startIcon={<Science />}
                    onClick={() => { setWhatIfMode(true); setShadowMode(false); }}
                    sx={{ 
                      bgcolor: whatIfMode ? MIREA_COLORS.secondary : 'transparent',
                      '&:hover': { bgcolor: whatIfMode ? MIREA_COLORS.secondary : 'rgba(255,255,255,0.1)' }
                    }}
                  >
                    What-If Analysis
                  </Button>
                  <Button
                    startIcon={<Timeline />}
                    onClick={() => { setShadowMode(false); setWhatIfMode(false); }}
                    sx={{ 
                      bgcolor: (!shadowMode && !whatIfMode) ? MIREA_COLORS.info : 'transparent',
                      '&:hover': { bgcolor: (!shadowMode && !whatIfMode) ? MIREA_COLORS.info : 'rgba(255,255,255,0.1)' }
                    }}
                  >
                    Real-Time
                  </Button>
                </ButtonGroup>
              </Stack>
            </Grid>
            
            <Grid item xs={12} md={6}>
              <Stack direction="row" spacing={1} justifyContent="flex-end" alignItems="center">
                <Tooltip title="Симулировать What-If анализ">
                  <Button
                    variant="contained"
                    startIcon={<PlayArrow />}
                    onClick={runWhatIfAnalysis}
                    disabled={isSimulating || !whatIfMode}
                    sx={{ 
                      bgcolor: MIREA_COLORS.secondary,
                      '&:hover': { bgcolor: MIREA_COLORS.secondary + 'CC' }
                    }}
                  >
                    {isSimulating ? 'Анализ...' : 'Запуск анализа'}
                  </Button>
                </Tooltip>
                
                <Tooltip title="Сброс параметров">
                  <IconButton onClick={resetWhatIf} sx={{ color: MIREA_COLORS.text }}>
                    <Refresh />
                  </IconButton>
                </Tooltip>
              </Stack>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
      
      {/* Основной контент */}
      <Grid container spacing={3}>
        {/* 3D визуализация */}
        <Grid item xs={12} lg={whatIfMode ? 8 : 9}>
          <Card sx={{ 
            height: 'calc(100vh - 250px)', 
            position: 'relative',
            overflow: 'hidden',
            border: `2px solid ${whatIfMode ? MIREA_COLORS.secondary : shadowMode ? MIREA_COLORS.primary : MIREA_COLORS.info}`,
            bgcolor: MIREA_COLORS.background
          }}>
            <Canvas
              shadows
              camera={{ position: [20, 10, 15], fov: 45 }}
              style={{ background: `linear-gradient(135deg, ${MIREA_COLORS.background}, ${MIREA_COLORS.backgroundLight})` }}
            >
              <EnhancedDigitalTwinScene 
                furnaceTemp={displayState.furnaceTemp}
                moldTemp={displayState.moldTemp}
                beltSpeed={displayState.beltSpeed}
                defects={displayState.defects}
                viscosity={displayState.viscosity}
                flowRate={displayState.flowRate}
                showPhysics={showPhysics}
              />
            </Canvas>
            
            {/* Наложение с информацией */}
            <Box sx={{ 
              position: 'absolute', 
              top: 16, 
              left: 16,
              display: 'flex',
              flexDirection: 'column',
              gap: 2
            }}>
              {/* Информация о режиме */}
              <Card sx={{ 
                bgcolor: 'rgba(0, 0, 0, 0.85)', 
                backdropFilter: 'blur(10px)',
                border: `1px solid ${whatIfMode ? MIREA_COLORS.secondary : shadowMode ? MIREA_COLORS.primary : MIREA_COLORS.info}`
              }}>
                <CardContent sx={{ p: 2 }}>
                  <Typography variant="caption" sx={{ 
                    color: whatIfMode ? MIREA_COLORS.secondary : shadowMode ? MIREA_COLORS.primary : MIREA_COLORS.info,
                    fontWeight: 700,
                    display: 'block',
                    mb: 1
                  }}>
                    {whatIfMode ? '🧪 WHAT-IF АНАЛИЗ' : shadowMode ? '🔮 SHADOW MODE' : '⚡ РЕАЛЬНОЕ ВРЕМЯ'}
                  </Typography>
                  
                  {shadowMode && (
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 0.5 }}>
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
                        <span style={{ color: MIREA_COLORS.info }}>Прогноз:</span> {predictedState.furnaceTemp.toFixed(0)}°C
                      </Typography>
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
                        <span style={{ color: MIREA_COLORS.success }}>Реальность:</span> {realState.furnaceTemp.toFixed(0)}°C
                      </Typography>
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.warning }}>
                        Точность прогноза: ~{Math.abs(100 - Math.abs(predictedState.furnaceTemp - realState.furnaceTemp) / 10).toFixed(1)}%
                      </Typography>
                    </Box>
                  )}
                  
                  {whatIfMode && (
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 0.5 }}>
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
                        <span style={{ color: MIREA_COLORS.secondary }}>Симуляция:</span> {whatIfState.furnaceTemp.toFixed(0)}°C
                      </Typography>
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
                        <span style={{ color: getRiskColor(whatIfResults.riskLevel) }}>Риск:</span> {whatIfResults.riskLevel}
                      </Typography>
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.success }}>
                        Прогноз качества: {whatIfResults.quality.toFixed(1)}%
                      </Typography>
                    </Box>
                  )}
                </CardContent>
              </Card>
              
              {/* Легенда дефектов */}
              <Card sx={{ 
                bgcolor: 'rgba(0, 0, 0, 0.85)', 
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255, 255, 255, 0.1)'
              }}>
                <CardContent sx={{ p: 2 }}>
                  <Typography variant="caption" sx={{ color: MIREA_COLORS.text, fontWeight: 700, mb: 1, display: 'block' }}>
                    🎨 ЛЕГЕНДА ДЕФЕКТОВ
                  </Typography>
                  <Stack spacing={0.5}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: MIREA_COLORS.error }} />
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>Трещины</Typography>
                    </Box>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: MIREA_COLORS.warning }} />
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>Пузыри</Typography>
                    </Box>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: MIREA_COLORS.info }} />
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>Сколы</Typography>
                    </Box>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: '#AAAAAA' }} />
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>Помутнение</Typography>
                    </Box>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: MIREA_COLORS.secondary }} />
                      <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>Деформация</Typography>
                    </Box>
                  </Stack>
                </CardContent>
              </Card>
            </Box>
          </Card>
          
          {/* Метрики производства */}
          {renderProductionMetrics()}
          {renderDefectsDashboard()}
        </Grid>
        
        {/* Панель What-If анализа или доп. информация */}
        <Grid item xs={12} lg={whatIfMode ? 4 : 3}>
          <Card sx={{ 
            height: 'calc(100vh - 250px)', 
            overflow: 'auto',
            bgcolor: 'rgba(0, 0, 0, 0.7)',
            backdropFilter: 'blur(20px)',
            border: `2px solid ${whatIfMode ? MIREA_COLORS.secondary : MIREA_COLORS.primary}`,
            '&::-webkit-scrollbar': { width: '8px' },
            '&::-webkit-scrollbar-track': { bgcolor: 'rgba(0,0,0,0.3)' },
            '&::-webkit-scrollbar-thumb': { bgcolor: MIREA_COLORS.primary, borderRadius: '4px' }
          }}>
            <CardContent>
              {whatIfMode ? (
                <>
                  <Typography variant="h5" sx={{ 
                    color: MIREA_COLORS.secondary, 
                    mb: 3, 
                    fontWeight: 700,
                    display: 'flex',
                    alignItems: 'center'
                  }}>
                    <Science sx={{ mr: 1 }} />
                    WHAT-IF АНАЛИЗ
                  </Typography>
                  
                  <Divider sx={{ mb: 3, borderColor: 'rgba(255, 255, 255, 0.1)' }} />
                  
                  <Tabs value={activeTab} onChange={(_, v) => setActiveTab(v)} sx={{ mb: 3 }}>
                    <Tab label="Параметры" />
                    <Tab label="Результаты" />
                    <Tab label="Рекомендации" />
                  </Tabs>
                  
                  {activeTab === 0 && (
                    <Stack spacing={3}>
                      <Box>
                        <Typography variant="body2" sx={{ color: MIREA_COLORS.text, mb: 1, display: 'flex', alignItems: 'center' }}>
                          <Thermostat sx={{ mr: 1, color: MIREA_COLORS.secondary }} />
                          Температура печи: {whatIfState.furnaceTemp}°C
                        </Typography>
                        <Slider
                          value={whatIfState.furnaceTemp}
                          onChange={(_, v) => setWhatIfState({ ...whatIfState, furnaceTemp: v as number })}
                          min={1400}
                          max={1700}
                          step={10}
                          marks={[
                            { value: 1400, label: '1400' },
                            { value: 1520, label: '1520' },
                            { value: 1700, label: '1700' }
                          ]}
                          sx={{ color: MIREA_COLORS.secondary }}
                        />
                      </Box>
                      
                      <Box>
                        <Typography variant="body2" sx={{ color: MIREA_COLORS.text, mb: 1, display: 'flex', alignItems: 'center' }}>
                          <HeatPump sx={{ mr: 1, color: MIREA_COLORS.warning }} />
                          Температура формы: {whatIfState.moldTemp}°C
                        </Typography>
                        <Slider
                          value={whatIfState.moldTemp}
                          onChange={(_, v) => setWhatIfState({ ...whatIfState, moldTemp: v as number })}
                          min={280}
                          max={380}
                          step={5}
                          marks={[
                            { value: 280, label: '280' },
                            { value: 320, label: '320' },
                            { value: 380, label: '380' }
                          ]}
                          sx={{ color: MIREA_COLORS.warning }}
                        />
                      </Box>
                      
                      <Box>
                        <Typography variant="body2" sx={{ color: MIREA_COLORS.text, mb: 1, display: 'flex', alignItems: 'center' }}>
                          <Speed sx={{ mr: 1, color: MIREA_COLORS.info }} />
                          Скорость ленты: {whatIfState.beltSpeed} м/мин
                        </Typography>
                        <Slider
                          value={whatIfState.beltSpeed}
                          onChange={(_, v) => setWhatIfState({ ...whatIfState, beltSpeed: v as number })}
                          min={100}
                          max={200}
                          step={5}
                          marks={[
                            { value: 100, label: '100' },
                            { value: 150, label: '150' },
                            { value: 200, label: '200' }
                          ]}
                          sx={{ color: MIREA_COLORS.info }}
                        />
                      </Box>
                    </Stack>
                  )}
                  
                  {activeTab === 1 && (
                    <Stack spacing={2}>
                      <Card sx={{ bgcolor: `rgba(${getRiskColor(whatIfResults.riskLevel) === MIREA_COLORS.error ? '239,83,80' : getRiskColor(whatIfResults.riskLevel) === MIREA_COLORS.warning ? '255,167,38' : '0,200,83'}, 0.1)` }}>
                        <CardContent>
                          <Typography variant="body2" sx={{ color: MIREA_COLORS.text }}>
                            УРОВЕНЬ РИСКА
                          </Typography>
                          <Typography variant="h4" sx={{ color: getRiskColor(whatIfResults.riskLevel), fontWeight: 700 }}>
                            {whatIfResults.riskLevel}
                          </Typography>
                        </CardContent>
                      </Card>
                      
                      <Card sx={{ bgcolor: 'rgba(41, 182, 246, 0.1)' }}>
                        <CardContent>
                          <Typography variant="body2" sx={{ color: MIREA_COLORS.text }}>
                            ПРОГНОЗ КАЧЕСТВА
                          </Typography>
                          <Typography variant="h4" sx={{ color: MIREA_COLORS.info, fontWeight: 700 }}>
                            {whatIfResults.quality.toFixed(1)}%
                          </Typography>
                        </CardContent>
                      </Card>
                      
                      <Box>
                        <Typography variant="body2" sx={{ color: MIREA_COLORS.text, mb: 2 }}>
                          ПРОГНОЗ ДЕФЕКТОВ:
                        </Typography>
                        <Stack spacing={1}>
                          {Object.entries(whatIfResults.defects).map(([type, severity]) => (
                            <Box key={type} sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                              <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
                                {type.toUpperCase()}
                              </Typography>
                              <Typography variant="caption" sx={{ color: MIREA_COLORS.warning }}>
                                {(severity * 100).toFixed(1)}%
                              </Typography>
                            </Box>
                          ))}
                        </Stack>
                      </Box>
                    </Stack>
                  )}
                  
                  {activeTab === 2 && (
                    <Stack spacing={2}>
                      <Typography variant="body2" sx={{ color: MIREA_COLORS.text }}>
                        {whatIfResults.recommendations.length > 0 ? (
                          <List dense>
                            {whatIfResults.recommendations.map((rec, i) => (
                              <ListItem key={i}>
                                <ListItemText
                                  primary={
                                    <Typography variant="body2" sx={{ color: MIREA_COLORS.text }}>
                                      • {rec}
                                    </Typography>
                                  }
                                />
                              </ListItem>
                            ))}
                          </List>
                        ) : (
                          <Box sx={{ textAlign: 'center', py: 4 }}>
                            <CheckCircle sx={{ fontSize: 48, color: MIREA_COLORS.success, mb: 2 }} />
                            <Typography variant="body2" sx={{ color: MIREA_COLORS.text }}>
                              Все параметры в оптимальном диапазоне
                            </Typography>
                          </Box>
                        )}
                      </Typography>
                    </Stack>
                  )}
                </>
              ) : (
                <>
                  <Typography variant="h5" sx={{ 
                    color: MIREA_COLORS.primary, 
                    mb: 3, 
                    fontWeight: 700,
                    display: 'flex',
                    alignItems: 'center'
                  }}>
                    <DataArray sx={{ mr: 1 }} />
                    СТАТИСТИКА В РЕАЛЬНОМ ВРЕМЕНИ
                  </Typography>
                  
                  <Divider sx={{ mb: 3, borderColor: 'rgba(255, 255, 255, 0.1)' }} />
                  
                  <Accordion defaultExpanded sx={{ bgcolor: 'transparent', color: MIREA_COLORS.text }}>
                    <AccordionSummary expandIcon={<ExpandMore sx={{ color: MIREA_COLORS.text }} />}>
                      <Typography variant="body2" sx={{ fontWeight: 700 }}>
                        Физические параметры
                      </Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                      <Stack spacing={2}>
                        <Box>
                          <Typography variant="caption" sx={{ color: MIREA_COLORS.text + 'CC' }}>
                            Температура печи
                          </Typography>
                          <Typography variant="h6" sx={{ color: MIREA_COLORS.secondary }}>
                            {realState.furnaceTemp.toFixed(0)}°C
                          </Typography>
                        </Box>
                        <Box>
                          <Typography variant="caption" sx={{ color: MIREA_COLORS.text + 'CC' }}>
                            Вязкость стекла
                          </Typography>
                          <Typography variant="h6" sx={{ color: MIREA_COLORS.info }}>
                            {(realState.viscosity / 1000).toFixed(1)} кПа·с
                          </Typography>
                        </Box>
                        <Box>
                          <Typography variant="caption" sx={{ color: MIREA_COLORS.text + 'CC' }}>
                            Температура формы
                          </Typography>
                          <Typography variant="h6" sx={{ color: MIREA_COLORS.warning }}>
                            {realState.moldTemp.toFixed(0)}°C
                          </Typography>
                        </Box>
                      </Stack>
                    </AccordionDetails>
                  </Accordion>
                  
                  <Accordion sx={{ bgcolor: 'transparent', color: MIREA_COLORS.text, mt: 2 }}>
                    <AccordionSummary expandIcon={<ExpandMore sx={{ color: MIREA_COLORS.text }} />}>
                      <Typography variant="body2" sx={{ fontWeight: 700 }}>
                        Производственные метрики
                      </Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                      <Stack spacing={2}>
                        <Box>
                          <Typography variant="caption" sx={{ color: MIREA_COLORS.text + 'CC' }}>
                            Скорость производства
                          </Typography>
                          <Typography variant="h6" sx={{ color: MIREA_COLORS.success }}>
                            {realState.beltSpeed} м/мин
                          </Typography>
                        </Box>
                        <Box>
                          <Typography variant="caption" sx={{ color: MIREA_COLORS.text + 'CC' }}>
                            Общее качество
                          </Typography>
                          <Typography variant="h6" sx={{ color: MIREA_COLORS.info }}>
                            {realState.quality.toFixed(1)}%
                          </Typography>
                        </Box>
                        <Box>
                          <Typography variant="caption" sx={{ color: MIREA_COLORS.text + 'CC' }}>
                            Всего дефектов
                          </Typography>
                          <Typography variant="h6" sx={{ color: MIREA_COLORS.error }}>
                            {Object.values(realState.defects).reduce((a, b) => a + b, 0).toFixed(3)}
                          </Typography>
                        </Box>
                      </Stack>
                    </AccordionDetails>
                  </Accordion>
                  
                  <Accordion sx={{ bgcolor: 'transparent', color: MIREA_COLORS.text, mt: 2 }}>
                    <AccordionSummary expandIcon={<ExpandMore sx={{ color: MIREA_COLORS.text }} />}>
                      <Typography variant="body2" sx={{ fontWeight: 700 }}>
                        Статус системы
                      </Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                      <Stack spacing={1.5}>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                          <Typography variant="caption" sx={{ color: MIREA_COLORS.text + 'CC' }}>
                            WebSocket соединение
                          </Typography>
                          <Chip 
                            label={isConnected ? 'Активно' : 'Неактивно'} 
                            size="small" 
                            color={isConnected ? 'success' : 'error'}
                          />
                        </Box>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                          <Typography variant="caption" sx={{ color: MIREA_COLORS.text + 'CC' }}>
                            Shadow Mode
                          </Typography>
                          <Chip 
                            label={shadowMode ? 'Включен' : 'Выключен'} 
                            size="small" 
                            color={shadowMode ? 'primary' : 'default'}
                          />
                        </Box>
                        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                          <Typography variant="caption" sx={{ color: MIREA_COLORS.text + 'CC' }}>
                            Физическая симуляция
                          </Typography>
                          <Chip 
                            label={showPhysics ? 'Включена' : 'Выключена'} 
                            size="small" 
                            color={showPhysics ? 'primary' : 'default'}
                          />
                        </Box>
                      </Stack>
                    </AccordionDetails>
                  </Accordion>
                </>
              )}
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default DigitalTwin3D;
