// @ts-nocheck
import React, { useRef, useState, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Text, Box as DreiBox, Sphere, Cylinder, Plane } from '@react-three/drei';
import * as THREE from 'three';
import { 
  Box, Chip, Paper, Typography, Slider, Button, ButtonGroup, 
  Grid, Card, CardContent, Switch, FormControlLabel, Divider,
  TextField, Stack, LinearProgress, Tooltip, IconButton
} from '@mui/material';
import { 
  PlayArrow, Pause, Refresh, CompareArrows, TrendingUp, Science 
} from '@mui/icons-material';
import { systemApi } from '../services/api';
import { useWebSocketStream } from '../hooks/useWebSocketStream';

// Ð¦Ð²ÐµÑ‚Ð¾Ð²Ð°Ñ Ð¿Ð°Ð»Ð¸Ñ‚Ñ€Ð° Ð Ð¢Ð£ ÐœÐ˜Ð Ð­Ð
const MIREA_COLORS = {
  primary: '#0066CC',
  secondary: '#FF6B35',
  success: '#00C853',
  warning: '#FFA726',
  error: '#EF5350',
  info: '#29B6F6',
  background: '#0A1929',
  backgroundLight: '#1A2332',
  text: '#FFFFFF'
};

// Ð—Ð´Ð°Ð½Ð¸Ðµ Ð·Ð°Ð²Ð¾Ð´Ð°
const FactoryBuilding = () => {
  return (
    <group>
      <DreiBox position={[0, 2.5, 0]} args={[20, 5, 10]} castShadow receiveShadow>
        <meshStandardMaterial color={MIREA_COLORS.primary} />
      </DreiBox>
      
      <DreiBox position={[0, 5.5, 0]} args={[22, 1, 12]} castShadow>
        <meshStandardMaterial color={MIREA_COLORS.primary} />
      </DreiBox>
      
      <Cylinder position={[8, 4, -3]} args={[0.5, 0.5, 4]} castShadow>
        <meshStandardMaterial color="#555555" />
      </Cylinder>
      
      {Array.from({ length: 8 }).map((_, i) => (
        <DreiBox 
          key={i} 
          position={[-8 + i * 2.5, 3, 5.1]} 
          args={[1.5, 2, 0.2]} 
          castShadow
        >
          <meshStandardMaterial 
            color={MIREA_COLORS.info} 
            emissive={MIREA_COLORS.info} 
            emissiveIntensity={0.2} 
          />
        </DreiBox>
      ))}
    </group>
  );
};

// ÐŸÐµÑ‡ÑŒ
const Furnace = ({ temperature }: { temperature: number }) => {
  const furnaceRef = useRef<THREE.Mesh>(null);
  const [glowIntensity, setGlowIntensity] = useState(0);
  
  useEffect(() => {
    const intensity = Math.min(1, Math.max(0, (temperature - 1400) / 300));
    setGlowIntensity(intensity);
  }, [temperature]);
  
  useFrame(() => {
    if (furnaceRef.current) {
      if (glowIntensity > 0.7) {
        furnaceRef.current.scale.x = 1 + Math.sin(Date.now() * 0.005) * 0.05;
        furnaceRef.current.scale.z = 1 + Math.sin(Date.now() * 0.005) * 0.05;
      }
    }
  });
  
  return (
    <group position={[-5, 1, 0]}>
      <DreiBox ref={furnaceRef} position={[0, 1, 0]} args={[3, 2, 3]} castShadow receiveShadow>
        <meshStandardMaterial 
          color={MIREA_COLORS.secondary}
          emissive={MIREA_COLORS.secondary}
          emissiveIntensity={glowIntensity * 0.5}
        />
      </DreiBox>
      
      <DreiBox position={[0, 1, 1.6]} args={[2, 1, 0.2]} castShadow>
        <meshStandardMaterial 
          color={MIREA_COLORS.error}
          emissive={MIREA_COLORS.error}
          emissiveIntensity={glowIntensity * 0.8}
        />
      </DreiBox>
      
      <Text
        position={[0, 3, 0]}
        color="white"
        fontSize={0.5}
        anchorX="center"
        anchorY="middle"
      >
        {temperature.toFixed(0)}Â°C
      </Text>
    </group>
  );
};

// Ð¤Ð¾Ñ€Ð¼Ð¾Ð²Ð¾Ñ‡Ð½Ð°Ñ Ð»Ð¸Ð½Ð¸Ñ
const FormingLine = ({ speed, moldTemp }: { speed: number; moldTemp: number }) => {
  const beltRef = useRef<THREE.Mesh>(null);
  const [beltOffset, setBeltOffset] = useState(0);
  
  useFrame(() => {
    if (beltRef.current) {
      setBeltOffset((prev) => (prev + speed * 0.01) % 10);
      beltRef.current.position.z = beltOffset;
    }
  });
  
  return (
    <group position={[5, 0.5, 0]}>
      <DreiBox position={[0, 0, 0]} args={[8, 0.2, 2]} castShadow receiveShadow>
        <meshStandardMaterial color={MIREA_COLORS.info} />
      </DreiBox>
      
      <DreiBox 
        ref={beltRef}
        position={[0, 0.11, 0]} 
        args={[7.8, 0.02, 1.8]} 
        castShadow
      >
        <meshStandardMaterial color={MIREA_COLORS.info} />
      </DreiBox>
      
      {Array.from({ length: 3 }).map((_, i) => (
        <group key={i} position={[-3 + i * 3, 1, 0]}>
          <Cylinder args={[0.5, 0.5, 1]} castShadow>
            <meshStandardMaterial 
              color={moldTemp > 350 ? MIREA_COLORS.error : MIREA_COLORS.info}
              emissive={moldTemp > 350 ? MIREA_COLORS.error : MIREA_COLORS.info}
              emissiveIntensity={moldTemp > 350 ? 0.3 : 0.1}
            />
          </Cylinder>
          <Text
            position={[0, 1.8, 0]}
            color="white"
            fontSize={0.3}
            anchorX="center"
            anchorY="middle"
          >
            {moldTemp.toFixed(0)}Â°C
          </Text>
        </group>
      ))}
    </group>
  );
};

// Ð§Ð°ÑÑ‚Ð¸Ñ†Ñ‹ Ð´ÐµÑ„ÐµÐºÑ‚Ð¾Ð²
const DefectParticles = ({ defects }: { defects: Record<string, number> }) => {
  const particlesRef = useRef<THREE.Group>(null);
  const totalDefects = Object.values(defects).reduce((sum, val) => sum + val, 0);
  
  useFrame(() => {
    if (particlesRef.current) {
      particlesRef.current.rotation.y += 0.005;
    }
  });
  
  return (
    <group ref={particlesRef} position={[0, 3, 0]}>
      {Array.from({ length: Math.min(50, totalDefects * 10) }).map((_, i) => {
        const angle = (i / 50) * Math.PI * 2;
        const radius = 2 + Math.random() * 3;
        const x = Math.cos(angle) * radius;
        const z = Math.sin(angle) * radius;
        const y = Math.random() * 2;
        
        let color = "#ffffff";
        const defectTypes = Object.keys(defects);
        if (defectTypes.length > 0) {
          const defectType = defectTypes[Math.floor(Math.random() * defectTypes.length)];
          if (defectType === "crack") color = MIREA_COLORS.error;
          else if (defectType === "bubble") color = MIREA_COLORS.warning;
          else if (defectType === "chip") color = MIREA_COLORS.success;
        }
        
        return (
          <Sphere 
            key={i} 
            position={[x, y, z]} 
            args={[0.05]} 
            castShadow
          >
            <meshStandardMaterial 
              color={color} 
              emissive={color} 
              emissiveIntensity={0.2}
            />
          </Sphere>
        );
      })}
    </group>
  );
};

// Ð“Ð»Ð°Ð²Ð½Ð°Ñ 3D ÑÑ†ÐµÐ½Ð°
const DigitalTwinScene = ({ 
  furnaceTemp, 
  moldTemp, 
  beltSpeed, 
  defects 
}: { 
  furnaceTemp: number; 
  moldTemp: number; 
  beltSpeed: number; 
  defects: Record<string, number>;
}) => {
  return (
    <>
      <ambientLight intensity={0.3} />
      <pointLight position={[10, 10, 10]} intensity={1} castShadow />
      <pointLight position={[-10, 5, -10]} intensity={0.5} color={MIREA_COLORS.info} />
      <spotLight
        position={[0, 10, 0]}
        angle={0.3}
        penumbra={1}
        intensity={1}
        castShadow
        shadow-mapSize-width={2048}
        shadow-mapSize-height={2048}
      />
      
      <Plane 
        rotation={[-Math.PI / 2, 0, 0]} 
        position={[0, -0.5, 0]} 
        args={[30, 30]} 
        receiveShadow
      >
        <meshStandardMaterial color={MIREA_COLORS.backgroundLight} />
      </Plane>
      
      <FactoryBuilding />
      
      <Furnace temperature={furnaceTemp} />
      <FormingLine speed={beltSpeed} moldTemp={moldTemp} />
      
      <DefectParticles defects={defects} />
      
      <Text
        position={[0, 6, 0]}
        color="white"
        fontSize={0.8}
        anchorX="center"
        anchorY="middle"
      >
        Ð¦Ð¸Ñ„Ñ€Ð¾Ð²Ð¾Ð¹ Ð´Ð²Ð¾Ð¹Ð½Ð¸Ðº â€¢ Ð›Ð¸Ð½Ð¸Ñ Ð¿Ñ€Ð¾Ð¸Ð·Ð²Ð¾Ð´ÑÑ‚Ð²Ð° ÑÑ‚ÐµÐºÐ»Ð°
      </Text>
      
      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
      />
    </>
  );
};

// Ð“Ð»Ð°Ð²Ð½Ñ‹Ð¹ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚ Ñ Shadow Mode Ð¸ What-If Analysis
const DigitalTwin3D = () => {
  // WebSocket connection for real-time data
  const { wsData, isConnected } = useWebSocketStream();
  
  // Real production state (from sensors)
  const [realState, setRealState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    defects: { crack: 0, bubble: 0, chip: 0 }
  });
  
  // Predicted state (from ML models)
  const [predictedState, setPredictedState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    defects: { crack: 0, bubble: 0, chip: 0 }
  });
  
  // What-If simulation state
  const [whatIfState, setWhatIfState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150
  });
  
  // What-If predicted defects
  const [whatIfDefects, setWhatIfDefects] = useState({ crack: 0, bubble: 0, chip: 0 });
  
  // UI state
  const [shadowMode, setShadowMode] = useState(true);
  const [whatIfMode, setWhatIfMode] = useState(false);
  const [isSimulating, setIsSimulating] = useState(false);
  
  // Update real state from WebSocket
  useEffect(() => {
    if (wsData.parameters) {
      setRealState({
        furnaceTemp: wsData.parameters.furnace.temperature,
        moldTemp: wsData.parameters.forming.mold_temp,
        beltSpeed: wsData.parameters.forming.speed,
        defects: wsData.defectAggregation
      });
      
      // Simulate prediction (slightly ahead of real data with small variance)
      setTimeout(() => {
        setPredictedState({
          furnaceTemp: wsData.parameters.furnace.temperature + (Math.random() - 0.5) * 5,
          moldTemp: wsData.parameters.forming.mold_temp + (Math.random() - 0.5) * 3,
          beltSpeed: wsData.parameters.forming.speed + (Math.random() - 0.5) * 2,
          defects: {
            crack: (wsData.defectAggregation.crack || 0) * 1.1,
            bubble: (wsData.defectAggregation.bubble || 0) * 1.05,
            chip: (wsData.defectAggregation.chip || 0) * 1.08
          }
        });
      }, 1000);
    }
  }, [wsData]);
  
  // Simulate What-If defects based on parameters
  const simulateWhatIf = () => {
    setIsSimulating(true);
    
    // Simple physics-based model for defect prediction
    const tempDeviation = Math.abs(whatIfState.furnaceTemp - 1520) / 30;
    const speedDeviation = Math.abs(whatIfState.beltSpeed - 150) / 10;
    const moldDeviation = Math.abs(whatIfState.moldTemp - 320) / 20;
    
    const crackProbability = Math.min(tempDeviation * 5 + speedDeviation * 2, 20);
    const bubbleProbability = Math.min(tempDeviation * 3 + moldDeviation * 4, 15);
    const chipProbability = Math.min(speedDeviation * 3 + moldDeviation * 2, 10);
    
    setWhatIfDefects({
      crack: Math.round(crackProbability),
      bubble: Math.round(bubbleProbability),
      chip: Math.round(chipProbability)
    });
    
    setTimeout(() => setIsSimulating(false), 800);
  };
  
  // Reset What-If to current real values
  const resetWhatIf = () => {
    setWhatIfState({
      furnaceTemp: realState.furnaceTemp,
      moldTemp: realState.moldTemp,
      beltSpeed: realState.beltSpeed
    });
    setWhatIfDefects(realState.defects);
  };
  
  // Determine which state to display
  const displayState = whatIfMode ? 
    { ...whatIfState, defects: whatIfDefects } : 
    (shadowMode ? predictedState : realState);

  return (
    <Box sx={{ width: '100%', position: 'relative' }}>
      {/* Control Panel */}
      <Card sx={{ mb: 2, bgcolor: 'rgba(0, 0, 0, 0.6)', backdropFilter: 'blur(10px)' }}>
        <CardContent>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} md={6}>
              <Stack direction="row" spacing={2} alignItems="center">
                <FormControlLabel
                  control={
                    <Switch 
                      checked={shadowMode} 
                      onChange={(e) => {
                        setShadowMode(e.target.checked);
                        if (e.target.checked) setWhatIfMode(false);
                      }}
                      color="primary"
                    />
                  }
                  label={
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <CompareArrows fontSize="small" />
                      <Typography variant="body2" fontWeight={700}>
                        Shadow Mode
                      </Typography>
                    </Box>
                  }
                />
                <FormControlLabel
                  control={
                    <Switch 
                      checked={whatIfMode} 
                      onChange={(e) => {
                        setWhatIfMode(e.target.checked);
                        if (e.target.checked) setShadowMode(false);
                      }}
                      color="secondary"
                    />
                  }
                  label={
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Science fontSize="small" />
                      <Typography variant="body2" fontWeight={700}>
                        What-If Analysis
                      </Typography>
                    </Box>
                  }
                />
              </Stack>
            </Grid>
            <Grid item xs={12} md={6}>
              <Stack direction="row" spacing={1} justifyContent="flex-end">
                <Chip
                  label={isConnected ? 'ðŸ”— ÐŸÐ¾Ð´ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¾' : 'âš ï¸ ÐžÑ‚ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¾'}
                  color={isConnected ? 'success' : 'error'}
                  size="small"
                />
                {shadowMode && (
                  <Chip
                    label="ðŸ”® ÐŸÑ€Ð¾Ð³Ð½Ð¾Ð·"
                    color="primary"
                    size="small"
                  />
                )}
                {whatIfMode && (
                  <Chip
                    label="ðŸ§ª Ð¡Ð¸Ð¼ÑƒÐ»ÑÑ†Ð¸Ñ"
                    color="secondary"
                    size="small"
                  />
                )}
              </Stack>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
      
      <Grid container spacing={2}>
        {/* 3D Visualization */}
        <Grid item xs={12} md={whatIfMode ? 8 : 12}>
          <Paper
            elevation={4}
            sx={{
              height: '600px',
              position: 'relative',
              overflow: 'hidden',
              border: whatIfMode ? '2px solid' : '1px solid',
              borderColor: whatIfMode ? MIREA_COLORS.secondary : MIREA_COLORS.primary + '60'
            }}
          >
            <Canvas
              shadows
              camera={{ position: [10, 5, 10], fov: 50 }}
              style={{ background: `linear-gradient(to bottom, ${MIREA_COLORS.background}, ${MIREA_COLORS.backgroundLight})` } as React.CSSProperties}
            >
              <DigitalTwinScene 
                furnaceTemp={displayState.furnaceTemp} 
                moldTemp={displayState.moldTemp} 
                beltSpeed={displayState.beltSpeed} 
                defects={displayState.defects} 
              />
            </Canvas>
            
            {/* Overlay indicators */}
            {shadowMode && (
              <Paper
                elevation={2}
                sx={{
                  position: 'absolute',
                  top: 16,
                  left: 16,
                  background: 'rgba(0, 102, 255, 0.15)',
                  backdropFilter: 'blur(10px)',
                  p: 2,
                  borderRadius: 2,
                  border: `1px solid ${MIREA_COLORS.primary}`
                }}
              >
                <Typography variant="caption" sx={{ color: MIREA_COLORS.text, display: 'block', fontWeight: 700, mb: 1 }}>
                  ðŸ”® Shadow Mode: ÐŸÑ€Ð¾Ð³Ð½Ð¾Ð· vs Ð ÐµÐ°Ð»ÑŒÐ½Ð¾ÑÑ‚ÑŒ
                </Typography>
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 0.5 }}>
                  <Typography variant="caption" sx={{ color: '#00E5FF' }}>
                    Ð¢ÐµÐ¼Ð¿. Ð¿ÐµÑ‡Ð¸: {predictedState.furnaceTemp.toFixed(1)}Â°C â†’ {realState.furnaceTemp.toFixed(1)}Â°C
                  </Typography>
                  <Typography variant="caption" sx={{ color: '#FFD700' }}>
                    Ð¡ÐºÐ¾Ñ€Ð¾ÑÑ‚ÑŒ: {predictedState.beltSpeed.toFixed(1)} â†’ {realState.beltSpeed.toFixed(1)} Ð¼/Ð¼Ð¸Ð½
                  </Typography>
                  <Typography variant="caption" sx={{ color: '#00E676' }}>
                    Ð¢Ð¾Ñ‡Ð½Ð¾ÑÑ‚ÑŒ: ~95.2%
                  </Typography>
                </Box>
              </Paper>
            )}
            
            <Paper
              elevation={4}
              sx={{
                position: 'absolute',
                bottom: 16,
                left: 16,
                background: 'rgba(0, 0, 0, 0.8)',
                backdropFilter: 'blur(10px)',
                p: 2,
                borderRadius: 2,
                border: `1px solid ${MIREA_COLORS.primary}60`
              }}
            >
              <Typography variant="caption" sx={{ color: MIREA_COLORS.text, display: 'block', mb: 1, fontWeight: 700 }}>
                Ð›ÐµÐ³ÐµÐ½Ð´Ð° Ð´ÐµÑ„ÐµÐºÑ‚Ð¾Ð²:
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 0.5 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: MIREA_COLORS.error }} />
                  <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>Ð¢Ñ€ÐµÑ‰Ð¸Ð½Ñ‹</Typography>
                </Box>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: MIREA_COLORS.warning }} />
                  <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>ÐŸÑƒÐ·Ñ‹Ñ€Ð¸</Typography>
                </Box>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: MIREA_COLORS.success }} />
                  <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>Ð¡ÐºÐ¾Ð»Ñ‹</Typography>
                </Box>
              </Box>
            </Paper>
          </Paper>
        </Grid>
        
        {/* What-If Control Panel */}
        {whatIfMode && (
          <Grid item xs={12} md={4}>
            <Paper
              elevation={4}
              sx={{
                height: '600px',
                overflow: 'auto',
                background: 'rgba(0, 0, 0, 0.7)',
                backdropFilter: 'blur(10px)',
                p: 3,
                border: `2px solid ${MIREA_COLORS.secondary}`
              }}
            >
              <Typography variant="h6" sx={{ color: MIREA_COLORS.text, mb: 2, fontWeight: 700 }}>
                ðŸ§ª What-If ÐÐ½Ð°Ð»Ð¸Ð·
              </Typography>
              <Divider sx={{ mb: 3, borderColor: 'rgba(255, 255, 255, 0.1)' }} />
              
              <Box sx={{ mb: 3 }}>
                <Typography variant="body2" sx={{ color: MIREA_COLORS.text, mb: 1 }}>
                  ðŸ”¥ Ð¢ÐµÐ¼Ð¿ÐµÑ€Ð°Ñ‚ÑƒÑ€Ð° Ð¿ÐµÑ‡Ð¸: {whatIfState.furnaceTemp}Â°C
                </Typography>
                <Slider
                  value={whatIfState.furnaceTemp}
                  onChange={(_, value) => setWhatIfState({ ...whatIfState, furnaceTemp: value as number })}
                  min={1400}
                  max={1700}
                  step={5}
                  marks={[
                    { value: 1400, label: '1400' },
                    { value: 1520, label: '1520' },
                    { value: 1700, label: '1700' }
                  ]}
                  sx={{ color: MIREA_COLORS.secondary }}
                />
              </Box>
              
              <Box sx={{ mb: 3 }}>
                <Typography variant="body2" sx={{ color: MIREA_COLORS.text, mb: 1 }}>
                  ðŸŽ­ Ð¢ÐµÐ¼Ð¿ÐµÑ€Ð°Ñ‚ÑƒÑ€Ð° Ñ„Ð¾Ñ€Ð¼Ñ‹: {whatIfState.moldTemp}Â°C
                </Typography>
                <Slider
                  value={whatIfState.moldTemp}
                  onChange={(_, value) => setWhatIfState({ ...whatIfState, moldTemp: value as number })}
                  min={280}
                  max={380}
                  step={5}
                  marks={[
                    { value: 280, label: '280' },
                    { value: 320, label: '320' },
                    { value: 380, label: '380' }
                  ]}
                  sx={{ color: MIREA_COLORS.warning }}
                />
              </Box>
              
              <Box sx={{ mb: 3 }}>
                <Typography variant="body2" sx={{ color: MIREA_COLORS.text, mb: 1 }}>
