import React, { useRef, useState, useEffect, useMemo } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Text } from '@react-three/drei';
import * as THREE from 'three';
import { 
  Box, Chip, Paper, Typography, Slider, Button, 
  Grid, Card, CardContent, Switch, FormControlLabel, Divider,
  Stack, LinearProgress, Tooltip, IconButton, Alert, Collapse, Tab, Tabs, Badge
} from '@mui/material';
import { 
  PlayArrow, Pause, Refresh, TrendingUp, Science,
  LocalFireDepartment, Thermostat, Speed, Warning, CheckCircle,
  Visibility, VisibilityOff, ErrorOutline, BubbleChart,
  Grain, Cloud, CompareArrows, Info
} from '@mui/icons-material';

// Интерфейсы типов
interface MIREAColors {
  primary: string;
  primaryDark: string;
  primaryLight: string;
  secondary: string;
  secondaryDark: string;
  accent: string;
  success: string;
  warning: string;
  error: string;
  background: string;
  backgroundLight: string;
  surface: string;
  surfaceLight: string;
  text: string;
  textSecondary: string;
  glass: string;
}

interface Defect {
  type: string;
  probability: number;
  color: string;
  icon: React.ReactNode;
  description: string;
}

interface FurnaceDefectVisualization {
  position: [number, number, number];
  type: string;
  size: number;
  rotation: number;
}

interface MoldDefectVisualization {
  position: [number, number, number];
  type: string;
  size: number;
  shape: 'bubble' | 'crack' | 'chip' | 'cloud';
}

// Цветовая палитра РТУ МИРЭА
const MIREA_COLORS: MIREAColors = {
  primary: '#0066CC',
  primaryDark: '#004C99',
  primaryLight: '#3385D6',
  secondary: '#FF6B35',
  secondaryDark: '#CC5529',
  accent: '#00D9FF',
  success: '#00C853',
  warning: '#FFA726',
  error: '#EF5350',
  background: '#0A0E1A',
  backgroundLight: '#141824',
  surface: '#1E2330',
  surfaceLight: '#2A3142',
  text: '#FFFFFF',
  textSecondary: '#B0B8C8',
  glass: 'rgba(255, 255, 255, 0.05)'
};

// Конфигурация дефектов
const DEFECT_CONFIG = {
  crack: {
    color: MIREA_COLORS.error,
    icon: <ErrorOutline />,
    description: 'Трещины от термического напряжения',
    severity: 'high'
  },
  bubble: {
    color: MIREA_COLORS.warning,
    icon: <BubbleChart />,
    description: 'Воздушные пузыри в стекле',
    severity: 'medium'
  },
  chip: {
    color: MIREA_COLORS.secondary,
    icon: <Grain />,
    description: 'Сколы и микротрещины',
    severity: 'medium'
  },
  cloudiness: {
    color: '#AAAAAA',
    icon: <Cloud />,
    description: 'Помутнение поверхности',
    severity: 'low'
  },
  deformation: {
    color: '#9C27B0',
    icon: <CompareArrows />,
    description: 'Деформация формы',
    severity: 'high'
  },
  stress: {
    color: '#E91E63',
    icon: <ErrorOutline />,
    description: 'Внутренние напряжения',
    severity: 'high'
  }
};

// Компонент: Визуализация трещины на поверхности
const CrackVisualization = ({ position, size, rotation }: { 
  position: [number, number, number]; 
  size: number; 
  rotation: number 
}) => {
  const groupRef = useRef<THREE.Group>(null);
  const [progress, setProgress] = useState(0);
  
  useFrame((state) => {
    if (groupRef.current) {
      // Медленное мерцание трещины
      const intensity = 0.6 + 0.4 * Math.sin(state.clock.elapsedTime * 2);
      groupRef.current.children.forEach((child) => {
        if (child instanceof THREE.Mesh) {
          const material = child.material as THREE.MeshStandardMaterial;
          material.emissiveIntensity = intensity * 0.5;
        }
      });
      
      // Постепенное проявление трещины
      if (progress < 1) {
        setProgress(Math.min(1, progress + 0.01));
      }
    }
  });
  
  const crackGeometry = useMemo(() => {
    const points = [];
    const segments = 8;
    
    for (let i = 0; i <= segments; i++) {
      const t = i / segments;
      const x = (Math.random() - 0.5) * 0.5 * size;
      const y = (Math.random() - 0.5) * 0.5 * size;
      const z = 0;
      points.push(new THREE.Vector3(x, y, z));
    }
    
    const curve = new THREE.CatmullRomCurve3(points);
    const geometry = new THREE.TubeGeometry(curve, 20, 0.02, 8, false);
    
    return geometry;
  }, [size]);
  
  return (
    <group ref={groupRef} position={position} rotation={[0, rotation, 0]}>
      <mesh geometry={crackGeometry}>
        <meshStandardMaterial 
          color={MIREA_COLORS.error}
          emissive={MIREA_COLORS.error}
          emissiveIntensity={0.5}
          transparent
          opacity={0.8 * progress}
          roughness={0.8}
          metalness={0.2}
        />
      </mesh>
    </group>
  );
};

// Компонент: Визуализация пузыря
const BubbleVisualization = ({ position, size }: { 
  position: [number, number, number]; 
  size: number 
}) => {
  const meshRef = useRef<THREE.Mesh>(null);
  
  useFrame((state) => {
    if (meshRef.current) {
      // Плавное движение пузыря
      const time = state.clock.elapsedTime;
      meshRef.current.position.y = position[1] + Math.sin(time * 1.5) * 0.05;
      meshRef.current.scale.setScalar(1 + Math.sin(time * 2) * 0.1);
    }
  });
  
  return (
    <mesh ref={meshRef} position={position}>
      <sphereGeometry args={[size, 16, 16]} />
      <meshPhysicalMaterial
        color={MIREA_COLORS.warning}
        transparent
        opacity={0.7}
        transmission={0.9}
        thickness={0.5}
        roughness={0.1}
        metalness={0.1}
        emissive={MIREA_COLORS.warning}
        emissiveIntensity={0.3}
      />
    </mesh>
  );
};

// Компонент: Визуализация скола
const ChipVisualization = ({ position, size }: { 
  position: [number, number, number]; 
  size: number 
}) => {
  const groupRef = useRef<THREE.Group>(null);
  
  useFrame((state) => {
    if (groupRef.current) {
      // Вращение скола для лучшей видимости
      groupRef.current.rotation.z += 0.002;
    }
  });
  
  return (
    <group ref={groupRef} position={position}>
      <mesh>
        <coneGeometry args={[size * 0.8, size, 5]} />
        <meshStandardMaterial
          color={MIREA_COLORS.secondary}
          emissive={MIREA_COLORS.secondary}
          emissiveIntensity={0.4}
          roughness={0.7}
          metalness={0.3}
        />
      </mesh>
      {/* Осколки вокруг */}
      {[...Array(3)].map((_, i) => (
        <mesh key={i} position={[
          Math.cos(i * Math.PI * 2/3) * size * 1.2,
          0,
          Math.sin(i * Math.PI * 2/3) * size * 1.2
        ]}>
          <boxGeometry args={[size * 0.3, size * 0.1, size * 0.3]} />
          <meshStandardMaterial
            color={MIREA_COLORS.secondaryDark}
            emissive={MIREA_COLORS.secondary}
            emissiveIntensity={0.2}
          />
        </mesh>
      ))}
    </group>
  );
};

// Компонент: Дефекты на печи с реалистичной визуализацией
const EnhancedFurnaceDefects = ({ defects, position }: { 
  defects: { [key: string]: number }; 
  position: [number, number, number] 
}) => {
  const groupRef = useRef<THREE.Group>(null);
  
  const defectVisualizations = useMemo(() => {
    const visualizations: FurnaceDefectVisualization[] = [];
    const defectTypes = Object.entries(defects);
    
    defectTypes.forEach(([type, prob]) => {
      const count = Math.floor(prob * 20); // Количество визуализаций зависит от вероятности
      
      for (let i = 0; i < count; i++) {
        if (type === 'crack') {
          // Трещины на поверхности печи
          const face = Math.floor(Math.random() * 6);
          let pos: [number, number, number] = [0, 0, 0];
          
          switch(face) {
            case 0: // front
              pos = [
                (Math.random() - 0.5) * 3.5,
                (Math.random() - 0.5) * 2.5,
                1.5
              ];
              break;
            case 1: // back
              pos = [
                (Math.random() - 0.5) * 3.5,
                (Math.random() - 0.5) * 2.5,
                -1.5
              ];
              break;
            case 2: // right
              pos = [
                2,
                (Math.random() - 0.5) * 2.5,
                (Math.random() - 0.5) * 2.5
              ];
              break;
            case 3: // left
              pos = [
                -2,
                (Math.random() - 0.5) * 2.5,
                (Math.random() - 0.5) * 2.5
              ];
              break;
            case 4: // top
              pos = [
                (Math.random() - 0.5) * 3.5,
                1.5,
                (Math.random() - 0.5) * 2.5
              ];
              break;
            case 5: // bottom
              pos = [
                (Math.random() - 0.5) * 3.5,
                -1.5,
                (Math.random() - 0.5) * 2.5
              ];
              break;
          }
          
          visualizations.push({
            position: [
              position[0] + pos[0],
              position[1] + pos[1],
              position[2] + pos[2]
            ],
            type: 'crack',
            size: 0.1 + Math.random() * 0.2,
            rotation: Math.random() * Math.PI * 2
          });
        } else if (type === 'stress') {
          // Горячие точки от напряжения
          visualizations.push({
            position: [
              position[0] + (Math.random() - 0.5) * 3,
              position[1] + (Math.random() - 0.5) * 2,
              position[2] + (Math.random() - 0.5) * 2
            ],
            type: 'stress',
            size: 0.05 + prob * 0.1,
            rotation: 0
          });
        }
      }
    });
    
    return visualizations;
  }, [defects, position]);
  
  useFrame((state) => {
    if (groupRef.current) {
      // Легкое дыхание всей группы дефектов
      const pulse = Math.sin(state.clock.elapsedTime * 0.5) * 0.02;
      groupRef.current.scale.setScalar(1 + pulse);
    }
  });
  
  return (
    <group ref={groupRef}>
      {defectVisualizations.map((defect, i) => {
        if (defect.type === 'crack') {
          return (
            <CrackVisualization
              key={i}
              position={defect.position}
              size={defect.size}
              rotation={defect.rotation}
            />
          );
        } else if (defect.type === 'stress') {
          return (
            <mesh key={i} position={defect.position}>
              <sphereGeometry args={[defect.size, 8, 8]} />
              <meshStandardMaterial
                color={DEFECT_CONFIG.stress.color}
                emissive={DEFECT_CONFIG.stress.color}
                emissiveIntensity={0.8}
                transparent
                opacity={0.6}
              />
            </mesh>
          );
        }
        return null;
      })}
    </group>
  );
};

// Компонент: Дефекты на формах с информативной визуализацией
const EnhancedMoldDefects = ({ defects, positions }: { 
  defects: { [key: string]: number }; 
  positions: Array<[number, number, number]>
}) => {
  const groupRef = useRef<THREE.Group>(null);
  const [activeDefect, setActiveDefect] = useState<number | null>(null);
  
  const moldDefects = useMemo(() => {
    const allDefects: MoldDefectVisualization[] = [];
    const defectTypes = Object.entries(defects);
    
    positions.forEach((moldPos, moldIndex) => {
      defectTypes.forEach(([type, prob]) => {
        const count = Math.floor(prob * 10); // Дефектов на форму
        
        for (let i = 0; i < count; i++) {
          if (type === 'bubble') {
            // Пузыри на поверхности формы
            const angle = Math.random() * Math.PI * 2;
            const height = (Math.random() - 0.5) * 0.6;
            const radius = 0.5 + Math.random() * 0.2;
            
            allDefects.push({
              position: [
                moldPos[0] + Math.cos(angle) * radius,
                moldPos[1] + height,
                moldPos[2] + Math.sin(angle) * radius
              ],
              type: 'bubble',
              size: 0.03 + Math.random() * 0.05,
              shape: 'bubble'
            });
          } else if (type === 'chip') {
            // Сколы на краях формы
            const angle = Math.random() * Math.PI * 2;
            const height = (Math.random() > 0.5 ? 0.4 : -0.4);
            
            allDefects.push({
              position: [
                moldPos[0] + Math.cos(angle) * 0.65,
                moldPos[1] + height,
                moldPos[2] + Math.sin(angle) * 0.65
              ],
              type: 'chip',
              size: 0.04 + Math.random() * 0.06,
              shape: 'chip'
            });
          } else if (type === 'cloudiness') {
            // Помутнения на поверхности
            const angle = Math.random() * Math.PI * 2;
            const height = (Math.random() - 0.5) * 0.4;
            
            allDefects.push({
              position: [
                moldPos[0] + Math.cos(angle) * 0.55,
                moldPos[1] + height + 0.5,
                moldPos[2] + Math.sin(angle) * 0.55
              ],
              type: 'cloudiness',
              size: 0.05 + Math.random() * 0.08,
              shape: 'cloud'
            });
          }
        }
      });
    });
    
    return allDefects;
  }, [defects, positions]);
  
  useFrame((state) => {
    if (groupRef.current) {
      // Медленное вращение для лучшего обзора
      groupRef.current.rotation.y += 0.001;
      
      // Пульсация активного дефекта
      if (activeDefect !== null) {
        const defect = groupRef.current.children[activeDefect];
        if (defect) {
          const pulse = 1 + Math.sin(state.clock.elapsedTime * 5) * 0.2;
          defect.scale.setScalar(pulse);
        }
      }
    }
  });
  
  const handleDefectClick = (index: number, defect: MoldDefectVisualization) => {
    setActiveDefect(index);
    console.log(`Дефект: ${defect.type}, Размер: ${defect.size.toFixed(3)}`);
  };
  
  return (
    <group ref={groupRef}>
      {moldDefects.map((defect, i) => {
        let visualComponent;
        
        switch(defect.shape) {
          case 'bubble':
            visualComponent = (
              <BubbleVisualization
                position={defect.position}
                size={defect.size}
              />
            );
            break;
            
          case 'chip':
            visualComponent = (
              <ChipVisualization
                position={defect.position}
                size={defect.size}
              />
            );
            break;
            
          case 'cloud':
            visualComponent = (
              <mesh position={defect.position}>
                <sphereGeometry args={[defect.size, 8, 8]} />
                <meshStandardMaterial
                  color="#AAAAAA"
                  emissive="#AAAAAA"
                  emissiveIntensity={0.3}
                  transparent
                  opacity={0.4}
                  roughness={0.9}
                />
              </mesh>
            );
            break;
        }
        
        return (
          <group 
            key={i} 
            onClick={() => handleDefectClick(i, defect)}
            onPointerOver={() => setActiveDefect(i)}
            onPointerOut={() => setActiveDefect(null)}
          >
            {visualComponent}
            
            {/* Информационная метка при наведении */}
            {activeDefect === i && (
              <Text
                position={[defect.position[0], defect.position[1] + defect.size + 0.1, defect.position[2]]}
                fontSize={0.15}
                color={MIREA_COLORS.text}
                anchorX="center"
                anchorY="bottom"
              >
                {defect.type.toUpperCase()}
              </Text>
            )}
          </group>
        );
      })}
    </group>
  );
};

// Компонент: Панель информации о дефектах
const DefectInfoPanel = ({ defects, position }: { 
  defects: { [key: string]: number }; 
  position: [number, number, number] 
}) => {
  return (
    <group position={position}>
      <mesh position={[0, 1.5, 0]}>
        <boxGeometry args={[3, 2, 0.1]} />
        <meshStandardMaterial 
          color={MIREA_COLORS.surface}
          transparent
          opacity={0.8}
          emissive={MIREA_COLORS.surface}
          emissiveIntensity={0.1}
        />
      </mesh>
      
      <Text
        position={[0, 2.2, 0.06]}
        fontSize={0.2}
        color={MIREA_COLORS.accent}
        anchorX="center"
      >
        Анализ дефектов
      </Text>
      
      {Object.entries(defects).map(([type, prob], index) => {
        const yPos = 1.8 - index * 0.3;
        const color = (DEFECT_CONFIG as any)[type]?.color || MIREA_COLORS.text;
        
        return (
          <group key={type} position={[0, yPos, 0.06]}>
            <mesh position={[-1.2, 0, 0]}>
              <sphereGeometry args={[0.05]} />
              <meshStandardMaterial color={color} emissive={color} emissiveIntensity={0.5} />
            </mesh>
            
            <Text
              position={[-0.8, 0, 0]}
              fontSize={0.15}
              color={MIREA_COLORS.text}
              anchorX="left"
            >
              {type.toUpperCase()}
            </Text>
            
            <Text
              position={[1, 0, 0]}
              fontSize={0.15}
              color={prob > 0.3 ? MIREA_COLORS.error : prob > 0.15 ? MIREA_COLORS.warning : MIREA_COLORS.success}
              anchorX="right"
            >
              {(prob * 100).toFixed(1)}%
            </Text>
          </group>
        );
      })}
    </group>
  );
};

// Компонент: Печь с улучшенной визуализацией дефектов
const EnhancedFurnace = ({ temperature, defects, showDefects }: { 
  temperature: number; 
  defects: { [key: string]: number };
  showDefects: boolean;
}) => {
  const furnaceRef = useRef<THREE.Mesh>(null);
  const [glowIntensity, setGlowIntensity] = useState(0);
  
  useEffect(() => {
    const intensity = Math.min(1, Math.max(0, (temperature - 1400) / 300));
    setGlowIntensity(intensity);
  }, [temperature]);
  
  useFrame((state) => {
    if (furnaceRef.current && glowIntensity > 0.7) {
      const pulse = Math.sin(state.clock.elapsedTime * 2) * 0.03;
      furnaceRef.current.scale.set(1 + pulse, 1, 1 + pulse);
    }
  });
  
  return (
    <group position={[-6, 1.5, 0]}>
      {/* Основной корпус печи */}
      <mesh ref={furnaceRef} castShadow receiveShadow>
        <boxGeometry args={[4, 3, 3]} />
        <meshStandardMaterial 
          color={MIREA_COLORS.secondary}
          emissive={MIREA_COLORS.secondary}
          emissiveIntensity={glowIntensity * 0.7}
          metalness={0.3}
          roughness={0.6}
        />
      </mesh>
      
      {/* Дефекты на печи */}
      {showDefects && (
        <EnhancedFurnaceDefects 
          defects={defects} 
          position={[-6, 1.5, 0]} 
        />
      )}
      
      {/* Панель информации о дефектах */}
      {showDefects && (
        <DefectInfoPanel 
          defects={defects}
          position={[-10, 1.5, 0]}
        />
      )}
      
      {/* Temperature label */}
      <Text
        position={[0, 2.5, 0]}
        color={MIREA_COLORS.accent}
        fontSize={0.4}
        fontWeight={700}
        anchorX="center"
        anchorY="middle"
      >
        {temperature.toFixed(0)}°C
      </Text>
    </group>
  );
};

// Компонент: Формовочная линия с дефектами
const EnhancedFormingLine = ({ speed, moldTemp, glass, defects, showDefects }: { 
  speed: number; 
  moldTemp: number; 
  glass: boolean;
  defects: { [key: string]: number };
  showDefects: boolean;
}) => {
  const beltRef = useRef<THREE.Mesh>(null);
  const [beltOffset, setBeltOffset] = useState(0);
  
  const moldPositions: Array<[number, number, number]> = [
    [-4, 0.5, 0],
    [-1.5, 0.5, 0],
    [1, 0.5, 0],
    [3.5, 0.5, 0]
  ];
  
  useFrame((state) => {
    if (beltRef.current) {
      const delta = state.clock.getDelta();
      setBeltOffset((prev) => (prev + speed * delta * 0.05) % 10);
      beltRef.current.position.x = -4 + beltOffset;
    }
  });
  
  return (
    <group position={[4, 0.5, 0]}>
      {/* Формы для стекла */}
      {moldPositions.map((pos, i) => {
        const tempColor = moldTemp > 350 ? MIREA_COLORS.error : MIREA_COLORS.warning;
        const intensity = moldTemp > 350 ? 0.4 : 0.1;
        
        return (
          <group key={i} position={pos}>
            <mesh castShadow>
              <cylinderGeometry args={[0.6, 0.6, 0.8]} />
              <meshStandardMaterial 
                color={tempColor}
                emissive={tempColor}
                emissiveIntensity={intensity}
                metalness={0.5}
                roughness={0.5}
              />
            </mesh>
            
            {/* Дефекты на форме */}
            {showDefects && i === 2 && (
              <EnhancedMoldDefects 
                defects={defects}
                positions={[pos]}
              />
            )}
          </group>
        );
      })}
      
      {/* Дефекты на ленте конвейера */}
      {showDefects && (
        <group position={[beltOffset - 4, 0, 0]}>
          {Object.entries(defects).map(([type, prob], i) => {
            if (prob > 0.1) {
              return (
                <mesh key={i} position={[0, 0.1, (i - 1) * 0.3]}>
                  <boxGeometry args={[0.1, 0.02, 0.1]} />
                  <meshStandardMaterial 
                    color={(DEFECT_CONFIG as any)[type]?.color || MIREA_COLORS.text}
                    emissive={(DEFECT_CONFIG as any)[type]?.color || MIREA_COLORS.text}
                    emissiveIntensity={0.3}
                  />
                </mesh>
              );
            }
            return null;
          })}
        </group>
      )}
      
      {/* Speed indicator */}
      <Text
        position={[0, -1, 0]}
        color={MIREA_COLORS.accent}
        fontSize={0.35}
        fontWeight={700}
        anchorX="center"
      >
        {speed.toFixed(0)} м/мин
      </Text>
    </group>
  );
};

// Компонент: Легенда дефектов в 3D пространстве
const DefectLegend = ({ defects, position }: { 
  defects: { [key: string]: number };
  position: [number, number, number];
}) => {
  const [activeTab, setActiveTab] = useState(0);
  
  const defectTypes = Object.entries(defects);
  
  return (
    <group position={position}>
      {/* Основа легенды */}
      <mesh position={[0, 1, 0]}>
        <boxGeometry args={[2.5, 1.8, 0.05]} />
        <meshStandardMaterial 
          color={MIREA_COLORS.surfaceLight}
          transparent
          opacity={0.9}
          emissive={MIREA_COLORS.surfaceLight}
          emissiveIntensity={0.1}
        />
      </mesh>
      
      <Text
        position={[0, 1.7, 0.06]}
        fontSize={0.2}
        color={MIREA_COLORS.primary}
        anchorX="center"
        fontWeight={700}
      >
        ЛЕГЕНДА ДЕФЕКТОВ
      </Text>
      
      {/* Примеры дефектов */}
      <group position={[0, 1.2, 0.06]}>
        <Text
          position={[0, 0, 0]}
          fontSize={0.15}
          color={MIREA_COLORS.textSecondary}
          anchorX="center"
        >
          Нажмите на дефект для информации
        </Text>
      </group>
      
      {/* Визуализация примеров дефектов */}
      <group position={[0, 0.6, 0.06]}>
        {/* Пример трещины */}
        <CrackVisualization 
          position={[-0.8, 0, 0]}
          size={0.1}
          rotation={Math.PI / 4}
        />
        <Text
          position={[-0.8, -0.15, 0]}
          fontSize={0.12}
          color={MIREA_COLORS.error}
          anchorX="center"
        >
          Трещина
        </Text>
        
        {/* Пример пузыря */}
        <BubbleVisualization 
          position={[0, 0, 0]}
          size={0.05}
        />
        <Text
          position={[0, -0.15, 0]}
          fontSize={0.12}
          color={MIREA_COLORS.warning}
          anchorX="center"
        >
          Пузырь
        </Text>
        
        {/* Пример скола */}
        <ChipVisualization 
          position={[0.8, 0, 0]}
          size={0.05}
        />
        <Text
          position={[0.8, -0.15, 0]}
          fontSize={0.12}
          color={MIREA_COLORS.secondary}
          anchorX="center"
        >
          Скол
        </Text>
      </group>
      
      {/* Статистика */}
      <group position={[0, -0.3, 0.06]}>
        <Text
          position={[0, 0, 0]}
          fontSize={0.15}
          color={MIREA_COLORS.accent}
          anchorX="center"
        >
          Всего дефектов: {Object.values(defects).reduce((a, b) => a + b, 0).toFixed(1)}
        </Text>
      </group>
    </group>
  );
};

// Главный компонент сцены
const DigitalTwinScene = ({ 
  furnaceTemp, 
  moldTemp, 
  beltSpeed, 
  defects,
  showDefects
}: { 
  furnaceTemp: number; 
  moldTemp: number; 
  beltSpeed: number; 
  defects: { [key: string]: number };
  showDefects: boolean;
}) => {
  return (
    <>
      <ambientLight intensity={0.3} />
      <pointLight position={[10, 10, 10]} intensity={1} castShadow />
      
      <EnhancedFurnace 
        temperature={furnaceTemp}
        defects={defects}
        showDefects={showDefects}
      />
      
      <EnhancedFormingLine 
        speed={beltSpeed}
        moldTemp={moldTemp}
        glass={true}
        defects={defects}
        showDefects={showDefects}
      />
      
      {showDefects && (
        <DefectLegend 
          defects={defects}
          position={[0, 3, -5]}
        />
      )}
      
      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        maxPolarAngle={Math.PI / 2}
        minDistance={5}
        maxDistance={30}
      />
    </>
  );
};

// UI Компонент: Детальная панель анализа дефектов
const DefectAnalysisPanel = ({ defects, activeDefectType, onSelectDefect }: {
  defects: { [key: string]: number };
  activeDefectType: string | null;
  onSelectDefect: (type: string | null) => void;
}) => {
  const defectTypes = Object.entries(defects);
  
  return (
    <Paper sx={{ 
      p: 2, 
      background: MIREA_COLORS.surface,
      border: `1px solid ${MIREA_COLORS.primary}40`,
      borderRadius: 2
    }}>
      <Typography variant="h6" sx={{ 
        color: MIREA_COLORS.text, 
        mb: 2,
        display: 'flex',
        alignItems: 'center',
        gap: 1
      }}>
        <ErrorOutline /> Анализ дефектов
      </Typography>
      
      <Stack spacing={2}>
        {defectTypes.map(([type, prob]) => {
          const config = (DEFECT_CONFIG as any)[type];
          const isActive = activeDefectType === type;
          
          return (
            <Card 
              key={type}
              sx={{ 
                cursor: 'pointer',
                background: isActive ? `${config?.color}20` : MIREA_COLORS.surfaceLight,
                border: `1px solid ${isActive ? config?.color : MIREA_COLORS.primary}40`,
                transition: 'all 0.3s',
                '&:hover': {
                  borderColor: config?.color,
                  background: `${config?.color}15`
                }
              }}
              onClick={() => onSelectDefect(isActive ? null : type)}
            >
              <CardContent sx={{ py: 1.5 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Box sx={{ 
                      width: 12, 
                      height: 12, 
                      borderRadius: '50%', 
                      background: config?.color,
                      border: `2px solid ${config?.color}80`
                    }} />
                    <Typography variant="body2" sx={{ 
                      color: MIREA_COLORS.text,
                      fontWeight: 700,
                      textTransform: 'uppercase'
                    }}>
                      {type}
                    </Typography>
                  </Box>
                  
                  <Badge 
                    badgeContent={(prob * 100).toFixed(1) + '%'}
                    color={
                      prob > 0.3 ? 'error' : 
                      prob > 0.15 ? 'warning' : 
                      'success'
                    }
                    sx={{ 
                      '& .MuiBadge-badge': {
                        fontWeight: 700
                      }
                    }}
                  />
                </Box>
                
                {config?.description && (
                  <Typography variant="caption" sx={{ 
                    color: MIREA_COLORS.textSecondary,
                    display: 'block',
                    mt: 1
                  }}>
                    {config.description}
                  </Typography>
                )}
                
                <LinearProgress 
                  variant="determinate" 
                  value={prob * 100}
                  sx={{ 
                    mt: 1,
                    height: 4,
                    borderRadius: 2,
                    background: `${MIREA_COLORS.glass}40`,
                    '& .MuiLinearProgress-bar': {
                      background: config?.color,
                      borderRadius: 2
                    }
                  }}
                />
              </CardContent>
            </Card>
          );
        })}
      </Stack>
    </Paper>
  );
};

// Главный компонент приложения
const EnhancedDigitalTwin3D = () => {
  const [realState, setRealState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    defects: { 
      crack: 0.15, 
      bubble: 0.08, 
      chip: 0.05, 
      cloudiness: 0.03, 
      deformation: 0.02, 
      stress: 0.1 
    }
  });
  
  const [whatIfMode, setWhatIfMode] = useState(false);
  const [showDefects, setShowDefects] = useState(true);
  const [activeDefectType, setActiveDefectType] = useState<string | null>(null);
  const [defectHistory, setDefectHistory] = useState<Array<{time: Date; type: string; severity: number}>>([]);
  
  // Симуляция появления новых дефектов
  useEffect(() => {
    const interval = setInterval(() => {
      if (Math.random() > 0.7) {
        const defectTypes = Object.keys(realState.defects);
        const randomType = defectTypes[Math.floor(Math.random() * defectTypes.length)];
        
        setDefectHistory(prev => [
          ...prev.slice(-9),
          {
            time: new Date(),
            type: randomType,
            severity: Math.random()
          }
        ]);
      }
    }, 5000);
    
    return () => clearInterval(interval);
  }, [realState.defects]);
  
  return (
    <Box sx={{ 
      width: '100%', 
      minHeight: '100vh',
      background: `linear-gradient(135deg, ${MIREA_COLORS.background} 0%, ${MIREA_COLORS.backgroundLight} 100%)`,
      p: 2
    }}>
      <Grid container spacing={2}>
        {/* 3D Визуализация */}
        <Grid item xs={12} md={8}>
          <Paper sx={{ 
            height: '700px', 
            position: 'relative',
            overflow: 'hidden',
            background: MIREA_COLORS.background,
            border: `2px solid ${MIREA_COLORS.primary}60`,
            borderRadius: 2
          }}>
            <Canvas
              shadows
              camera={{ position: [15, 8, 15], fov: 50 }}
            >
              <DigitalTwinScene 
                furnaceTemp={realState.furnaceTemp}
                moldTemp={realState.moldTemp}
                beltSpeed={realState.beltSpeed}
                defects={realState.defects}
                showDefects={showDefects}
              />
            </Canvas>
            
            {/* Overlay Controls */}
            <Box sx={{ 
              position: 'absolute', 
              top: 16, 
              right: 16,
              display: 'flex',
              flexDirection: 'column',
              gap: 1
            }}>
              <Tooltip title="Показать/скрыть дефекты">
                <IconButton
                  onClick={() => setShowDefects(!showDefects)}
                  sx={{
                    background: `${MIREA_COLORS.surface}CC`,
                    color: showDefects ? MIREA_COLORS.error : MIREA_COLORS.text,
                    '&:hover': { background: `${MIREA_COLORS.surface}E6` }
                  }}
                >
                  {showDefects ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </Tooltip>
              
              <Tooltip title="What-If Анализ">
                <IconButton
                  onClick={() => setWhatIfMode(!whatIfMode)}
                  sx={{
                    background: `${MIREA_COLORS.surface}CC`,
                    color: whatIfMode ? MIREA_COLORS.secondary : MIREA_COLORS.text,
                    '&:hover': { background: `${MIREA_COLORS.surface}E6` }
                  }}
                >
                  <Science />
                </IconButton>
              </Tooltip>
            </Box>
          </Paper>
        </Grid>
        
        {/* Панель анализа */}
        <Grid item xs={12} md={4}>
          <Stack spacing={2}>
            <DefectAnalysisPanel 
              defects={realState.defects}
              activeDefectType={activeDefectType}
              onSelectDefect={setActiveDefectType}
            />
            
            {/* История дефектов */}
            <Paper sx={{ 
              p: 2, 
              background: MIREA_COLORS.surface,
              border: `1px solid ${MIREA_COLORS.primary}40`
            }}>
              <Typography variant="h6" sx={{ 
                color: MIREA_COLORS.text, 
                mb: 2,
                display: 'flex',
                alignItems: 'center',
                gap: 1
              }}>
                <Info /> История дефектов
              </Typography>
              
              <Stack spacing={1}>
                {defectHistory.slice().reverse().map((event, i) => (
                  <Box 
                    key={i}
                    sx={{ 
                      p: 1.5,
                      background: `${(DEFECT_CONFIG as any)[event.type]?.color || MIREA_COLORS.text}15`,
                      borderRadius: 1,
                      borderLeft: `4px solid ${(DEFECT_CONFIG as any)[event.type]?.color || MIREA_COLORS.text}`
                    }}
                  >
                    <Typography variant="caption" sx={{ 
                      color: MIREA_COLORS.textSecondary,
                      display: 'block'
                    }}>
                      {event.time.toLocaleTimeString()}
                    </Typography>
                    <Typography variant="body2" sx={{ 
                      color: MIREA_COLORS.text,
                      fontWeight: 700
                    }}>
                      Обнаружен {event.type}
                    </Typography>
                    <LinearProgress 
                      variant="determinate" 
                      value={event.severity * 100}
                      sx={{ 
                        mt: 1,
                        height: 3,
                        borderRadius: 1.5,
                        '& .MuiLinearProgress-bar': {
                          background: (DEFECT_CONFIG as any)[event.type]?.color,
                          borderRadius: 1.5
                        }
                      }}
                    />
                  </Box>
                ))}
              </Stack>
            </Paper>
          </Stack>
        </Grid>
      </Grid>
    </Box>
  );
};

export default EnhancedDigitalTwin3D;
