import React, { useState, useEffect, useCallback, useRef } from 'react';
import {
  Box,
  Paper,
  Typography,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  IconButton,
  Chip,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Badge,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Divider,
  Stack,
  Alert,
  Tooltip,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from '@mui/material';
import {
  Error as ErrorIcon,
  Warning as WarningIcon,
  Info as InfoIcon,
  CheckCircle as CheckCircleIcon,
  Close as CloseIcon,
  FilterList as FilterIcon,
  Search as SearchIcon,
  Notifications as NotificationsIcon,
  NotificationsActive as NotificationsActiveIcon,
  Delete as DeleteIcon,
  Check as CheckIcon,
  ExpandMore as ExpandMoreIcon,
  Psychology as PsychologyIcon,
  Timeline as TimelineIcon,
  Settings as SettingsIcon,
  Insights as InsightsIcon
} from '@mui/icons-material';
import { useTheme } from '@mui/material/styles';

interface NotificationAction {
  label: string;
  action: string;
  params?: Record<string, any>;
}

interface Notification {
  id: string;
  timestamp: string;
  category: string; // ML_DEFECT_PREDICTION, ML_SENSOR_ANOMALY, RL_RECOMMENDATION, CRITICAL_DEFECT, etc.
  priority: string; // CRITICAL, HIGH, MEDIUM, LOW
  title: string;
  message: string;
  source: string; // LSTM_Model, GNN_Model, RL_Agent, ML_MODEL, SENSOR, SYSTEM
  actions?: NotificationAction[];
  acknowledged: boolean;
  resolved: boolean;
  resolution_notes?: string;
  metadata?: Record<string, any>;
}

const API_BASE_URL = 'http://localhost:8000';

// Priority colors defined within component using theme
const priorityIcons: Record<string, React.ReactElement> = {
  CRITICAL: <ErrorIcon />,
  HIGH: <WarningIcon />,
  MEDIUM: <InfoIcon />,
  LOW: <CheckCircleIcon />
};

const categoryLabels: Record<string, string> = {
  CRITICAL_DEFECT: 'ÐšÑ€Ð¸Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸Ð¹ Ð´ÐµÑ„ÐµÐºÑ‚',
  PARAMETER_ANOMALY: 'ÐÐ½Ð¾Ð¼Ð°Ð»Ð¸Ñ Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ð¾Ð²',
  ML_PREDICTION_WARNING: 'ÐŸÑ€ÐµÐ´ÑƒÐ¿Ñ€ÐµÐ¶Ð´ÐµÐ½Ð¸Ðµ ML',
  MAINTENANCE_REMINDER: 'ÐÐ°Ð¿Ð¾Ð¼Ð¸Ð½Ð°Ð½Ð¸Ðµ Ð¾ Ð¾Ð±ÑÐ»ÑƒÐ¶Ð¸Ð²Ð°Ð½Ð¸Ð¸',
  SYSTEM_HEALTH: 'Ð—Ð´Ð¾Ñ€Ð¾Ð²ÑŒÐµ ÑÐ¸ÑÑ‚ÐµÐ¼Ñ‹',
  ML_DEFECT_PREDICTION: 'ðŸ¤– LSTM ÐŸÑ€Ð¾Ð³Ð½Ð¾Ð· Ð´ÐµÑ„ÐµÐºÑ‚Ð°',
  ML_SENSOR_ANOMALY: 'ðŸ§  GNN ÐÐ½Ð¾Ð¼Ð°Ð»Ð¸Ñ Ð´Ð°Ñ‚Ñ‡Ð¸ÐºÐ¾Ð²',
  RL_RECOMMENDATION: 'ðŸŽ® RL Ð ÐµÐºÐ¾Ð¼ÐµÐ½Ð´Ð°Ñ†Ð¸Ñ'
};

// Extended category icons
const categoryIcons: Record<string, React.ReactElement> = {
  ML_DEFECT_PREDICTION: <TimelineIcon />,
  ML_SENSOR_ANOMALY: <InsightsIcon />,
  RL_RECOMMENDATION: <PsychologyIcon />,
  CRITICAL_DEFECT: <ErrorIcon />,
  PARAMETER_ANOMALY: <SettingsIcon />,
  ML_PREDICTION_WARNING: <WarningIcon />,
  MAINTENANCE_REMINDER: <SettingsIcon />,
  SYSTEM_HEALTH: <CheckCircleIcon />
};

const NotificationsCenter: React.FC = () => {
  const theme = useTheme();

  const priorityColors: Record<string, string> = {
    CRITICAL: theme.palette.error.main,
    HIGH: theme.palette.warning.main,
    MEDIUM: theme.palette.info.main,
    LOW: theme.palette.success.main
  };

  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Filters
  const [priorityFilter, setPriorityFilter] = useState<string>('ALL');
  const [categoryFilter, setCategoryFilter] = useState<string>('ALL');
  const [searchQuery, setSearchQuery] = useState('');
  const [showOnlyUnacknowledged, setShowOnlyUnacknowledged] = useState(false);

  // Detail dialog
  const [selectedNotification, setSelectedNotification] = useState<Notification | null>(null);
  const [detailDialogOpen, setDetailDialogOpen] = useState(false);

  // Sound alerts
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const [soundEnabled, setSoundEnabled] = useState(true);
  const lastNotificationId = useRef<string | null>(null);

  // Fetch notifications
  const fetchNotifications = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const params = new URLSearchParams();
      if (priorityFilter !== 'ALL') params.append('priority', priorityFilter);
      if (categoryFilter !== 'ALL') params.append('category', categoryFilter);

      const response = await fetch(`${API_BASE_URL}/api/notifications/active?${params.toString()}`);

      if (!response.ok) {
        throw new Error(`Failed to fetch notifications: ${response.statusText}`);
      }

      const data = await response.json();
      setNotifications(data.notifications || []);

      // Play sound for new critical notifications
      if (soundEnabled && data.notifications && data.notifications.length > 0) {
        const latestNotification = data.notifications[0];
        if (
          latestNotification.priority === 'CRITICAL' &&
          !latestNotification.acknowledged &&
          latestNotification.id !== lastNotificationId.current
        ) {
          playAlertSound();
          lastNotificationId.current = latestNotification.id;
        }
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error occurred');
      console.error('Error fetching notifications:', err);
    } finally {
      setLoading(false);
    }
  }, [priorityFilter, categoryFilter, soundEnabled]);

  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 5000); // Poll every 5 seconds
    return () => clearInterval(interval);
  }, [fetchNotifications]);

  // Initialize audio
  useEffect(() => {
    audioRef.current = new Audio('/alert.mp3'); // Ensure alert.mp3 is in public folder
    audioRef.current.volume = 0.5;
  }, []);

  const playAlertSound = () => {
    if (audioRef.current && soundEnabled) {
      audioRef.current.play().catch(err => console.warn('Audio playback failed:', err));
    }
  };

  const acknowledgeNotification = async (notificationId: string) => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/notifications/${notificationId}/acknowledge`, {
        method: 'POST'
      });

      if (!response.ok) {
        throw new Error('Failed to acknowledge notification');
      }

      // Update local state
      setNotifications(prev =>
        prev.map(n => (n.id === notificationId ? { ...n, acknowledged: true } : n))
      );
    } catch (err) {
      console.error('Error acknowledging notification:', err);
    }
  };

  const deleteNotification = async (notificationId: string) => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/notifications/${notificationId}`, {
        method: 'DELETE'
      });

      if (!response.ok) {
        throw new Error('Failed to delete notification');
      }

      // Remove from local state
      setNotifications(prev => prev.filter(n => n.id !== notificationId));
    } catch (err) {
      console.error('Error deleting notification:', err);
    }
  };

  const executeAction = async (action: NotificationAction, notificationId: string) => {
    try {
      console.log('Executing action:', action);

      // Handle specific actions
      if (action.action === 'apply_recommendation') {
        // Apply RL recommendation
        await fetch(`${API_BASE_URL}/api/knowledge-graph/enrich/human-decision`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            notification_id: notificationId,
            decision: 'applied',
            defect_type: selectedNotification?.metadata?.defect_type || 'unknown',
            notes: 'Applied via notification center'
          })
        });
      } else if (action.action === 'adjust_parameters') {
        // Adjust parameters action - would typically open a parameter adjustment dialog
        console.log('Adjust parameters action triggered');
      } else if (action.action === 'apply_rl_recommendation') {
        // Apply RL recommendation from sensor anomaly
        await fetch(`${API_BASE_URL}/api/knowledge-graph/enrich/human-decision`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            notification_id: notificationId,
            decision: 'applied',
            defect_type: 'sensor_anomaly',
            notes: 'Applied RL recommendation for sensor anomaly'
          })
        });
      } else if (action.action === 'inspect_sensors') {
        // Inspect sensors action
        console.log('Inspect sensors action triggered');
      } else if (action.action === 'dismiss') {
        // Dismiss action
        await acknowledgeNotification(notificationId);
      }

      // Acknowledge the notification after action
      await acknowledgeNotification(notificationId);
    } catch (err) {
      console.error('Error executing action:', err);
    }
  };

  const openDetailDialog = (notification: Notification) => {
    setSelectedNotification(notification);
    setDetailDialogOpen(true);
  };

  const closeDetailDialog = () => {
    setDetailDialogOpen(false);
    setSelectedNotification(null);
  };

  // Filter notifications
  const filteredNotifications = notifications.filter(notification => {
    if (showOnlyUnacknowledged && notification.acknowledged) return false;
    if (searchQuery && !notification.message.toLowerCase().includes(searchQuery.toLowerCase()) &&
        !notification.title.toLowerCase().includes(searchQuery.toLowerCase())) {
      return false;
    }
    return true;
  });

  const unacknowledgedCount = notifications.filter(n => !n.acknowledged).length;

  // Format timestamp for display
  const formatTimestamp = (timestamp: string) => {
    return new Date(timestamp).toLocaleString('ru-RU');
  };

  // Render parameter snapshot table
  const renderParameterSnapshot = (parameters: Record<string, any>) => {
    if (!parameters) return null;

    return (
      <TableContainer>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>ÐŸÐ°Ñ€Ð°Ð¼ÐµÑ‚Ñ€</TableCell>
              <TableCell align="right">Ð—Ð½Ð°Ñ‡ÐµÐ½Ð¸Ðµ</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {Object.entries(parameters).map(([key, value]) => (
              <TableRow key={key}>
                <TableCell>{key}</TableCell>
                <TableCell align="right">{typeof value === 'number' ? value.toFixed(2) : value}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    );
  };

  // Render LSTM analysis details
  const renderLSTMAnalysis = (metadata: Record<string, any>) => {
    if (!metadata || !metadata.defect_probabilities) return null;

    return (
      <Accordion>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Typography>ðŸ¤– ÐÐ½Ð°Ð»Ð¸Ð· LSTM</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography variant="subtitle2" gutterBottom>
            Ð’ÐµÑ€Ð¾ÑÑ‚Ð½Ð¾ÑÑ‚Ð¸ Ð´ÐµÑ„ÐµÐºÑ‚Ð¾Ð²:
          </Typography>
          <TableContainer>
            <Table size="small">
              <TableHead>
                <TableRow>
                  <TableCell>Ð¢Ð¸Ð¿ Ð´ÐµÑ„ÐµÐºÑ‚Ð°</TableCell>
                  <TableCell align="right">Ð’ÐµÑ€Ð¾ÑÑ‚Ð½Ð¾ÑÑ‚ÑŒ</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {Object.entries(metadata.defect_probabilities).map(([defectType, probability]) => (
                  <TableRow key={defectType}>
                    <TableCell>{defectType}</TableCell>
                    <TableCell align="right">{(probability as number).toFixed(2)}%</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </AccordionDetails>
      </Accordion>
    );
  };

  // Render RL recommendations
  const renderRLRecommendations = (metadata: Record<string, any>) => {
    if (!metadata || !metadata.recommendations) return null;

    const recommendations = Array.isArray(metadata.recommendations) 
      ? metadata.recommendations 
      : [metadata.recommendations];

    return (
      <Accordion>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Typography>ðŸŽ® Ð ÐµÐºÐ¾Ð¼ÐµÐ½Ð´Ð°Ñ†Ð¸Ð¸ RL Ð°Ð³ÐµÐ½Ñ‚Ð°</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Stack spacing={2}>
            {recommendations.map((rec: any, index: number) => (
              <Paper key={index} variant="outlined" sx={{ p: 2 }}>
                <Typography variant="subtitle2" gutterBottom>
                  {rec.action || rec.text || 'Ð ÐµÐºÐ¾Ð¼ÐµÐ½Ð´Ð°Ñ†Ð¸Ñ'}
                </Typography>
                <Stack direction="row" spacing={1} flexWrap="wrap">
                  {rec.confidence && (
                    <Chip 
                      label={`Ð”Ð¾Ð²ÐµÑ€Ð¸Ðµ: ${(rec.confidence * 100).toFixed(1)}%`} 
                      size="small" 
                      color={rec.confidence > 0.8 ? "success" : rec.confidence > 0.6 ? "warning" : "default"}
                    />
                  )}
                  {rec.priority && (
                    <Chip 
                      label={`ÐŸÑ€Ð¸Ð¾Ñ€Ð¸Ñ‚ÐµÑ‚: ${rec.priority}`} 
                      size="small" 
                      color={rec.priority === 'HIGH' ? "error" : rec.priority === 'MEDIUM' ? "warning" : "info"}
                    />
                  )}
                  {rec.parameter && (
                    <Chip 
                      label={`ÐŸÐ°Ñ€Ð°Ð¼ÐµÑ‚Ñ€: ${rec.parameter}`} 
                      size="small" 
                      variant="outlined"
                    />
                  )}
                </Stack>
                {rec.expected_impact && (
                  <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                    ÐžÐ¶Ð¸Ð´Ð°ÐµÐ¼Ñ‹Ð¹ ÑÑ„Ñ„ÐµÐºÑ‚: {rec.expected_impact}
                  </Typography>
                )}
                {rec.justification && (
                  <Typography variant="body2" sx={{ mt: 1, fontStyle: 'italic' }}>
                    ÐžÐ±Ð¾ÑÐ½Ð¾Ð²Ð°Ð½Ð¸Ðµ: {rec.justification}
                  </Typography>
                )}
                {(rec.current_value !== undefined || rec.suggested_value !== undefined) && (
                  <Typography variant="body2" sx={{ mt: 1 }}>
                    Ð¢ÐµÐºÑƒÑ‰ÐµÐµ Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ðµ: {rec.current_value !== undefined ? rec.current_value.toFixed(1) : 'N/A'} â†’ 
                    Ð ÐµÐºÐ¾Ð¼ÐµÐ½Ð´ÑƒÐµÐ¼Ð¾Ðµ: {rec.suggested_value !== undefined ? rec.suggested_value.toFixed(1) : 'N/A'}
                  </Typography>
                )}
              </Paper>
            ))}
          </Stack>
        </AccordionDetails>
      </Accordion>
    );
  };

  return (
    // @ts-expect-error MUI sx prop complexity limit
    <Box sx={{ p: 3 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        {/* Header */}
        <Stack direction="row" justifyContent="space-between" alignItems="center" mb={3}>
          <Stack direction="row" alignItems="center" spacing={2}>
            <Badge badgeContent={unacknowledgedCount} color="error">
              <NotificationsActiveIcon sx={{ fontSize: 40, color: theme.palette.primary.main }} />
            </Badge>
            <Typography variant="h4">
              Ð¦ÐµÐ½Ñ‚Ñ€ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹
            </Typography>
          </Stack>

          <Stack direction="row" spacing={2}>
            <Tooltip title={soundEnabled ? 'ÐžÑ‚ÐºÐ»ÑŽÑ‡Ð¸Ñ‚ÑŒ Ð·Ð²ÑƒÐº' : 'Ð’ÐºÐ»ÑŽÑ‡Ð¸Ñ‚ÑŒ Ð·Ð²ÑƒÐº'}>
              <IconButton onClick={() => setSoundEnabled(!soundEnabled)}>
                {soundEnabled ? <NotificationsActiveIcon /> : <NotificationsIcon />}
              </IconButton>
            </Tooltip>
            <Button
              variant={showOnlyUnacknowledged ? 'contained' : 'outlined'}
              onClick={() => setShowOnlyUnacknowledged(!showOnlyUnacknowledged)}
            >
              Ð¢Ð¾Ð»ÑŒÐºÐ¾ Ð½ÐµÐ¿Ñ€Ð¾Ñ‡Ð¸Ñ‚Ð°Ð½Ð½Ñ‹Ðµ ({unacknowledgedCount})
            </Button>
          </Stack>
        </Stack>

        {/* Filters */}
        <Stack direction="row" spacing={2} mb={3}>
          <TextField
            placeholder="ÐŸÐ¾Ð¸ÑÐº..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            InputProps={{
              startAdornment: <SearchIcon sx={{ mr: 1, color: theme.palette.text.secondary }} />
            }}
            sx={{ flexGrow: 1 }}
          />

          <FormControl sx={{ minWidth: 150 }}>
            <InputLabel>ÐŸÑ€Ð¸Ð¾Ñ€Ð¸Ñ‚ÐµÑ‚</InputLabel>
            <Select
              value={priorityFilter}
              onChange={(e) => setPriorityFilter(e.target.value)}
              label="ÐŸÑ€Ð¸Ð¾Ñ€Ð¸Ñ‚ÐµÑ‚"
            >
              <MenuItem value="ALL">Ð’ÑÐµ</MenuItem>
              <MenuItem value="CRITICAL">ÐšÑ€Ð¸Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸Ð¹</MenuItem>
              <MenuItem value="HIGH">Ð’Ñ‹ÑÐ¾ÐºÐ¸Ð¹</MenuItem>
              <MenuItem value="MEDIUM">Ð¡Ñ€ÐµÐ´Ð½Ð¸Ð¹</MenuItem>
              <MenuItem value="LOW">ÐÐ¸Ð·ÐºÐ¸Ð¹</MenuItem>
            </Select>
          </FormControl>

          <FormControl sx={{ minWidth: 200 }}>
            <InputLabel>ÐšÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ñ</InputLabel>
            <Select
              value={categoryFilter}
              onChange={(e) => setCategoryFilter(e.target.value)}
              label="ÐšÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ñ"
            >
              <MenuItem value="ALL">Ð’ÑÐµ</MenuItem>
              <MenuItem value="ML_DEFECT_PREDICTION">ðŸ¤– LSTM ÐŸÑ€Ð¾Ð³Ð½Ð¾Ð· Ð´ÐµÑ„ÐµÐºÑ‚Ð°</MenuItem>
              <MenuItem value="ML_SENSOR_ANOMALY">ðŸ§  GNN ÐÐ½Ð¾Ð¼Ð°Ð»Ð¸Ñ Ð´Ð°Ñ‚Ñ‡Ð¸ÐºÐ¾Ð²</MenuItem>
              <MenuItem value="RL_RECOMMENDATION">ðŸŽ® RL Ð ÐµÐºÐ¾Ð¼ÐµÐ½Ð´Ð°Ñ†Ð¸Ñ</MenuItem>
              <MenuItem value="CRITICAL_DEFECT">ÐšÑ€Ð¸Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¸Ð¹ Ð´ÐµÑ„ÐµÐºÑ‚</MenuItem>
              <MenuItem value="PARAMETER_ANOMALY">ÐÐ½Ð¾Ð¼Ð°Ð»Ð¸Ñ Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ð¾Ð²</MenuItem>
              <MenuItem value="ML_PREDICTION_WARNING">ÐŸÑ€ÐµÐ´ÑƒÐ¿Ñ€ÐµÐ¶Ð´ÐµÐ½Ð¸Ðµ ML</MenuItem>
              <MenuItem value="MAINTENANCE_REMINDER">ÐžÐ±ÑÐ»ÑƒÐ¶Ð¸Ð²Ð°Ð½Ð¸Ðµ</MenuItem>
              <MenuItem value="SYSTEM_HEALTH">Ð—Ð´Ð¾Ñ€Ð¾Ð²ÑŒÐµ ÑÐ¸ÑÑ‚ÐµÐ¼Ñ‹</MenuItem>
            </Select>
          </FormControl>
        </Stack>

        {/* Error alert */}
        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {/* Notifications list */}
        <List sx={{ maxHeight: 600, overflow: 'auto' }}>
          {filteredNotifications.length === 0 ? (
            <Box sx={{ textAlign: 'center', py: 4 }}>
              <Typography variant="h6" color="textSecondary">
                {loading ? 'Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹...' : 'ÐÐµÑ‚ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹'}
              </Typography>
            </Box>
          ) : (
            filteredNotifications.map((notification, index) => (
              <React.Fragment key={notification.id}>
                {index > 0 && <Divider />}
                <ListItem
                  sx={{
                    backgroundColor: notification.acknowledged
                      ? 'transparent'
                      : `${priorityColors[notification.priority]}10`,
                    borderLeft: `4px solid ${priorityColors[notification.priority]}`,
                    mb: 1,
                    borderRadius: 1,
                    '&:hover': { backgroundColor: `${theme.palette.primary.main}08` }
                  }}
                >
                  <ListItemIcon>
                    {React.cloneElement(priorityIcons[notification.priority], {
                      sx: { color: priorityColors[notification.priority], fontSize: 32 }
                    })}
                  </ListItemIcon>

                  <ListItemText
                    primary={
                      <Stack direction="row" alignItems="center" spacing={1}>
                        <Typography variant="subtitle1" fontWeight="bold">
                          {notification.title}
                        </Typography>
                        <Chip
                          label={notification.priority}
                          size="small"
                          sx={{
                            backgroundColor: priorityColors[notification.priority],
                            color: 'white',
                            fontSize: '0.7rem'
                          }}
                        />
                        <Chip
                          label={categoryLabels[notification.category]}
                          size="small"
                          variant="outlined"
                          sx={{ fontSize: '0.7rem' }}
                        />
                      </Stack>
                    }
                    secondary={
                      <Box>
                        <Typography variant="body2" color="textSecondary" sx={{ mt: 0.5 }}>
                          {notification.message}
                        </Typography>
                        <Typography variant="caption" color="textSecondary" sx={{ mt: 0.5 }}>
                          {formatTimestamp(notification.timestamp)} â€¢ {notification.source}
                        </Typography>

                        {notification.actions && notification.actions.length > 0 && (
                          <Stack direction="row" spacing={1} sx={{ mt: 1 }}>
                            {notification.actions.map((action, idx) => (
                              <Button
                                key={idx}
                                size="small"
                                variant="outlined"
                                onClick={() => executeAction(action, notification.id)}
                              >
                                {action.label}
                              </Button>
                            ))}
                          </Stack>
                        )}
                      </Box>
                    }
                    onClick={() => openDetailDialog(notification)}
                    sx={{ cursor: 'pointer' }}
                  />

                  <Stack direction="row" spacing={1}>
                    {!notification.acknowledged && (
                      <Tooltip title="ÐŸÐ¾Ð´Ñ‚Ð²ÐµÑ€Ð´Ð¸Ñ‚ÑŒ">
                        <IconButton
                          size="small"
                          onClick={() => acknowledgeNotification(notification.id)}
                          sx={{ color: theme.palette.success.main }}
                        >
                          <CheckIcon />
                        </IconButton>
                      </Tooltip>
                    )}
                    <Tooltip title="Ð£Ð´Ð°Ð»Ð¸Ñ‚ÑŒ">
                      <IconButton
                        size="small"
                        onClick={() => deleteNotification(notification.id)}
                        sx={{ color: theme.palette.error.main }}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </Tooltip>
                  </Stack>
                </ListItem>
              </React.Fragment>
            ))
          )}
        </List>
      </Paper>

      {/* Detail Dialog */}
      <Dialog
        open={detailDialogOpen}
        onClose={closeDetailDialog}
        maxWidth="md"
        fullWidth
      >
        {selectedNotification && (
          <>
            <DialogTitle>
              <Stack direction="row" alignItems="center" spacing={2}>
                {priorityIcons[selectedNotification.priority]}
                <Typography variant="h6">{selectedNotification.title}</Typography>
                <Chip
                  label={selectedNotification.priority}
                  size="small"
                  sx={{ backgroundColor: priorityColors[selectedNotification.priority], color: 'white' }}
                />
              </Stack>
            </DialogTitle>
            <DialogContent dividers>
              <Typography variant="body1" paragraph>
                {selectedNotification.message}
              </Typography>

              <Divider sx={{ my: 2 }} />

              <Stack spacing={2}>
                <Typography variant="subtitle2" color="textSecondary">
                  ÐšÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ñ: {categoryLabels[selectedNotification.category]}
                </Typography>
                <Typography variant="subtitle2" color="textSecondary">
                  Ð˜ÑÑ‚Ð¾Ñ‡Ð½Ð¸Ðº: {selectedNotification.source}
                </Typography>
                <Typography variant="subtitle2" color="textSecondary">
                  Ð’Ñ€ÐµÐ¼Ñ: {formatTimestamp(selectedNotification.timestamp)}
                </Typography>
                <Typography variant="subtitle2" color="textSecondary">
                  Ð¡Ñ‚Ð°Ñ‚ÑƒÑ: {selectedNotification.acknowledged ? 'ÐŸÐ¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¾' : 'ÐÐµ Ð¿Ð¾Ð´Ñ‚Ð²ÐµÑ€Ð¶Ð´ÐµÐ½Ð¾'}
                </Typography>

                {/* Metadata section */}
                {selectedNotification.metadata && Object.keys(selectedNotification.metadata).length > 0 && (
                  <>
                    <Divider sx={{ my: 2 }} />
                    <Typography variant="h6">Ð”ÐµÑ‚Ð°Ð»Ð¸ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ñ</Typography>

                    {/* Parameter snapshot */}
                    {selectedNotification.metadata.parameters_snapshot && (
                      <>
                        <Typography variant="subtitle2" gutterBottom>
                          Ð¡Ð½Ð¸Ð¼Ð¾Ðº Ð¿Ð°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ð¾Ð²:
