sourcesContent:
import React, { useState, useEffect } from 'react';
import {
    Card,
    CardContent,
    Typography,
    Grid,
    Chip,
    Button,
    Box,
    LinearProgress,
    Avatar,
    Paper,
    Fade,
    Zoom,
    Grow,
    Slide,
    useTheme,
    alpha,
    IconButton,
    Tooltip,
    Skeleton
} from '@mui/material';
import { styled } from '@mui/material/styles';
import {
    TrendingUp,
    Warning,
    CheckCircle,
    Factory,
    Speed,
    Thermostat,
    DeviceHub,
    Psychology,
    LocalFireDepartment,
    Science,
    BubbleChart,
    Timeline,
    AutoAwesome,
    ArrowUpward,
    ArrowDownward,
    Refresh,
    FilterList,
    MoreVert,
    School,
    RocketLaunch,
    Insights,
    Palette,
    WaterDrop,
    Diamond,
    Visibility,
    TrendingFlat,
    PlayCircle,
    PauseCircle
} from '@mui/icons-material';
import {
    LineChart,
    Line,
    PieChart,
    Pie,
    Cell,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip as RechartsTooltip,
    Legend,
    ResponsiveContainer,
    Area,
    AreaChart,
    RadialBarChart,
    RadialBar,
    PolarAngleAxis,
    PolarGrid,
    PolarRadiusAxis
} from 'recharts';
import type { Theme } from '@mui/material/styles';

// Расширенная палитра РТУ МИРЭА 2025
const MIREA_2025_COLORS = {
    primary: '#0066FF',
    primaryGradient: 'linear-gradient(135deg, #0066FF 0%, #00E5FF 100%)',
    primaryGlass: 'rgba(0, 102, 255, 0.15)',
    secondary: '#FF3366',
    secondaryGradient: 'linear-gradient(135deg, #FF3366 0%, #FF9E6D 100%)',
    tertiary: '#00E676',
    tertiaryGradient: 'linear-gradient(135deg, #00E676 0%, #6EFFB2 100%)',
    warning: '#FFD700',
    warningGradient: 'linear-gradient(135deg, #FFD700 0%, #FFEE4D 100%)',
    error: '#FF1744',
    errorGradient: 'linear-gradient(135deg, #FF1744 0%, #FF5C7C 100%)',
    info: '#00E5FF',
    infoGradient: 'linear-gradient(135deg, #00E5FF 0%, #6EFFFF 100%)',
    dark: '#000814',
    darkSurface: 'rgba(13, 27, 42, 0.7)',
    glassSurface: 'rgba(255, 255, 255, 0.05)',
    lightSurface: 'rgba(255, 255, 255, 0.1)',
    textPrimary: '#FFFFFF',
    textSecondary: 'rgba(255, 255, 255, 0.8)',
    textTertiary: 'rgba(255, 255, 255, 0.6)',
    accentPurple: '#9D4EDD',
    accentCyan: '#00E5FF',
    accentPink: '#FF3366',
    accentYellow: '#FFD700',
};

// Стилизованные компоненты с glassmorphism эффектом
const GlassCard = styled(Card)(({ theme }) => ({
    background: 'rgba(13, 27, 42, 0.4)',
    backdropFilter: 'blur(20px) saturate(180%)',
    border: '1px solid rgba(0, 102, 255, 0.2)',
    borderRadius: '24px',
    position: 'relative',
    overflow: 'hidden',
    transition: 'all 0.4s cubic-bezier(0.34, 1.56, 0.64, 1)',
    '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: '-100%',
        width: '100%',
        height: '100%',
        background: 'linear-gradient(90deg, transparent, rgba(0, 229, 255, 0.1), transparent)',
        transition: 'left 0.7s',
    },
    '&:hover': {
        transform: 'translateY(-8px)',
        borderColor: 'rgba(0, 229, 255, 0.5)',
        boxShadow: `0 20px 60px rgba(0, 102, 255, 0.3),
      0 0 120px rgba(0, 229, 255, 0.2),
      inset 0 1px 0 rgba(255, 255, 255, 0.1)`,
        '&::before': {
            left: '100%',
        },
    },
}));

const HolographicBadge = styled(Box)(({ theme }) => ({
    position: 'relative',
    padding: '8px 16px',
    background: 'linear-gradient(135deg, rgba(0, 102, 255, 0.2), rgba(0, 229, 255, 0.1))',
    border: '1px solid rgba(0, 229, 255, 0.3)',
    borderRadius: '12px',
    backdropFilter: 'blur(10px)',
    overflow: 'hidden',
    '&::before': {
        content: '""',
        position: 'absolute',
        top: '-50%',
        left: '-50%',
        width: '200%',
        height: '200%',
        background: 'linear-gradient(45deg, transparent 30%, rgba(0, 229, 255, 0.1) 50%, transparent 70%)',
        animation: 'hologramMove 3s linear infinite',
    },
    '@keyframes hologramMove': {
        '0%': { transform: 'rotate(0deg) translateX(-25%)' },
        '100%': { transform: 'rotate(360deg) translateX(-25%)' },
    },
}));

const DecorativeElement = styled(Box)(({ theme }) => ({
    position: 'absolute',
    top: -20,
    right: -20,
    width: 80,
    height: 80,
    borderRadius: '50%',
    opacity: 0.5,
    zIndex: 0
}));

const CardHeaderContainer = styled(Box)(({ theme }) => ({
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    marginBottom: theme.spacing(2)
}));

const TrendContainer = styled(Box)(({ theme }) => ({
    display: 'flex',
    alignItems: 'center',
    gap: theme.spacing(1),
    marginTop: theme.spacing(2)
}));

const TrendValueContainer = styled(Box)(({ theme }) => ({
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0.75, 1.5),
    borderRadius: '12px'
}));

interface AnimatedKPICardProps {
    title: string;
    value: string | number;
    icon: React.ReactElement;
    color: string;
    trend?: { value: number; direction: 'up' | 'down' | 'stable' };
    unit?: string;
    delay?: number;
}

const AnimatedKPICard: React.FC<AnimatedKPICardProps> = ({
    title,
    value,
    icon,
    color,
    trend,
    unit = '',
    delay = 0
}) => {
    const [animatedValue, setAnimatedValue] = useState(0);

    useEffect(() => {
        if (typeof value === 'number') {
            const duration = 1500;
            const steps = 60;
            const increment = value / steps;
            let current = 0;
            const timer = setInterval(() => {
                current += increment;
                if (current >= value) {
                    setAnimatedValue(value);
                    clearInterval(timer);
                } else {
                    setAnimatedValue(current);
                }
            }, duration / steps);

            return () => clearInterval(timer);
        }
    }, [value]);

    const AvatarStyled = styled(Avatar)({
        width: 56,
        height: 56,
        backdropFilter: 'blur(10px)',
    });

    const LinearProgressStyled = styled(LinearProgress)({
        mt: 3,
        height: 8,
        borderRadius: 4,
        background: 'rgba(255, 255, 255, 0.1)',
        '& .MuiLinearProgress-bar': {
            borderRadius: 4,
            position: 'relative',
            overflow: 'hidden',
            '&::after': {
                content: '""',
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                background: 'linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent)',
                animation: 'shimmer 2s infinite',
            },
        },
        '@keyframes shimmer': {
            '0%': { transform: 'translateX(-100%)' },
            '100%': { transform: 'translateX(100%)' },
        },
    });

    const AvatarContent = styled(AvatarStyled)({
        background: `linear-gradient(135deg, ${color}20, ${color}05)`,
        border: `2px solid ${color}40`,
    });

    const LinearProgressBar = styled(LinearProgressStyled)({
        '& .MuiLinearProgress-bar': {
            background: `linear-gradient(90deg, ${color}, ${color}CC)`,
        },
    });

    return (
        <Grow in timeout={800} style={{ transitionDelay: `${delay}ms` }}>
            <GlassCard>
                <CardContent sx={{ p: 3.5, position: 'relative', zIndex: 1 }}>
                    <CardHeaderContainer>
                        <Typography variant="subtitle2" sx={{
                            color: MIREA_2025_COLORS.textTertiary,
                            fontWeight: 600,
                            letterSpacing: '0.05em',
                            textTransform: 'uppercase'
                        }}>
                            {title}
                        </Typography>
                        <AvatarContent>
                            {React.cloneElement(icon, {
                                sx: {
                                    fontSize: 28,
                                    background: color,
                                    WebkitBackgroundClip: 'text',
                                    WebkitTextFillColor: 'transparent',
                                }
                            })}
                        </AvatarContent>
                    </CardHeaderContainer>

                    <Typography variant="h2" sx={{
                        fontWeight: 800,
                        background: color,
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent',
                        mb: 1,
                        fontSize: '3.5rem',
                        lineHeight: 1,
                    }}>
                        {typeof value === 'number' ? animatedValue.toFixed(1) : value}{unit}
                    </Typography>

                    {trend && (
                        <TrendContainer>
                            <TrendValueContainer sx={{
                                background: trend.direction === 'up'
                                    ? 'rgba(0, 230, 118, 0.15)'
                                    : trend.direction === 'down'
                                        ? 'rgba(255, 23, 68, 0.15)'
                                        : 'rgba(0, 229, 255, 0.15)',
                                border: trend.direction === 'up'
                                    ? '1px solid rgba(0, 230, 118, 0.3)'
                                    : trend.direction === 'down'
                                        ? '1px solid rgba(255, 23, 68, 0.3)'
                                        : '1px solid rgba(0, 229, 255, 0.3)',
                            }}>
                                {trend.direction === 'up' ? (
                                    <ArrowUpward sx={{ fontSize: 16, color: '#00E676', mr: 0.5 }} />
                                ) : trend.direction === 'down' ? (
                                    <ArrowDownward sx={{ fontSize: 16, color: '#FF1744', mr: 0.5 }} />
                                ) : (
                                    <TrendingFlat sx={{ fontSize: 16, color: '#00E5FF', mr: 0.5 }} />
                                )}
                                <Typography variant="caption" sx={{
                                    fontWeight: 700,
                                    color: trend.direction === 'up' ? '#00E676' : trend.direction === 'down' ? '#FF1744' : '#00E5FF'
                                }}>
                                    {Math.abs(trend.value)}%
                                </Typography>
                            </TrendValueContainer>
                            <Typography variant="caption" sx={{ color: MIREA_2025_COLORS.textTertiary }}>
                                за последний час
                            </Typography>
                        </TrendContainer>
                    )}

                    <LinearProgressBar
                        variant="determinate"
                        value={typeof value === 'number' ? Math.min(100, Number(value)) : 0}
                    />
                </CardContent>

                {/* Декоративные элементы */}
                <DecorativeElement sx={{
                    background: `radial-gradient(circle, ${color}30 0%, transparent 70%)`,
                }} />
            </GlassCard>
        </Grow>
    );
};

const ChartCardHeaderContainer = styled(Box)(({ theme }) => ({
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    marginBottom: theme.spacing(3)
}));

interface InteractiveChartCardProps {
    title: string;
    subtitle?: string;
    icon: React.ReactElement;
    children: React.ReactNode;
    action?: React.ReactNode;
}

const InteractiveChartCard: React.FC<InteractiveChartCardProps> = ({
    title,
    subtitle,
    icon,
    children,
    action
}) => {
    const FlexContainer = styled(Box)({
        flex: '1 1 auto'
    });

    const IconBox = styled(Box)({
        display: 'flex',
        alignItems: 'center',
        gap: 12
    });

    const TitleTypography = styled(Typography)({
        fontWeight: 700,
        color: MIREA_2025_COLORS.textPrimary,
        background: MIREA_2025_COLORS.primaryGradient,
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
    });

    const SubtitleTypography = styled(Typography)({
        color: MIREA_2025_COLORS.textTertiary
    });

    return (
        <Slide in timeout={1000} direction="up">
            <GlassCard style={{ height: '100%' }}>
                <CardContent style={{ padding: '28px', height: '100%', display: 'flex', flexDirection: 'column' }}>
                    <ChartCardHeaderContainer>
                        <IconBox>
                            {icon}
                            <TitleTypography variant="h6">
                                {title}
                            </TitleTypography>
                        </IconBox>
                        {subtitle && (
                            <SubtitleTypography variant="body2">
                                {subtitle}
                            </SubtitleTypography>
                        )}
                    </ChartCardHeaderContainer>
                    <FlexContainer>
                        {children}
                    </FlexContainer>
                </CardContent>
            </GlassCard>
        </Slide>
    );
};

const StatusChips = styled(Box)({
    display: 'flex',
    flexWrap: 'wrap',
    gap: '12px',
    marginTop: '24px',
    justifyContent: 'center'
});

const DefectIndicator = styled(Box)({
    width: 8,
    height: 8,
    borderRadius: '50%'
});

interface PerformanceRadialChartProps {
    data: { name: string; value: number; color: string }[];
}

const PerformanceRadialChart: React.FC<PerformanceRadialChartProps> = ({ data }) => {
    const total = data.reduce((sum, item) => sum + item.value, 0);

    const IconButtonStyled = styled(IconButton)({
        background: 'rgba(0, 102, 255, 0.1)',
        '&:hover': { background: 'rgba(0, 102, 255, 0.2)' }
    });

    const ColoredDefectIndicator = styled(DefectIndicator)<{ color: string }>(({ color }) => ({
        background: color,
        boxShadow: `0 0 8px ${color}`
    }));

    const ChipBox = styled(Box)({
        display: 'flex',
        alignItems: 'center',
        gap: 4
    });

    return (
        <InteractiveChartCard
            title="Распределение дефектов"
            subtitle="В реальном времени"
            icon={<BubbleChart sx={{ color: MIREA_2025_COLORS.accentPurple, fontSize: 28 }} />}
            action={
                <Tooltip title="Показать детали">
                    <IconButtonStyled>
                        <MoreVert sx={{ color: MIREA_2025_COLORS.textSecondary }} />
                    </IconButtonStyled>
                </Tooltip>
            }
        >
            <ResponsiveContainer width="100%" height={250}>
                <RadialBarChart
                    innerRadius="20%"
                    outerRadius="90%"
                    data={data}
                    startAngle={180}
                    endAngle={-180}
                >
                    <PolarGrid
                        stroke={MIREA_2025_COLORS.glassSurface}
                        strokeWidth={1}
                    />
                    <PolarAngleAxis
                        type="number"
                        domain={[0, 100]}
                        tick={false}
                    />
                    <RadialBar
                        dataKey="value"
                        background={{ fill: 'rgba(255, 255, 255, 0.05)' }}
                        cornerRadius={8}
                    >
                        {data.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={entry.color} />
                        ))}
                    </RadialBar>
                </RadialBarChart>
            </ResponsiveContainer>

            <StatusChips>
                {data.map((item, index) => (
                    <Chip
                        key={index}
                        label={
                            <ChipBox>
                                <ColoredDefectIndicator color={item.color} />
                                <Typography variant="caption" sx={{ fontWeight: 600 }}>
                                    {item.name}: {item.value} ({((item.value / total) * 100).toFixed(1)}%)
                                </Typography>
                            </ChipBox>
                        }
                        sx={{
                            bgcolor: 'rgba(255, 255, 255, 0.05)',
                            border: `1px solid ${item.color}40`,
                            backdropFilter: 'blur(10px)',
                            '& .MuiChip-label': { px: 1.5, py: 1 }
                        }}
                    />
                ))}
            </StatusChips>
        </InteractiveChartCard>
    );
};

interface PerformanceTrendChartProps {
    data: { time: string; quality: number; defects: number }[];
}

const PerformanceTrendChart: React.FC<PerformanceTrendChartProps> = ({ data }) => {
    const [isPlaying, setIsPlaying] = useState(true);

    const IconButtonStyled = styled(IconButton)({
        background: 'rgba(0, 102, 255, 0.1)',
        '&:hover': { background: 'rgba(0, 102, 255, 0.2)' }
    });

    return (
        <InteractiveChartCard
            title="Динамика производства"
            subtitle="Качество vs Дефекты"
            icon={<Timeline sx={{ color: MIREA_2025_COLORS.primary, fontSize: 28 }} />}
            action={
                <Tooltip title={isPlaying ? 'Пауза' : 'Воспроизвести'}>
                    <IconButtonStyled
                        onClick={() => setIsPlaying(!isPlaying)}
                    >
                        {isPlaying ?
                            <PauseCircle sx={{ color: MIREA_2025_COLORS.primary }} /> :
                            <PlayCircle sx={{ color: MIREA_2025_COLORS.primary }} />
                        }
                    </IconButtonStyled>
                </Tooltip>
            }
        >
            <ResponsiveContainer width="100%" height={250}>
                <AreaChart data={data}>
                    <defs>
                        <linearGradient id="colorQuality" x1="0" y1="0" x2="0" y2="1">
                            <stop offset="5%" stopColor={MIREA_2025_COLORS.tertiary} stopOpacity={0.8} />
                            <stop offset="95%" stopColor={MIREA_2025_COLORS.tertiary} stopOpacity={0.1} />
                        </linearGradient>
                        <linearGradient id="colorDefects" x1="0" y1="0" x2="0" y2="1">
                            <stop offset="5%" stopColor={MIREA_2025_COLORS.error} stopOpacity={0.8} />
                            <stop offset="95%" stopColor={MIREA_2025_COLORS.error} stopOpacity={0.1} />
                        </linearGradient>
                    </defs>
                    <CartesianGrid
                        strokeDasharray="3 3"
                        stroke={MIREA_2025_COLORS.glassSurface}
                        strokeWidth={0.5}
                    />
                    <XAxis
                        dataKey="time"
                        stroke={MIREA_2025_COLORS.textTertiary}
                        fontSize={12}
                        tickLine={false}
                        axisLine={{ stroke: MIREA_2025_COLORS.glassSurface }}
                    />
                    <YAxis
                        stroke={MIREA_2025_COLORS.textTertiary}
                        fontSize={12}
                        tickLine={false}
                        axisLine={{ stroke: MIREA_2025_COLORS.glassSurface }}
                    />
                    <RechartsTooltip
                        contentStyle={{
                            backgroundColor: MIREA_2025_COLORS.darkSurface,
                            border: `1px solid ${MIREA_2025_COLORS.glassSurface}`,
                            borderRadius: '12px',
                            backdropFilter: 'blur(20px)',
                            color: MIREA_2025_COLORS.textPrimary,
                            boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
                        }}
                        formatter={(value: number) => [value.toFixed(1), 'Значение']}
                    />
                    <Area
                        type="monotone"
                        dataKey="quality"
                        stroke={MIREA_2025_COLORS.tertiary}
                        fill="url(#colorQuality)"
                        strokeWidth={3}
                        dot={{ stroke: MIREA_2025_COLORS.tertiary, strokeWidth: 2, r: 4 }}
                        activeDot={{ r: 6, strokeWidth: 0 }}
                        name="Качество (%)"
                        animationDuration={isPlaying ? 1500 : 0}
                    />
                    <Area
                        type="monotone"
                        dataKey="defects"
                        stroke={MIREA_2025_COLORS.error}
                        fill="url(#colorDefects)"
                        strokeWidth={3}
                        dot={{ stroke: MIREA_2025_COLORS.error, strokeWidth: 2, r: 4 }}
                        activeDot={{ r: 6, strokeWidth: 0 }}
                        name="Дефекты (шт)"
                        animationDuration={isPlaying ? 1500 : 0}
                    />
                </AreaChart>
            </ResponsiveContainer>
        </InteractiveChartCard>
    );
};

interface RealTimeMetricsPanelProps {
    metrics: {
        name: string;
        value: number;
        unit: string;
        max: number;
        icon: React.ReactElement;
        trend?: 'up' | 'down' | 'stable';
    }[];
}

const RealTimeMetricsPanel: React.FC<RealTimeMetricsPanelProps> = ({ metrics }) => {
    const [currentMetric, setCurrentMetric] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentMetric((prev) => (prev + 1) % metrics.length);
        }, 5000);

        return () => clearInterval(interval);
    }, [metrics.length]);

    const HeaderContainer = styled(Box)({
        display: 'flex',
        alignItems: 'center',
        gap: 16,
        marginBottom: 24
    });

    const MetricsIcon = styled(Box)({
        width: 56,
        height: 56,
        borderRadius: '16px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: MIREA_2025_COLORS.primaryGradient,
        boxShadow: `0 8px 32px ${MIREA_2025_COLORS.primary}40`,
    });

    const IndicatorContainer = styled(Box)({
        display: 'flex',
        justifyContent: 'center',
        gap: 8,
        marginBottom: 24
    });

    const IndicatorDot = styled(Box)({
        width: 8,
        height: 8,
        borderRadius: '50%',
        cursor: 'pointer',
        transition: 'all 0.3s'
    });

    const MetricsList = styled(Box)({
        display: 'flex',
        flexDirection: 'column',
        gap: 16
    });

    const MetricRow = styled(Box)({
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 8
    });

    const MetricIconRow = styled(Box)({
        display: 'flex',
        alignItems: 'center',
        gap: 8
    });

    return (
        <Slide in timeout={1200} direction="right">
            <GlassCard style={{ height: '100%' }}>
                <CardContent style={{ padding: '28px', height: '100%' }}>
                    <HeaderContainer>
                        <MetricsIcon>
                            <Speed sx={{ fontSize: 28, color: '#FFFFFF' }} />
                        </MetricsIcon>
                        <Box>
                            <Typography variant="h6" sx={{
                                fontWeight: 700,
                                color: MIREA_2025_COLORS.textPrimary,
                                mb: 0.5
                            }}>
                                Метрики в реальном времени
                            </Typography>
                            <Typography variant="body2" sx={{ color: MIREA_2025_COLORS.textTertiary }}>
                                Автоматическое обновление
                            </Typography>
                        </Box>
                    </HeaderContainer>

                    <Box sx={{ marginBottom: 3 }}>
                        {metrics.map((metric, index) => (
                            <Fade in={index === currentMetric} key={index} timeout={500}>
                                <Box sx={{ textAlign: 'center' }}>
                                    <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 2, mb: 2 }}>
                                        {metric.icon}
                                        <Typography variant="h3" sx={{
                                            fontWeight: 800,
                                            background: MIREA_2025_COLORS.infoGradient,
                                            WebkitBackgroundClip: 'text',
                                            WebkitTextFillColor: 'transparent',
                                        }}>
                                            {metric.value} {metric.unit}
                                        </Typography>
                                    </Box>
                                    <Typography variant="h6" sx={{
                                        color: MIREA_2025_COLORS.textPrimary,
                                        mb: 1
                                    }}>
                                        {metric.name}
                                    </Typography>
                                </Box>
                            </Fade>
                        ))}
                    </Box>

                    <IndicatorContainer>
                        {metrics.map((_, index) => (
                            <IndicatorDot
                                key={index}
                                onClick={() => setCurrentMetric(index)}
                                sx={{
                                    background: index === currentMetric ? MIREA_2025_COLORS.primary : MIREA_2025_COLORS.glassSurface,
                                    '&:hover': {
                                        background: MIREA_2025_COLORS.primary,
                                        transform: 'scale(1.2)',
                                    },
                                }}
                            />
                        ))}
                    </IndicatorContainer>

                    <MetricsList>
                        {metrics.map((metric, index) => {
                            const percentage = (metric.value / metric.max) * 100;
                            const isWarning = percentage > 80;
                            const isCritical = percentage > 95;

                            const LinearProgressStyled = styled(LinearProgress)({
                                height: 6,
                                borderRadius: 3,
                                background: 'rgba(255, 255, 255, 0.1)',
                                '& .MuiLinearProgress-bar': {
                                    borderRadius: 3,
                                },
                            });

                            const barColor = isCritical
                                ? MIREA_2025_COLORS.errorGradient
                                : isWarning
                                    ? MIREA_2025_COLORS.warningGradient
                                    : MIREA_2025_COLORS.tertiaryGradient;

                            const ColoredLinearProgress = styled(LinearProgressStyled)({
                                '& .MuiLinearProgress-bar': {
                                    background: barColor,
                                },
                            });

                            return (
                                <Box key={index} sx={{ opacity: index === currentMetric ? 1 : 0.6 }}>
                                    <MetricRow>
                                        <MetricIconRow>
                                            {metric.icon}
                                            <Typography variant="body2" sx={{ color: MIREA_2025_COLORS.textSecondary }}>
                                                {metric.name}
                                            </Typography>
                                        </MetricIconRow>
                                        <Typography variant="body2" sx={{
                                            fontWeight: 700,
                                            color: isCritical ? MIREA_2025_COLORS.error : isWarning ? MIREA_2025_COLORS.warning : MIREA_2025_COLORS.tertiary
                                        }}>
                                            {metric.value} {metric.unit}
                                        </Typography>
                                    </MetricRow>
                                    <ColoredLinearProgress
                                        variant="determinate"
                                        value={percentage}
                                    />
                                </Box>
                            );
                        })}
                    </MetricsList>
                </CardContent>
            </GlassCard>
        </Slide>
    );
};

interface AIRecommendation {
