                <GridContainer container spacing={3}>
                    <Grid item xs={12} sm={6} md={3}>
                        <AnimatedKPICard
                            title="Уровень качества"
                            value={kpiData.qualityRate}
                            unit="%"
                            icon={<CheckCircle />}
                            color={MIREA_2025_COLORS.tertiaryGradient}
                            trend={{ value: 2.3, direction: 'up' }}
                            delay={100}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6} md={3}>
                        <AnimatedKPICard
                            title="Обнаружено дефектов"
                            value={kpiData.defectCount}
                            icon={<Warning />}
                            color={MIREA_2025_COLORS.errorGradient}
                            trend={{ value: 5.1, direction: 'down' }}
                            delay={200}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6} md={3}>
                        <AnimatedKPICard
                            title="Произведено единиц"
                            value={kpiData.unitsProduced}
                            icon={<Factory />}
                            color={MIREA_2025_COLORS.infoGradient}
                            trend={{ value: 8.7, direction: 'up' }}
                            delay={300}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6} md={3}>
                        <AnimatedKPICard
                            title="Время работы"
                            value={kpiData.uptime}
                            unit="%"
                            icon={<DeviceHub />}
                            color={MIREA_2025_COLORS.primaryGradient}
                            trend={{ value: 1.2, direction: 'up' }}
                            delay={400}
                        />
                    </Grid>
                </GridContainer>

                <GridContainer container spacing={3}>
                    <Grid item xs={12} md={7}>
                        <PerformanceTrendChart data={performanceData} />
                    </Grid>
                    <Grid item xs={12} md={5}>
                        <PerformanceRadialChart data={defectDistribution} />
                    </Grid>
                </GridContainer>

                <GridContainer container spacing={3}>
                    <Grid item xs={12} md={4}>
                        <RealTimeMetricsPanel metrics={realTimeMetrics} />
                    </Grid>
                    <Grid item xs={12} md={8}>
                        <AIInsightsPanel recommendations={aiRecommendations} />
                    </Grid>
                </GridContainer>

                {/* Дополнительный контент */}
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                        <GlassCard>
                            <CardContent sx={{ p: 3 }}>
                                <StatusCardHeader>
                                    <Typography variant="h6" sx={{
                                        fontWeight: 700,
                                        background: 'linear-gradient(135deg, #FF3366, #FF9E6D)',
                                        WebkitBackgroundClip: 'text',
                                        WebkitTextFillColor: 'transparent',
                                    }}>
                                        Система в реальном времени подключена
                                    </Typography>
                                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                        <StatusIndicator />
                                        <Typography variant="body2" sx={{ color: MIREA_2025_COLORS.textTertiary }}>
                                            Активно
                                        </Typography>
                                    </Box>
                                </StatusCardHeader>
                                <Typography variant="body1" sx={{
                                    color: MIREA_2025_COLORS.textSecondary,
                                    textAlign: 'center',
                                    lineHeight: 1.8
                                }}>
                                    Система искусственного интеллекта непрерывно анализирует производственные данные для
                                    максимальной эффективности и качества продукции. Все системы функционируют в штатном режиме.
                                </Typography>
                            </CardContent>
                        </GlassCard>
                    </Grid>
                </Grid>
            </ContentContainer>
        </DashboardContainer>
    );
};

export default AdvancedDashboard;
