              </Stack>
              
              <Divider sx={{ my: 2, borderColor: 'rgba(255, 255, 255, 0.1)' }} />
              
              <Typography variant="body2" sx={{ color: MIREA_COLORS.text, mb: 2, fontWeight: 700 }}>
                üìä –ü—Ä–æ–≥–Ω–æ–∑ –¥–µ—Ñ–µ–∫—Ç–æ–≤:
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1.5 }}>
                <Card sx={{ bgcolor: 'rgba(255, 23, 68, 0.1)', border: '1px solid', borderColor: MIREA_COLORS.error }}>
                  <CardContent sx={{ py: 1.5 }}>
                    <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>–¢—Ä–µ—â–∏–Ω—ã</Typography>
                    <Typography variant="h6" sx={{ color: MIREA_COLORS.error, fontWeight: 700 }}>
                      {whatIfDefects.crack} –µ–¥/—á–∞—Å
                    </Typography>
                  </CardContent>
                </Card>
                <Card sx={{ bgcolor: 'rgba(255, 167, 38, 0.1)', border: '1px solid', borderColor: MIREA_COLORS.warning }}>
                  <CardContent sx={{ py: 1.5 }}>
                    <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>–ü—É–∑—ã—Ä–∏</Typography>
                    <Typography variant="h6" sx={{ color: MIREA_COLORS.warning, fontWeight: 700 }}>
                      {whatIfDefects.bubble} –µ–¥/—á–∞—Å
                    </Typography>
                  </CardContent>
                </Card>
                <Card sx={{ bgcolor: 'rgba(0, 230, 118, 0.1)', border: '1px solid', borderColor: MIREA_COLORS.success }}>
                  <CardContent sx={{ py: 1.5 }}>
                    <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>–°–∫–æ–ª—ã</Typography>
                    <Typography variant="h6" sx={{ color: MIREA_COLORS.success, fontWeight: 700 }}>
                      {whatIfDefects.chip} –µ–¥/—á–∞—Å
                    </Typography>
                  </CardContent>
                </Card>
              </Box>
              
              <Box sx={{ mt: 3, p: 2, bgcolor: 'rgba(0, 229, 255, 0.1)', borderRadius: 1 }}>
                <Typography variant="caption" sx={{ color: MIREA_COLORS.info, fontWeight: 700 }}>
                  üí° –†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏—è:
                </Typography>
                <Typography variant="caption" sx={{ color: MIREA_COLORS.text, display: 'block', mt: 0.5 }}>
                  {whatIfDefects.crack > 15 || whatIfDefects.bubble > 12 ? 
                    '‚ö†Ô∏è –ü–∞—Ä–∞–º–µ—Ç—Ä—ã –≤—ã—Ö–æ–¥—è—Ç –∑–∞ –æ–ø—Ç–∏–º–∞–ª—å–Ω—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è. –í—ã—Å–æ–∫–∏–π —Ä–∏—Å–∫ –¥–µ—Ñ–µ–∫—Ç–æ–≤!' : 
                    '‚úÖ –ü–∞—Ä–∞–º–µ—Ç—Ä—ã –≤ –Ω–æ—Ä–º–µ. –ö–∞—á–µ—Å—Ç–≤–æ –ø—Ä–æ–¥—É–∫—Ü–∏–∏ —Å—Ç–∞–±–∏–ª—å–Ω–æ.'
                  }
                </Typography>
              </Box>
            </Paper>
          </Grid>
        )}
      </Grid>
    </Box>
  );
};

export default DigitalTwin3D;
