import React, { useState, useEffect, useMemo } from 'react';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  ScatterChart,
  Scatter,
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Cell,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  ComposedChart,
  PieChart,
  Pie
} from 'recharts';
import {
  TrendingUp,
  TrendingDown,
  Activity,
  Gauge,
  AlertTriangle,
  CheckCircle,
  TrendingUp as ChartIcon,
  RefreshCw,
  Download,
  Sparkles,
  GitBranch,
  BarChart2
} from 'lucide-react';

const API_BASE_URL = 'http://localhost:8000';

interface DefectTrendData {
  timestamp: string;
  total_defects: number;
  crack: number;
  bubble: number;
  chip: number;
  stain: number;
  cloudiness: number;
  deformation: number;
}

interface EfficiencyData {
  timestamp: string;
  production_rate: number;
  quality_rate: number;
  efficiency_score: number;
}

interface CorrelationData {
  [parameter: string]: {
    [defect: string]: number;
  };
}

const AnalyticsDashboard: React.FC = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [timeRange, setTimeRange] = useState('24h');
  const [grouping, setGrouping] = useState('hourly');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [autoRefresh, setAutoRefresh] = useState(true);

  const [defectTrends, setDefectTrends] = useState<DefectTrendData[]>([]);
  const [efficiencyData, setEfficiencyData] = useState<EfficiencyData[]>([]);
  const [correlations, setCorrelations] = useState<CorrelationData>({});

  const fetchDefectTrends = async () => {
    try {
      const response = await fetch(
        `${API_BASE_URL}/api/analytics/defect-trends?timerange=${timeRange}&grouping=${grouping}`
      );
      if (!response.ok) throw new Error('Failed to fetch defect trends');
      const data = await response.json();
      setDefectTrends(data.data_points || []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    }
  };

  const fetchEfficiencyData = async () => {
    try {
      const response = await fetch(
        `${API_BASE_URL}/api/analytics/production-efficiency?timerange=${timeRange}`
      );
      if (!response.ok) throw new Error('Failed to fetch efficiency data');
      const data = await response.json();
      setEfficiencyData(data.data_points || []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    }
  };

  const fetchCorrelations = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/analytics/parameter-correlations`);
      if (!response.ok) throw new Error('Failed to fetch correlations');
      const data = await response.json();
      setCorrelations(data.correlations || {});
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
    }
  };

  const fetchAllData = async () => {
    setLoading(true);
    setError(null);
    await Promise.all([fetchDefectTrends(), fetchEfficiencyData(), fetchCorrelations()]);
    setLoading(false);
  };

  useEffect(() => {
    fetchAllData();
  }, [timeRange, grouping]);

  useEffect(() => {
    if (!autoRefresh) return;
    const interval = setInterval(fetchAllData, 30000);
    return () => clearInterval(interval);
  }, [autoRefresh, timeRange, grouping]);

  const metrics = useMemo(() => {
    if (defectTrends.length === 0) return null;

    const latest = defectTrends[defectTrends.length - 1];
    const previous = defectTrends[defectTrends.length - 2] || latest;

    const totalDefects = latest.total_defects;
    const defectChange = totalDefects - previous.total_defects;
    const defectTrend = defectChange > 0 ? 'up' : defectChange < 0 ? 'down' : 'neutral';

    const avgEfficiency = efficiencyData.length > 0
      ? efficiencyData.reduce((sum, d) => sum + d.efficiency_score, 0) / efficiencyData.length
      : 0;

    const avgQuality = efficiencyData.length > 0
      ? efficiencyData.reduce((sum, d) => sum + d.quality_rate, 0) / efficiencyData.length
      : 0;

    const avgProduction = efficiencyData.length > 0
      ? efficiencyData.reduce((sum, d) => sum + d.production_rate, 0) / efficiencyData.length
      : 0;

    return {
      totalDefects,
      defectChange,
      defectTrend,
      avgEfficiency,
      avgQuality,
      avgProduction,
      criticalDefects: latest.crack + latest.deformation,
      minorDefects: latest.bubble + latest.chip + latest.stain + latest.cloudiness,
    };
  }, [defectTrends, efficiencyData]);

  const defectDistribution = useMemo(() => {
    if (defectTrends.length === 0) return [];
    const latest = defectTrends[defectTrends.length - 1];
    return [
      { name: 'Трещины', value: latest.crack, color: '#FF1744' },
      { name: 'Пузыри', value: latest.bubble, color: '#FFD700' },
      { name: 'Сколы', value: latest.chip, color: '#0066FF' },
      { name: 'Пятна', value: latest.stain, color: '#00E676' },
      { name: 'Помутнения', value: latest.cloudiness, color: '#00E5FF' },
      { name: 'Деформации', value: latest.deformation, color: '#FF3366' },
    ];
  }, [defectTrends]);

  const getCorrelationColor = (value: number) => {
    if (value > 0.6) return '#FF1744';
    if (value > 0.3) return '#FFD700';
    if (value > 0) return '#0066FF';
    if (value > -0.3) return '#00E676';
    return '#00E5FF';
  };

  const tabs = [
    { id: 0, label: 'Тренды дефектов', icon: ChartIcon },
    { id: 1, label: 'Эффективность', icon: Gauge },
    { id: 2, label: 'Корреляции', icon: GitBranch },
    { id: 3, label: 'Распределение', icon: BarChart2 },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-blue-950 to-slate-900 p-6">
      {/* Header */}
      <div className="relative mb-8 p-8 rounded-3xl bg-gradient-to-r from-blue-600/20 via-cyan-500/20 to-blue-600/20 border-2 border-cyan-500/30 backdrop-blur-xl shadow-2xl shadow-cyan-500/20 overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-r from-transparent via-cyan-400/10 to-transparent animate-pulse"></div>
        <div className="relative z-10 flex items-center justify-between">
          <div className="flex items-center space-x-6">
            <div className="w-20 h-20 rounded-2xl bg-gradient-to-br from-blue-500 to-cyan-400 flex items-center justify-center shadow-lg shadow-cyan-500/50">
              <Activity className="w-10 h-10 text-white" />
            </div>
            <div>
              <h1 className="text-4xl font-black text-white mb-2 tracking-tight">
                Расширенная аналитика
              </h1>
              <p className="text-lg text-cyan-200 font-semibold">
                Интеллектуальный анализ производственных данных
              </p>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            <select
              value={timeRange}
              onChange={(e) => setTimeRange(e.target.value)}
              className="px-4 py-3 rounded-xl bg-slate-800/50 border-2 border-cyan-500/30 text-white font-bold focus:border-cyan-400 focus:ring-2 focus:ring-cyan-400/50 transition-all backdrop-blur-sm"
            >
              <option value="24h">24 часа</option>
              <option value="7d">7 дней</option>
              <option value="30d">30 дней</option>
            </select>

            <select
              value={grouping}
              onChange={(e) => setGrouping(e.target.value)}
              className="px-4 py-3 rounded-xl bg-slate-800/50 border-2 border-cyan-500/30 text-white font-bold focus:border-cyan-400 focus:ring-2 focus:ring-cyan-400/50 transition-all backdrop-blur-sm"
            >
              <option value="hourly">По часам</option>
              <option value="daily">По дням</option>
              <option value="weekly">По неделям</option>
            </select>

            <button
              onClick={() => setAutoRefresh(!autoRefresh)}
              className={`p-3 rounded-xl font-bold transition-all ${
                autoRefresh
                  ? 'bg-gradient-to-r from-green-500 to-emerald-400 text-white shadow-lg shadow-green-500/50'
                  : 'bg-slate-700/50 text-slate-400 border-2 border-slate-600'
              }`}
            >
              <RefreshCw className={`w-5 h-5 ${autoRefresh ? 'animate-spin' : ''}`} />
            </button>

            <button
              onClick={fetchAllData}
              disabled={loading}
              className="p-3 rounded-xl bg-gradient-to-r from-blue-500 to-cyan-400 text-white font-bold hover:scale-110 transition-all shadow-lg shadow-blue-500/50"
            >
              <Download className="w-5 h-5" />
            </button>
          </div>
        </div>
      </div>

      {error && (
        <div className="mb-6 p-4 rounded-2xl bg-red-500/20 border-2 border-red-500/50 text-red-200 font-semibold backdrop-blur-sm">
          {error}
          <button onClick={() => setError(null)} className="ml-4 text-red-300 hover:text-white">×</button>
        </div>
      )}

      {loading && (
        <div className="mb-6 h-2 rounded-full bg-blue-900/50 overflow-hidden">
          <div className="h-full bg-gradient-to-r from-blue-500 via-cyan-400 to-blue-500 animate-pulse"></div>
        </div>
      )}

      {/* KPI Metrics */}
      {metrics && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {/* Total Defects */}
          <div className="group relative p-6 rounded-3xl bg-gradient-to-br from-slate-800/90 to-slate-900/90 border-2 border-yellow-500/30 backdrop-blur-xl transition-all hover:scale-105 hover:border-yellow-400/60 hover:shadow-2xl hover:shadow-yellow-500/30">
            <div className="absolute inset-0 bg-gradient-to-r from-transparent via-yellow-400/10 to-transparent opacity-0 group-hover:opacity-100 transition-opacity rounded-3xl"></div>
            <div className="relative">
              <div className="flex items-center justify-between mb-4">
                <span className="text-sm font-bold text-cyan-300 uppercase tracking-wider">Всего дефектов</span>
                <AlertTriangle className="w-7 h-7 text-yellow-400" />
              </div>
              <div className="text-5xl font-black text-white mb-3 bg-gradient-to-r from-white to-cyan-200 bg-clip-text text-transparent">
                {metrics.totalDefects}
              </div>
              <div className="flex items-center space-x-2 mb-4">
                {metrics.defectTrend === 'up' && <TrendingUp className="w-5 h-5 text-red-400" />}
