              <div className="text-5xl font-black text-white mb-3 bg-gradient-to-r from-white to-cyan-200 bg-clip-text text-transparent">
                {metrics.avgEfficiency.toFixed(1)}%
              </div>
              <div className="mb-4">
                <span className={`inline-block px-4 py-2 rounded-xl font-bold text-sm ${
                  metrics.avgEfficiency > 80 
                    ? 'bg-green-500/30 text-green-300 border-2 border-green-400/50 shadow-lg shadow-green-500/30'
                    : metrics.avgEfficiency > 60
                    ? 'bg-cyan-500/30 text-cyan-300 border-2 border-cyan-400/50 shadow-lg shadow-cyan-500/30'
                    : 'bg-yellow-500/30 text-yellow-300 border-2 border-yellow-400/50 shadow-lg shadow-yellow-500/30'
                }`}>
                  {metrics.avgEfficiency > 80 ? "ОТЛИЧНО" : metrics.avgEfficiency > 60 ? "ХОРОШО" : "ТРЕБУЕТ ВНИМАНИЯ"}
                </span>
              </div>
              <div className="h-2 rounded-full bg-cyan-900/30 overflow-hidden">
                <div 
                  className="h-full bg-gradient-to-r from-cyan-500 to-cyan-400 shadow-lg shadow-cyan-500/50"
                  style={{ width: `${metrics.avgEfficiency}%` }}
                ></div>
              </div>
            </div>
          </div>

          {/* Quality */}
          <div className="group relative p-6 rounded-3xl bg-gradient-to-br from-slate-800/90 to-slate-900/90 border-2 border-green-500/30 backdrop-blur-xl transition-all hover:scale-105 hover:border-green-400/60 hover:shadow-2xl hover:shadow-green-500/30">
            <div className="absolute inset-0 bg-gradient-to-r from-transparent via-green-400/10 to-transparent opacity-0 group-hover:opacity-100 transition-opacity rounded-3xl"></div>
            <div className="relative">
              <div className="flex items-center justify-between mb-4">
                <span className="text-sm font-bold text-cyan-300 uppercase tracking-wider">Качество</span>
                <CheckCircle className="w-7 h-7 text-green-400" />
              </div>
              <div className="text-5xl font-black text-white mb-3 bg-gradient-to-r from-white to-green-200 bg-clip-text text-transparent">
                {metrics.avgQuality.toFixed(1)}%
              </div>
              <div className="mb-4">
                <span className="inline-block px-4 py-2 rounded-xl font-bold text-sm bg-green-500/30 text-green-300 border-2 border-green-400/50 shadow-lg shadow-green-500/30">
                  {metrics.avgQuality.toFixed(0)}% без дефектов
                </span>
              </div>
              <div className="h-2 rounded-full bg-green-900/30 overflow-hidden">
                <div 
                  className="h-full bg-gradient-to-r from-green-500 to-green-400 shadow-lg shadow-green-500/50"
                  style={{ width: `${metrics.avgQuality}%` }}
                ></div>
              </div>
            </div>
          </div>

          {/* Production */}
          <div className="group relative p-6 rounded-3xl bg-gradient-to-br from-slate-800/90 to-slate-900/90 border-2 border-blue-500/30 backdrop-blur-xl transition-all hover:scale-105 hover:border-blue-400/60 hover:shadow-2xl hover:shadow-blue-500/30">
            <div className="absolute inset-0 bg-gradient-to-r from-transparent via-blue-400/10 to-transparent opacity-0 group-hover:opacity-100 transition-opacity rounded-3xl"></div>
            <div className="relative">
              <div className="flex items-center justify-between mb-4">
                <span className="text-sm font-bold text-cyan-300 uppercase tracking-wider">Производительность</span>
                <Sparkles className="w-7 h-7 text-blue-400" />
              </div>
              <div className="text-5xl font-black text-white mb-3 bg-gradient-to-r from-white to-blue-200 bg-clip-text text-transparent">
                {metrics.avgProduction.toFixed(0)}
              </div>
              <div className="mb-4">
                <span className="text-cyan-300 font-bold">единиц/час</span>
              </div>
              <div className="h-2 rounded-full bg-blue-900/30 overflow-hidden">
                <div 
                  className="h-full bg-gradient-to-r from-blue-500 to-blue-400 shadow-lg shadow-blue-500/50"
                  style={{ width: `${Math.min((metrics.avgProduction / 500) * 100, 100)}%` }}
                ></div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Tabs */}
      <div className="mb-6 p-2 rounded-2xl bg-slate-800/50 border-2 border-cyan-500/30 backdrop-blur-xl">
        <div className="flex space-x-2">
          {tabs.map((tab) => {
            const Icon = tab.icon;
            return (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex-1 flex items-center justify-center space-x-2 px-6 py-4 rounded-xl font-bold transition-all ${
                  activeTab === tab.id
                    ? 'bg-gradient-to-r from-blue-500 to-cyan-400 text-white shadow-lg shadow-cyan-500/50 scale-105'
                    : 'text-cyan-300 hover:bg-slate-700/50'
                }`}
              >
                <Icon className="w-5 h-5" />
                <span>{tab.label}</span>
              </button>
            );
          })}
        </div>
      </div>

      {/* Tab Content */}
      {!loading && (
        <>
          {/* Tab 0: Defect Trends */}
          {activeTab === 0 && (
            <div className="space-y-6">
              <div className="p-8 rounded-3xl bg-slate-800/50 border-2 border-cyan-500/30 backdrop-blur-xl">
                <div className="flex items-center justify-between mb-6">
                  <div>
                    <h3 className="text-2xl font-black text-white mb-2">Динамика дефектов по времени</h3>
                    <p className="text-cyan-300 font-semibold">Мониторинг всех типов дефектов в реальном времени</p>
                  </div>
                  <span className="px-4 py-2 rounded-xl bg-cyan-500/30 text-cyan-300 border-2 border-cyan-400/50 font-bold shadow-lg shadow-cyan-500/30">
                    {defectTrends.length} точек данных
                  </span>
                </div>
                <ResponsiveContainer width="100%" height={450}>
                  <AreaChart data={defectTrends}>
                    <defs>
                      <linearGradient id="crack" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#FF1744" stopOpacity={0.8}/>
                        <stop offset="95%" stopColor="#FF1744" stopOpacity={0.1}/>
                      </linearGradient>
                      <linearGradient id="bubble" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#FFD700" stopOpacity={0.8}/>
                        <stop offset="95%" stopColor="#FFD700" stopOpacity={0.1}/>
                      </linearGradient>
                      <linearGradient id="chip" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#0066FF" stopOpacity={0.8}/>
                        <stop offset="95%" stopColor="#0066FF" stopOpacity={0.1}/>
                      </linearGradient>
                    </defs>
                    <CartesianGrid strokeDasharray="3 3" stroke="rgba(0, 229, 255, 0.1)" />
                    <XAxis
                      dataKey="timestamp"
                      tickFormatter={(val) => new Date(val).toLocaleTimeString('ru-RU', { hour: '2-digit', minute: '2-digit' })}
                      stroke="#B8C5D6"
                      style={{ fontSize: '12px', fontWeight: 600 }}
                    />
                    <YAxis stroke="#B8C5D6" style={{ fontSize: '12px', fontWeight: 600 }} />
                    <Tooltip
                      contentStyle={{
                        background: 'rgba(13, 27, 42, 0.95)',
                        border: '2px solid rgba(0, 229, 255, 0.5)',
                        borderRadius: '12px',
                        backdropFilter: 'blur(20px)',
                      }}
                      labelFormatter={(val) => new Date(val).toLocaleString('ru-RU')}
                    />
                    <Legend wrapperStyle={{ fontWeight: 700 }} />
                    <Area type="monotone" dataKey="crack" stroke="#FF1744" strokeWidth={3} fill="url(#crack)" name="Трещины" />
                    <Area type="monotone" dataKey="bubble" stroke="#FFD700" strokeWidth={3} fill="url(#bubble)" name="Пузыри" />
                    <Area type="monotone" dataKey="chip" stroke="#0066FF" strokeWidth={3} fill="url(#chip)" name="Сколы" />
                  </AreaChart>
                </ResponsiveContainer>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="p-8 rounded-3xl bg-slate-800/50 border-2 border-cyan-500/30 backdrop-blur-xl">
                  <h3 className="text-xl font-black text-white mb-6">Суммарное количество дефектов</h3>
                  <ResponsiveContainer width="100%" height={350}>
                    <BarChart data={defectTrends.slice(-20)}>
                      <defs>
                        <linearGradient id="barGradient" x1="0" y1="0" x2="0" y2="1">
                          <stop offset="0%" stopColor="#0066FF" />
                          <stop offset="100%" stopColor="#00E5FF" />
                        </linearGradient>
                      </defs>
                      <CartesianGrid strokeDasharray="3 3" stroke="rgba(0, 229, 255, 0.1)" />
                      <XAxis
                        dataKey="timestamp"
                        tickFormatter={(val) => new Date(val).toLocaleTimeString('ru-RU', { hour: '2-digit' })}
                        stroke="#B8C5D6"
                      />
                      <YAxis stroke="#B8C5D6" />
                      <Tooltip
                        contentStyle={{
                          background: 'rgba(13, 27, 42, 0.95)',
                          border: '2px solid rgba(0, 229, 255, 0.5)',
                          borderRadius: '12px',
                        }}
                      />
                      <Bar dataKey="total_defects" fill="url(#barGradient)" radius={[8, 8, 0, 0]} name="Всего дефектов" />
                    </BarChart>
                  </ResponsiveContainer>
                </div>

                <div className="p-8 rounded-3xl bg-slate-800/50 border-2 border-cyan-500/30 backdrop-blur-xl">
                  <h3 className="text-xl font-black text-white mb-6">Критичность дефектов</h3>
                  <ResponsiveContainer width="100%" height={350}>
                    <PieChart>
                      <Pie
                        data={[
                          { name: 'Критические', value: metrics?.criticalDefects || 0, color: '#FF1744' },
                          { name: 'Незначительные', value: metrics?.minorDefects || 0, color: '#FFD700' },
                        ]}
                        cx="50%"
                        cy="50%"
                        labelLine={false}
                        label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                        outerRadius={120}
                        dataKey="value"
                      >
                        <Cell fill="#FF1744" />
                        <Cell fill="#FFD700" />
                      </Pie>
                      <Tooltip />
                    </PieChart>
                  </ResponsiveContainer>
                </div>
              </div>
            </div>
          )}

          {/* Tab 1: Efficiency */}
          {activeTab === 1 && (
            <div className="space-y-6">
              <div className="p-8 rounded-3xl bg-slate-800/50 border-2 border-cyan-500/30 backdrop-blur-xl">
                <h3 className="text-2xl font-black text-white mb-2">Производительность vs Качество</h3>
                <p className="text-cyan-300 font-semibold mb-6">Корреляционный анализ показателей производства</p>
                <ResponsiveContainer width="100%" height={450}>
