// DigitalTwinEnhanced.tsx
import React, { useRef, useState, useEffect, useCallback } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { 
  OrbitControls, Text, Box as DreiBox, Sphere, Cylinder, Plane,
  Line, Html
} from '@react-three/drei';
import * as THREE from 'three';
import { 
  Box, Chip, Paper, Typography, Slider, Button, ButtonGroup, 
  Grid, Card, CardContent, Switch, FormControlLabel, Divider,
  TextField, Stack, Tabs, Tab, LinearProgress, IconButton,
  Tooltip, CircularProgress, Alert, Snackbar, MenuItem, Select,
  FormControl, InputLabel, Avatar, Badge, List, ListItem,
  ListItemText, ListItemIcon, Accordion, AccordionSummary,
  AccordionDetails
} from '@mui/material';
import { 
  PlayArrow, Pause, Refresh, CompareArrows, TrendingUp, Science,
  Thermostat, Speed, Whatshot, BlurCircular, Grain, Warning,
  Info, Settings, Timeline, BubbleChart, ShowChart, BarChart,
  Download, Upload, Cloud, Memory, Hub, Security,
  ExpandMore, CameraAlt, Visibility, VisibilityOff,
  ZoomIn, ZoomOut, RotateLeft, RotateRight, GridOn,
  HeatPump, WaterDrop, ScatterPlot, Polyline, Gradient,
  Colorize, ArrowForward, ArrowBack, CheckCircle,
  ErrorOutline, Lightbulb, Analytics, Build,
  Speed as SpeedIcon, DeviceThermostat, Compress,
  FilterVintage, Gradient as GradientIcon, Waves,
  Timeline as TimelineIcon, StackedLineChart
} from '@mui/icons-material';
import { systemApi, knowledgeGraphApi } from '../services/api';
import { useWebSocketStream } from '../hooks/useWebSocketStream';

// –¶–≤–µ—Ç–æ–≤–∞—è –ø–∞–ª–∏—Ç—Ä–∞ –†–¢–£ –ú–ò–†–≠–ê —Å —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω—ã–º–∏ –≤–∞—Ä–∏–∞–Ω—Ç–∞–º–∏
const MIREA_COLORS = {
  primary: '#0066CC',
  primaryLight: '#4D94FF',
  primaryDark: '#004799',
  secondary: '#FF6B35',
  secondaryLight: '#FF9B68',
  secondaryDark: '#CC4800',
  success: '#00C853',
  successLight: '#5EFC82',
  successDark: '#009624',
  warning: '#FFA726',
  warningLight: '#FFD95B',
  warningDark: '#C77800',
  error: '#EF5350',
  errorLight: '#FF867C',
  errorDark: '#B61827',
  info: '#29B6F6',
  infoLight: '#73E8FF',
  infoDark: '#0086C3',
  background: '#0A1929',
  backgroundLight: '#1A2332',
  backgroundLighter: '#2A3342',
  text: '#FFFFFF',
  textSecondary: '#B0BEC5',
  textDisabled: '#546E7A',
  divider: '#37474F'
} as const;

// –ì—Ä–∞–¥–∏–µ–Ω—Ç—ã –¥–ª—è –∫—Ä–∞—Å–∏–≤–æ–≥–æ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏—è
const GRADIENTS = {
  primary: `linear-gradient(135deg, ${MIREA_COLORS.primary}, ${MIREA_COLORS.info})`,
  secondary: `linear-gradient(135deg, ${MIREA_COLORS.secondary}, ${MIREA_COLORS.warning})`,
  success: `linear-gradient(135deg, ${MIREA_COLORS.success}, ${MIREA_COLORS.infoLight})`,
  warning: `linear-gradient(135deg, ${MIREA_COLORS.warning}, ${MIREA_COLORS.error})`,
  background: `linear-gradient(180deg, ${MIREA_COLORS.background}, ${MIREA_COLORS.backgroundLight})`
} as const;

// –ò–Ω—Ç–µ—Ä—Ñ–µ–π—Å—ã
interface DefectInfo {
  name: string;
  color: string;
  icon: string;
  description: string;
  causes: string[];
}

interface DefectsState {
  crack: number;
  bubble: number;
  chip: number;
  deformation: number;
  cloudiness: number;
  stress: number;
}

interface RealState {
  furnaceTemp: number;
  moldTemp: number;
  beltSpeed: number;
  defects: DefectsState;
  viscosity: number;
  pressure: number;
  quality: number;
}

interface WhatIfState {
  furnaceTemp: number;
  moldTemp: number;
  beltSpeed: number;
  coolingRate: number;
  pressure: number;
  annealingTemp: number;
}

// –¢–∏–ø—ã –¥–µ—Ñ–µ–∫—Ç–æ–≤ —Å —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω–æ–π –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π
const DEFECT_TYPES: Record<string, DefectInfo> = {
  crack: { 
    name: '–¢—Ä–µ—â–∏–Ω–∞', 
    color: MIREA_COLORS.error,
    icon: '‚ö°',
    description: '–õ–∏–Ω–µ–π–Ω—ã–π —Ä–∞–∑—Ä—ã–≤ –≤ –º–∞—Ç–µ—Ä–∏–∞–ª–µ',
    causes: ['–¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω—ã–π —à–æ–∫', '–ú–µ—Ö–∞–Ω–∏—á–µ—Å–∫–æ–µ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–µ', '–ë—ã—Å—Ç—Ä–æ–µ –æ—Ö–ª–∞–∂–¥–µ–Ω–∏–µ']
  },
  bubble: { 
    name: '–ü—É–∑—ã—Ä—å', 
    color: MIREA_COLORS.warning,
    icon: 'ü´ß',
    description: '–ì–∞–∑–æ–≤—ã–µ –≤–∫–ª—é—á–µ–Ω–∏—è –≤ —Å—Ç–µ–∫–ª–µ',
    causes: ['–î–µ–≥–∞–∑–∞—Ü–∏—è', '–ù–∏–∑–∫–∞—è —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ –ø–ª–∞–≤–ª–µ–Ω–∏—è', '–ö–æ–Ω—Ç–∞–º–∏–Ω–∞—Ü–∏—è']
  },
  chip: { 
    name: '–°–∫–æ–ª', 
    color: MIREA_COLORS.info,
    icon: 'üî™',
    description: '–û—Ç–∫–æ–ª–æ–≤—à–∏–µ—Å—è —Ñ—Ä–∞–≥–º–µ–Ω—Ç—ã –∫—Ä–æ–º–∫–∏',
    causes: ['–ú–µ—Ö–∞–Ω–∏—á–µ—Å–∫–∏–π —É–¥–∞—Ä', '–í–Ω—É—Ç—Ä–µ–Ω–Ω–µ–µ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–µ', '–ù–∏–∑–∫–∞—è –ø—Ä–æ—á–Ω–æ—Å—Ç—å']
  },
  deformation: { 
    name: '–î–µ—Ñ–æ—Ä–º–∞—Ü–∏—è', 
    color: MIREA_COLORS.secondary,
    icon: 'üåÄ',
    description: '–ò—Å–∫–∞–∂–µ–Ω–∏–µ —Ñ–æ—Ä–º—ã –∏–∑–¥–µ–ª–∏—è',
    causes: ['–ù–µ—Ä–∞–≤–Ω–æ–º–µ—Ä–Ω–æ–µ –æ—Ö–ª–∞–∂–¥–µ–Ω–∏–µ', '–°–ª–∏—à–∫–æ–º –≤—ã—Å–æ–∫–∞—è —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ —Ñ–æ—Ä–º–æ–≤–∞–Ω–∏—è']
  },
  cloudiness: { 
    name: '–ü–æ–º—É—Ç–Ω–µ–Ω–∏–µ', 
    color: MIREA_COLORS.textSecondary,
    icon: '‚òÅÔ∏è',
    description: '–ü–æ—Ç–µ—Ä—è –ø—Ä–æ–∑—Ä–∞—á–Ω–æ—Å—Ç–∏',
    causes: ['–ö—Ä–∏—Å—Ç–∞–ª–ª–∏–∑–∞—Ü–∏—è', '–ù–µ–ø–æ–ª–Ω–æ–µ –ø–ª–∞–≤–ª–µ–Ω–∏–µ', '–ó–∞–≥—Ä—è–∑–Ω–µ–Ω–∏—è']
  },
  stress: { 
    name: '–ù–∞–ø—Ä—è–∂–µ–Ω–∏–µ', 
    color: MIREA_COLORS.primary,
    icon: '‚öñÔ∏è',
    description: '–í–Ω—É—Ç—Ä–µ–Ω–Ω–∏–µ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏—è –≤ –º–∞—Ç–µ—Ä–∏–∞–ª–µ',
    causes: ['–¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω—ã–π –≥—Ä–∞–¥–∏–µ–Ω—Ç', '–ë—ã—Å—Ç—Ä–æ–µ –æ—Ö–ª–∞–∂–¥–µ–Ω–∏–µ']
  }
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω–æ–≥–æ –ø–æ–ª—è
const TemperatureField = ({ temperatureData, dimensions }: { 
  temperatureData: number[][]; 
  dimensions: { width: number; height: number; depth: number };
}) => {
  const meshRef = useRef<THREE.InstancedMesh>(null);
  const { width, height, depth } = dimensions;
  const count = temperatureData.length * (temperatureData[0]?.length || 0);
  
  useEffect(() => {
    if (!meshRef.current || count === 0) return;
    
    const tempArray = temperatureData.flat();
    const minTemp = Math.min(...tempArray);
    const maxTemp = Math.max(...tempArray);
    
    const matrix = new THREE.Matrix4();
    const color = new THREE.Color();
    
    for (let i = 0; i < count; i++) {
      const x = (i % temperatureData[0].length) * (width / temperatureData[0].length);
      const z = Math.floor(i / temperatureData[0].length) * (depth / temperatureData.length);
      const temp = tempArray[i];
      
      // –ù–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä—ã –¥–ª—è —Ü–≤–µ—Ç–∞
      const normalized = (temp - minTemp) / (maxTemp - minTemp);
      
      // –¶–≤–µ—Ç–æ–≤–∞—è –≥—Ä–∞–¥–∞—Ü–∏—è –æ—Ç —Å–∏–Ω–µ–≥–æ –∫ –∫—Ä–∞—Å–Ω–æ–º—É
      color.setRGB(
        normalized, // –∫—Ä–∞—Å–Ω—ã–π
        Math.max(0, 0.5 - Math.abs(normalized - 0.5)), // –∑–µ–ª–µ–Ω—ã–π
        1 - normalized // —Å–∏–Ω–∏–π
      );
      
      matrix.setPosition(x - width/2, 0, z - depth/2);
      meshRef.current.setMatrixAt(i, matrix);
      meshRef.current.setColorAt(i, color);
    }
    
    if (meshRef.current.instanceMatrix) meshRef.current.instanceMatrix.needsUpdate = true;
    if (meshRef.current.instanceColor) meshRef.current.instanceColor.needsUpdate = true;
  }, [temperatureData, dimensions, count]);
  
  if (count === 0) return null;
  
  return (
    <instancedMesh ref={meshRef} args={[undefined, undefined, count]}>
      <boxGeometry args={[width / temperatureData[0].length, 0.1, depth / temperatureData.length]} />
      <meshStandardMaterial />
    </instancedMesh>
  );
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ —Ç–µ—á–µ–Ω–∏—è —Å—Ç–µ–∫–ª–æ–º–∞—Å—Å—ã
const GlassFlow = ({ 
  viscosity, 
  temperature, 
  flowRate 
}: { 
  viscosity: number; 
  temperature: number; 
  flowRate: number;
}) => {
  const groupRef = useRef<THREE.Group>(null);
  const particlesRef = useRef<THREE.Points>(null);
  const [particles, setParticles] = useState<Float32Array>(new Float32Array());
  
  useEffect(() => {
    // –°–æ–∑–¥–∞–Ω–∏–µ —á–∞—Å—Ç–∏—Ü –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ –ø–æ—Ç–æ–∫–∞
    const count = 1000;
    const positions = new Float32Array(count * 3);
    
    for (let i = 0; i < count; i++) {
      positions[i * 3] = (Math.random() - 0.5) * 10;
      positions[i * 3 + 1] = Math.random() * 2;
      positions[i * 3 + 2] = (Math.random() - 0.5) * 3;
    }
    
    setParticles(positions);
  }, []);
  
  useFrame((state) => {
    if (groupRef.current) {
      // –ê–Ω–∏–º–∞—Ü–∏—è —Ç–µ—á–µ–Ω–∏—è –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç –≤—è–∑–∫–æ—Å—Ç–∏ –∏ —Å–∫–æ—Ä–æ—Å—Ç–∏ –ø–æ—Ç–æ–∫–∞
      const speed = flowRate / (viscosity + 1) * 0.1;
      groupRef.current.position.x += Math.sin(state.clock.elapsedTime) * speed * 0.1;
      groupRef.current.rotation.y = Math.sin(state.clock.elapsedTime * 0.5) * 0.1;
    }
  });
  
  // –¶–≤–µ—Ç –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä—ã
  const tempColor = new THREE.Color();
  const normalizedTemp = (temperature - 1400) / 400;
  tempColor.setRGB(
    Math.min(1, normalizedTemp * 2),
    Math.max(0, 0.5 - Math.abs(normalizedTemp - 0.5)),
    Math.max(0, 1 - normalizedTemp * 1.5)
  );
  
  return (
    <group ref={groupRef}>
      {/* –û—Å–Ω–æ–≤–Ω–æ–π –ø–æ—Ç–æ–∫ —Å—Ç–µ–∫–ª–æ–º–∞—Å—Å—ã */}
      <DreiBox args={[8, 1, 3]} position={[0, 0.5, 0]}>
        <meshStandardMaterial 
          color={tempColor}
          transparent
          opacity={0.7}
          roughness={0.3}
          metalness={0.1}
        />
      </DreiBox>
      
      {/* –ß–∞—Å—Ç–∏—Ü—ã –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ —Ç–µ—á–µ–Ω–∏—è */}
      {particles.length > 0 && (
        <points ref={particlesRef}>
          <bufferGeometry>
            <bufferAttribute
              attach="attributes-position"
              count={particles.length / 3}
              array={particles}
              itemSize={3}
            />
          </bufferGeometry>
          <pointsMaterial
            size={0.05}
            color={tempColor}
            transparent
            opacity={0.6}
          />
        </points>
      )}
      
      {/* –í–∏—Ö—Ä–µ–≤—ã–µ –ø–æ—Ç–æ–∫–∏ */}
      {Array.from({ length: 3 }).map((_, i) => (
        <mesh key={i} position={[(i - 1) * 2, 0.6, 0]} rotation={[Math.PI / 2, 0, 0]}>
          <torusGeometry args={[0.3, 0.1, 16, 100]} />
          <meshStandardMaterial 
            color={MIREA_COLORS.info}
            emissive={MIREA_COLORS.info}
            emissiveIntensity={0.3}
            transparent
            opacity={0.5}
          />
        </mesh>
      ))}
    </group>
  );
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–π
const StressVisualization = ({ stressData }: { stressData: number[][] }) => {
  const linesRef = useRef<THREE.Group>(null);
  
  useFrame(() => {
    if (linesRef.current) {
      linesRef.current.rotation.y += 0.001;
    }
  });
  
  return (
    <group ref={linesRef}>
      {stressData.map((row, i) => 
        row.map((stress, j) => {
          const intensity = Math.min(1, stress / 100);
          const length = 0.1 + intensity * 0.5;
          
          return (
            <Line
              key={`${i}-${j}`}
              points={[
                [j - row.length/2, 0, i - stressData.length/2],
                [j - row.length/2, length, i - stressData.length/2]
              ]}
              color={intensity > 0.7 ? MIREA_COLORS.error : MIREA_COLORS.warning}
              lineWidth={2}
            />
          );
        })
      )}
    </group>
  );
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –¥–µ—Ñ–µ–∫—Ç–æ–≤ —Å –ø–æ–¥—Ä–æ–±–Ω–æ–π –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π
const EnhancedDefectVisualization = ({ defects, position }: {
  defects: DefectsState;
  position: [number, number, number];
}) => {
  const groupRef = useRef<THREE.Group>(null);
  const { clock } = useThree();
  
  useFrame(() => {
    if (groupRef.current) {
      groupRef.current.rotation.y += 0.005;
    }
  });
  
  const defectEntries = Object.entries(defects).filter(([_, severity]) => severity > 0);
  
  if (defectEntries.length === 0) return null;
  
  return (
    <group ref={groupRef} position={position}>
      {defectEntries.map(([type, severity], idx) => {
        const defectInfo = DEFECT_TYPES[type];
        if (!defectInfo) return null;
        
        const angle = (idx / defectEntries.length) * Math.PI * 2;
        const radius = 1 + severity * 2;
        const x = Math.cos(angle) * radius;
        const z = Math.sin(angle) * radius;
        const y = Math.sin(clock.elapsedTime * 2 + idx) * 0.5;
        
        return (
          <React.Fragment key={type}>
            <Sphere position={[x, y, z]} args={[0.05 + severity * 0.15]}>
              <meshStandardMaterial 
                color={defectInfo.color}
                emissive={defectInfo.color}
                emissiveIntensity={severity * 0.8}
                transparent
                opacity={0.7}
              />
            </Sphere>
            
            {/* –°–æ–µ–¥–∏–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –ª–∏–Ω–∏–∏ */}
            <Line
              points={[[0, 0, 0], [x, y, z]]}
              color={defectInfo.color}
              lineWidth={1}
            />
            
            {/* –¢–µ–∫—Å—Ç —Å –æ–ø–∏—Å–∞–Ω–∏–µ–º –¥–µ—Ñ–µ–∫—Ç–∞ */}
            <Html position={[x, y + 0.3, z]} center>
              <div style={{
                backgroundColor: 'rgba(0,0,0,0.8)',
                backdropFilter: 'blur(10px)',
                borderRadius: 4,
                padding: 8,
                minWidth: 120,
                border: `1px solid ${defectInfo.color}`
              }}>
                <div style={{ color: 'white', display: 'block', fontSize: 12 }}>
                  <strong>{defectInfo.icon} {defectInfo.name}</strong>
                </div>
                <div style={{ color: MIREA_COLORS.textSecondary, display: 'block', fontSize: 11 }}>
                  –í–µ—Ä–æ—è—Ç–Ω–æ—Å—Ç—å: {(severity * 100).toFixed(1)}%
                </div>
              </div>
            </Html>
          </React.Fragment>
        );
      })}
    </group>
  );
};

// –û—Å–Ω–æ–≤–Ω–∞—è 3D —Å—Ü–µ–Ω–∞ —Å —É–ª—É—á—à–µ–Ω–Ω–æ–π –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–µ–π
