              <meshStandardMaterial 
                color={moldTemp > 350 ? MIREA_COLORS.warning : MIREA_COLORS.info}
                emissive={moldTemp > 350 ? MIREA_COLORS.warning : MIREA_COLORS.info}
                emissiveIntensity={moldTemp > 350 ? 0.4 : 0.1}
              />
            </Cylinder>
            <Text
              position={[0, 1.8, 0]}
              color="white"
              fontSize={0.3}
