      />
      
      {/* –û—Å–∏ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç */}
      <Line
        points={[[-10, 0, 0], [10, 0, 0]]}
        color={MIREA_COLORS.error}
        lineWidth={1}
      />
      <Line
        points={[[0, -1, 0], [0, 10, 0]]}
        color={MIREA_COLORS.success}
        lineWidth={1}
      />
