};

// –ì–ª–∞–≤–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω–æ–≥–æ —Ü–∏—Ñ—Ä–æ–≤–æ–≥–æ –¥–≤–æ–π–Ω–∏–∫–∞
const DigitalTwinEnhanced = () => {
  // WebSocket —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ
  const { wsData, isConnected } = useWebSocketStream();
  
  // –°–æ—Å—Ç–æ—è–Ω–∏—è —Ä–µ–∞–ª—å–Ω–æ–≥–æ –ø—Ä–æ–∏–∑–≤–æ–¥—Å—Ç–≤–∞
  const [realState, setRealState] = useState<RealState>({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    defects: { crack: 0, bubble: 0, chip: 0, deformation: 0, cloudiness: 0, stress: 0 },
    viscosity: 1200,
    pressure: 45,
    quality: 95.2
  });
  
  // –ü—Ä–æ–≥–Ω–æ–∑–∏—Ä—É–µ–º–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
  const [predictedState, setPredictedState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    defects: { crack: 0, bubble: 0, chip: 0, deformation: 0, cloudiness: 0, stress: 0 },
    viscosity: 1200,
    quality: 95.2
  });
  
  // What-If —Å–æ—Å—Ç–æ—è–Ω–∏–µ
  const [whatIfState, setWhatIfState] = useState<WhatIfState>({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    coolingRate: 3.5,
    pressure: 45,
    annealingTemp: 580
  });
  
  // What-If —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã
  const [whatIfResults, setWhatIfResults] = useState({
    defects: { crack: 0, bubble: 0, chip: 0, deformation: 0, cloudiness: 0, stress: 0 },
    quality: 95.2,
    viscosity: 1200,
    stressLevel: 35
  });
  
  // UI —Å–æ—Å—Ç–æ—è–Ω–∏—è
  const [shadowMode, setShadowMode] = useState(true);
  const [whatIfMode, setWhatIfMode] = useState(false);
  const [isSimulating, setIsSimulating] = useState(false);
  const [activeTab, setActiveTab] = useState(0);
  const [showTemperatureField, setShowTemperatureField] = useState(true);
  const [showStressField, setShowStressField] = useState(false);
  const [showDefects, setShowDefects] = useState(true);
  
  // –î–∞–Ω–Ω—ã–µ –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏
  const [temperatureData, setTemperatureData] = useState<number[][]>([]);
  const [stressData, setStressData] = useState<number[][]>([]);
  
  // –†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏ –∏ –ø—Ä–∏—á–∏–Ω—ã –¥–µ—Ñ–µ–∫—Ç–æ–≤
  const [recommendations, setRecommendations] = useState<any[]>([]);
  const [defectCauses, setDefectCauses] = useState<Record<string, any[]>>({});
  
  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –¥–∞–Ω–Ω—ã—Ö –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏
  useEffect(() => {
    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω–æ–≥–æ –ø–æ–ª—è
    const tempField = Array.from({ length: 20 }, (_, i) => 
      Array.from({ length: 10 }, (_, j) => {
        const centerDist = Math.sqrt(Math.pow(i - 10, 2) + Math.pow(j - 5, 2));
        return realState.furnaceTemp - 200 * (centerDist / 14);
      })
    );
    setTemperatureData(tempField);
    
    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –ø–æ–ª—è –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–π
    const stressField = Array.from({ length: 15 }, (_, i) =>
      Array.from({ length: 15 }, (_, j) => {
        const x = (i - 7.5) / 7.5;
        const y = (j - 7.5) / 7.5;
        return Math.exp(-(x * x + y * y)) * 50 + Math.random() * 20;
      })
    );
    setStressData(stressField);
  }, [realState]);
  
  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ —Ä–µ–∞–ª—å–Ω–æ–≥–æ —Å–æ—Å—Ç–æ—è–Ω–∏—è –∏–∑ WebSocket
  useEffect(() => {
    if (wsData.parameters) {
      const wsDefects = wsData.defectAggregation || {};
      const newState = {
        furnaceTemp: wsData.parameters.furnace?.temperature || 1520,
        moldTemp: wsData.parameters.forming?.mold_temp || 320,
        beltSpeed: wsData.parameters.forming?.speed || 150,
        defects: {
          crack: wsDefects.crack || 0,
          bubble: wsDefects.bubble || 0,
          chip: wsDefects.chip || 0,
          deformation: wsDefects.deformation || 0,
          cloudiness: wsDefects.cloudiness || 0,
          stress: wsDefects.stress || 0
        },
        viscosity: 1200,
        pressure: wsData.parameters.forming?.pressure || 45,
        quality: wsData.qualityMetrics?.qualityRate || 95.2
      };
      setRealState(newState);
      
      // –°–∏–º—É–ª—è—Ü–∏—è –ø—Ä–æ–≥–Ω–æ–∑–∞
      setTimeout(() => {
        setPredictedState({
          furnaceTemp: newState.furnaceTemp + (Math.random() - 0.5) * 10,
          moldTemp: newState.moldTemp + (Math.random() - 0.5) * 5,
          beltSpeed: newState.beltSpeed + (Math.random() - 0.5) * 3,
          defects: {
            crack: newState.defects.crack * 1.1,
            bubble: newState.defects.bubble * 1.05,
            chip: newState.defects.chip * 1.08,
            deformation: newState.defects.deformation || 0,
            cloudiness: newState.defects.cloudiness || 0,
            stress: newState.defects.stress || 0
          },
          viscosity: 1200,
          quality: newState.quality + (Math.random() - 0.5) * 2
        });
      }, 1000);
    }
  }, [wsData]);
  
  // –ü–æ–ª—É—á–µ–Ω–∏–µ –ø—Ä–∏—á–∏–Ω –¥–µ—Ñ–µ–∫—Ç–æ–≤
  const fetchDefectCauses = async () => {
    const causes: Record<string, any[]> = {};
    for (const defectType of Object.keys(realState.defects)) {
      try {
        const response = await knowledgeGraphApi.getCausesOfDefect(defectType);
        causes[defectType] = response;
      } catch (error) {
        console.error(`Error fetching causes for ${defectType}:`, error);
      }
    }
    setDefectCauses(causes);
  };
  
  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–π
  const fetchRecommendations = async () => {
    try {
      const response = await knowledgeGraphApi.getRecommendations('crack', {
        furnace_temperature: realState.furnaceTemp,
        belt_speed: realState.beltSpeed,
        mold_temperature: realState.moldTemp
      });
      setRecommendations(response);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };
  
  // –ó–∞–ø—É—Å–∫ What-If —Å–∏–º—É–ª—è—Ü–∏–∏
  const runWhatIfSimulation = async () => {
    setIsSimulating(true);
    
    // –§–∏–∑–∏—á–µ—Å–∫–∞—è –º–æ–¥–µ–ª—å —Ä–∞—Å—á–µ—Ç–∞ –¥–µ—Ñ–µ–∫—Ç–æ–≤
    const tempDeviation = Math.abs(whatIfState.furnaceTemp - 1520) / 50;
    const speedDeviation = Math.abs(whatIfState.beltSpeed - 150) / 20;
    const moldDeviation = Math.abs(whatIfState.moldTemp - 320) / 30;
    const coolingDeviation = Math.abs(whatIfState.coolingRate - 3.5) / 2;
    const pressureDeviation = Math.abs(whatIfState.pressure - 45) / 15;
    
    const crackProbability = Math.min(tempDeviation * 8 + speedDeviation * 3 + coolingDeviation * 5, 30);
    const bubbleProbability = Math.min(tempDeviation * 4 + moldDeviation * 6 + pressureDeviation * 3, 25);
    const chipProbability = Math.min(speedDeviation * 5 + moldDeviation * 3 + coolingDeviation * 2, 20);
    const deformationProbability = Math.min(tempDeviation * 3 + speedDeviation * 4 + moldDeviation * 2, 18);
    const cloudinessProbability = Math.min(tempDeviation * 5 + coolingDeviation * 4, 15);
    const stressProbability = Math.min(tempDeviation * 6 + coolingDeviation * 7, 35);
    
    const quality = Math.max(70, 95 - crackProbability - bubbleProbability * 0.7 - chipProbability * 0.5);
