                          </ListItemIcon>
                          <ListItemText
                            primary={
                              <Typography variant="body2" style={{ color: MIREA_COLORS.text }}>
                                {rec.action || rec.recommendation}
                              </Typography>
                            }
                            secondary={
                              <Typography variant="caption" style={{ color: MIREA_COLORS.textSecondary }}>
                                –ü—Ä–∏–æ—Ä–∏—Ç–µ—Ç: {rec.priority || '–°–†–ï–î–ù–ò–ô'} ‚Ä¢ –≠—Ñ—Ñ–µ–∫—Ç–∏–≤–Ω–æ—Å—Ç—å: {rec.expected_impact || '–í—ã—Å–æ–∫–∞—è'}
                              </Typography>
                            }
                          />
                        </ListItem>
                      ))}
                      
                      {recommendations.length === 0 && (
                        <Typography variant="body2" style={{ color: MIREA_COLORS.textSecondary, textAlign: 'center', padding: 16 }}>
                          –ù–µ—Ç –∞–∫—Ç–∏–≤–Ω—ã—Ö —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–π
                        </Typography>
                      )}
                    </List>
                  </CardContent>
                </Card>
              </Stack>
            )}
            
            {/* –í–∫–ª–∞–¥–∫–∞ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è What-If */}
            {activeTab === 2 && whatIfMode && (
              <Stack spacing={2}>
                <Card style={{ backgroundColor: 'rgba(255, 107, 53, 0.1)', border: `2px solid ${MIREA_COLORS.secondary}` }}>
                  <CardContent>
                    <Typography variant="subtitle2" style={{ 
                      color: MIREA_COLORS.text, 
                      marginBottom: 24, 
                      fontWeight: 700,
                      display: 'flex',
                      alignItems: 'center',
                      gap: 8
                    }}>
                      <Science />
                      What-If –ê–Ω–∞–ª–∏–∑
                    </Typography>
                    
                    <Stack spacing={3}>
                      {/* –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ –ø–µ—á–∏ */}
                      <div>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.text, display: 'flex', alignItems: 'center', gap: 4 }}>
                            <Whatshot fontSize="small" />
                            –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ –ø–µ—á–∏
                          </Typography>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.text, fontWeight: 700 }}>
                            {whatIfState.furnaceTemp}¬∞C
                          </Typography>
                        </div>
                        <Slider
                          value={whatIfState.furnaceTemp}
                          onChange={(_, value) => setWhatIfState({ ...whatIfState, furnaceTemp: value as number })}
                          min={1400}
                          max={1700}
                          step={5}
                          marks={[
                            { value: 1400, label: '1400' },
                            { value: 1550, label: '1550' },
                            { value: 1700, label: '1700' }
                          ]}
                          sx={{ 
                            color: MIREA_COLORS.secondary,
                            '& .MuiSlider-markLabel': { color: MIREA_COLORS.textSecondary }
                          }}
                        />
                      </div>
                      
                      {/* –°–∫–æ—Ä–æ—Å—Ç—å –ª–µ–Ω—Ç—ã */}
                      <div>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.text, display: 'flex', alignItems: 'center', gap: 4 }}>
                            <Speed fontSize="small" />
                            –°–∫–æ—Ä–æ—Å—Ç—å –ª–µ–Ω—Ç—ã
                          </Typography>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.text, fontWeight: 700 }}>
                            {whatIfState.beltSpeed} –º/–º–∏–Ω
                          </Typography>
                        </div>
                        <Slider
                          value={whatIfState.beltSpeed}
                          onChange={(_, value) => setWhatIfState({ ...whatIfState, beltSpeed: value as number })}
                          min={100}
                          max={200}
                          step={5}
                          marks={[
                            { value: 100, label: '100' },
                            { value: 150, label: '150' },
                            { value: 200, label: '200' }
                          ]}
                          sx={{ 
                            color: MIREA_COLORS.info,
                            '& .MuiSlider-markLabel': { color: MIREA_COLORS.textSecondary }
                          }}
                        />
                      </div>
                      
                      {/* –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ —Ñ–æ—Ä–º—ã */}
                      <div>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.text, display: 'flex', alignItems: 'center', gap: 4 }}>
                            <DeviceThermostat fontSize="small" />
                            –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ —Ñ–æ—Ä–º—ã
                          </Typography>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.text, fontWeight: 700 }}>
                            {whatIfState.moldTemp}¬∞C
                          </Typography>
                        </div>
                        <Slider
                          value={whatIfState.moldTemp}
                          onChange={(_, value) => setWhatIfState({ ...whatIfState, moldTemp: value as number })}
                          min={280}
                          max={380}
                          step={5}
                          marks={[
                            { value: 280, label: '280' },
                            { value: 330, label: '330' },
                            { value: 380, label: '380' }
                          ]}
                          sx={{ 
                            color: MIREA_COLORS.warning,
                            '& .MuiSlider-markLabel': { color: MIREA_COLORS.textSecondary }
                          }}
                        />
                      </div>
                      
                      {/* –°–∫–æ—Ä–æ—Å—Ç—å –æ—Ö–ª–∞–∂–¥–µ–Ω–∏—è */}
                      <div>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.text, display: 'flex', alignItems: 'center', gap: 4 }}>
                            <WaterDrop fontSize="small" />
                            –°–∫–æ—Ä–æ—Å—Ç—å –æ—Ö–ª–∞–∂–¥–µ–Ω–∏—è
                          </Typography>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.text, fontWeight: 700 }}>
                            {whatIfState.coolingRate} ¬∞C/–º–∏–Ω
                          </Typography>
                        </div>
                        <Slider
                          value={whatIfState.coolingRate}
                          onChange={(_, value) => setWhatIfState({ ...whatIfState, coolingRate: value as number })}
                          min={1}
                          max={10}
                          step={0.5}
                          marks={[
                            { value: 1, label: '1' },
                            { value: 5.5, label: '5.5' },
                            { value: 10, label: '10' }
                          ]}
                          sx={{ 
                            color: MIREA_COLORS.info,
                            '& .MuiSlider-markLabel': { color: MIREA_COLORS.textSecondary }
                          }}
                        />
                      </div>
                      
                      {/* –ö–Ω–æ–ø–∫–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
                      <Stack direction="row" spacing={2}>
                        <Button
                          variant="contained"
                          fullWidth
                          onClick={runWhatIfSimulation}
                          disabled={isSimulating}
                          startIcon={isSimulating ? <CircularProgress size={20} /> : <TrendingUp />}
                          style={{
                            background: GRADIENTS.secondary,
                            fontWeight: 700,
                            padding: '12px 0'
                          }}
                        >
                          {isSimulating ? '–ê–Ω–∞–ª–∏–∑...' : '–°–∏–º—É–ª–∏—Ä–æ–≤–∞—Ç—å'}
                        </Button>
                        <Button
                          variant="outlined"
                          onClick={resetWhatIf}
                          style={{ 
                            borderColor: MIREA_COLORS.text, 
                            color: MIREA_COLORS.text,
                            minWidth: 'auto'
                          }}
                        >
                          <Refresh />
                        </Button>
                      </Stack>
                    </Stack>
                  </CardContent>
                </Card>
                
                {/* –†–µ–∑—É–ª—å—Ç–∞—Ç—ã What-If */}
                <Card style={{ backgroundColor: 'rgba(0, 200, 83, 0.1)', border: `1px solid ${MIREA_COLORS.success}` }}>
                  <CardContent>
                    <Typography variant="subtitle2" style={{ color: MIREA_COLORS.text, marginBottom: 16, fontWeight: 700 }}>
                      üìä –†–µ–∑—É–ª—å—Ç–∞—Ç—ã —Å–∏–º—É–ª—è—Ü–∏–∏
                    </Typography>
                    
                    <Grid container spacing={1}>
                      <Grid item xs={6}>
                        <Paper style={{ padding: 12, backgroundColor: 'rgba(0,0,0,0.3)', borderRadius: 4, height: '100%' }}>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.textSecondary, display: 'block', marginBottom: 4 }}>
                            –ö–∞—á–µ—Å—Ç–≤–æ
                          </Typography>
                          <Typography variant="h5" style={{ 
                            color: whatIfResults.quality > 90 ? MIREA_COLORS.success : 
                                   whatIfResults.quality > 80 ? MIREA_COLORS.warning : 
                                   MIREA_COLORS.error,
                            fontWeight: 700
                          }}>
                            {whatIfResults.quality}%
                          </Typography>
                        </Paper>
                      </Grid>
                      
                      <Grid item xs={6}>
                        <Paper style={{ padding: 12, backgroundColor: 'rgba(0,0,0,0.3)', borderRadius: 4, height: '100%' }}>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.textSecondary, display: 'block', marginBottom: 4 }}>
                            –í—è–∑–∫–æ—Å—Ç—å
                          </Typography>
                          <Typography variant="h5" style={{ 
                            color: whatIfResults.viscosity > 1500 ? MIREA_COLORS.warning : 
                                   whatIfResults.viscosity < 800 ? MIREA_COLORS.error : 
                                   MIREA_COLORS.success,
                            fontWeight: 700
                          }}>
                            {whatIfResults.viscosity}
                          </Typography>
                        </Paper>
                      </Grid>
                      
                      <Grid item xs={12}>
                        <Paper style={{ padding: 12, backgroundColor: 'rgba(0,0,0,0.3)', borderRadius: 4, marginTop: 8 }}>
                          <Typography variant="caption" style={{ color: MIREA_COLORS.textSecondary, display: 'block', marginBottom: 8 }}>
                            –£—Ä–æ–≤–µ–Ω—å –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–π
                          </Typography>
                          <LinearProgress 
                            variant="determinate" 
                            value={whatIfResults.stressLevel} 
                            style={{ 
                              height: 10,
                              borderRadius: 4,
                              backgroundColor: MIREA_COLORS.backgroundLighter
                            }}
                            sx={{
                              '& .MuiLinearProgress-bar': { 
                                backgroundColor: whatIfResults.stressLevel > 70 ? MIREA_COLORS.error : 
                                         whatIfResults.stressLevel > 40 ? MIREA_COLORS.warning : 
                                         MIREA_COLORS.success
                              }
                            }}
                          />
                          <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: 4 }}>
                            <Typography variant="caption" style={{ color: MIREA_COLORS.textSecondary }}>
                              –ù–∏–∑–∫–∏–π
                            </Typography>
                            <Typography variant="caption" style={{ color: MIREA_COLORS.textSecondary }}>
                              –í—ã—Å–æ–∫–∏–π
                            </Typography>
                          </div>
                        </Paper>
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              </Stack>
            )}
          </div>
        </Grid>
      </Grid>
    </div>
  );
};

export default DigitalTwinEnhanced;
