import React, { useRef, useState, useEffect, useMemo } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { OrbitControls, Text, Box as DreiBox, Sphere, Cylinder, Plane, Cone } from '@react-three/drei';
import * as THREE from 'three';
import { 
  Box, Chip, Paper, Typography, Slider, Button, ButtonGroup, 
  Grid, Card, CardContent, Switch, FormControlLabel, Divider,
  Stack, LinearProgress, Tooltip, IconButton, Alert, Tabs, Tab,
  Dialog, DialogTitle, DialogContent, DialogActions, Collapse
} from '@mui/material';
import { 
  PlayArrow, Pause, Refresh, CompareArrows, TrendingUp, Science,
  LocalFireDepartment, Thermostat, Speed, Warning, CheckCircle,
  Visibility, VisibilityOff, Info, ExpandMore, ExpandLess
} from '@mui/icons-material';

// Цветовая палитра РТУ МИРЭА
const MIREA_COLORS = {
  primary: '#0066CC',
  primaryDark: '#004C99',
  primaryLight: '#3385D6',
  secondary: '#FF6B35',
  secondaryDark: '#CC5529',
  accent: '#00D9FF',
  success: '#00C853',
  warning: '#FFA726',
  error: '#EF5350',
  background: '#0A0E1A',
  backgroundLight: '#141824',
  surface: '#1E2330',
  surfaceLight: '#2A3142',
  text: '#FFFFFF',
  textSecondary: '#B0B8C8',
  glass: 'rgba(255, 255, 255, 0.05)'
};

// Hook для WebSocket подключения
const useWebSocket = (url) => {
  const [wsData, setWsData] = useState({
    parameters: null,
    defectAlerts: [],
    isConnected: false
  });

  useEffect(() => {
    const ws = new WebSocket(url);
    
    ws.onopen = () => {
      console.log('WebSocket connected');
      setWsData(prev => ({ ...prev, isConnected: true }));
    };
    
    ws.onmessage = (event) => {
      try {
        const message = JSON.parse(event.data);
        
        if (message.type === 'parameter_update') {
          setWsData(prev => ({
            ...prev,
            parameters: message.data
          }));
        } else if (message.type === 'defect_alert') {
          setWsData(prev => ({
            ...prev,
            defectAlerts: [...prev.defectAlerts.slice(-19), message.data]
          }));
        }
      } catch (err) {
        console.error('WebSocket parse error:', err);
      }
    };
    
    ws.onclose = () => {
      console.log('WebSocket disconnected');
      setWsData(prev => ({ ...prev, isConnected: false }));
    };
    
    return () => ws.close();
  }, [url]);

  return wsData;
};

// Компонент: Печь с температурной визуализацией
const EnhancedFurnace = ({ temperature, thermalField, showField }) => {
  const furnaceRef = useRef();
  const [glowIntensity, setGlowIntensity] = useState(0);
  
  useEffect(() => {
    const intensity = Math.min(1, Math.max(0, (temperature - 1400) / 300));
    setGlowIntensity(intensity);
  }, [temperature]);
  
  useFrame((state) => {
    if (furnaceRef.current && glowIntensity > 0.7) {
      const pulse = Math.sin(state.clock.elapsedTime * 2) * 0.03;
      furnaceRef.current.scale.x = 1 + pulse;
      furnaceRef.current.scale.z = 1 + pulse;
    }
  });
  
  return (
    <group position={[-6, 1.5, 0]}>
      {/* Основной корпус печи */}
      <DreiBox ref={furnaceRef} position={[0, 0, 0]} args={[4, 3, 3]} castShadow receiveShadow>
        <meshStandardMaterial 
          color={MIREA_COLORS.secondary}
          emissive={MIREA_COLORS.secondary}
          emissiveIntensity={glowIntensity * 0.7}
          metalness={0.3}
          roughness={0.6}
        />
      </DreiBox>
      
      {/* Дверца печи */}
      <DreiBox position={[0, 0, 1.6]} args={[2.5, 2, 0.15]} castShadow>
        <meshStandardMaterial 
          color={MIREA_COLORS.error}
          emissive={MIREA_COLORS.error}
          emissiveIntensity={glowIntensity * 0.9}
          metalness={0.5}
          roughness={0.3}
        />
      </DreiBox>
      
      {/* Окно печи */}
      <DreiBox position={[0, 0.5, 1.65]} args={[1, 0.8, 0.05]} castShadow>
        <meshStandardMaterial 
          color="#FFD700"
          emissive="#FFD700"
          emissiveIntensity={glowIntensity}
          transparent
