        <group key={i} position={[-1.5 + i * 1.5, -1.2, 0]}>
          <Cylinder args={[0.15, 0.15, 0.8]} rotation={[Math.PI / 2, 0, 0]}>
            <meshStandardMaterial color="#333333" metalness={0.8} roughness={0.2} />
          </Cylinder>
          {/* Flame effect */}
          <Cone 
            args={[0.2, 0.6, 8]} 
            position={[0, 0, 0.6]}
