};

// Компонент: Визуализация температурного поля
const ThermalFieldVisualization = ({ field, position }) => {
  const meshRef = useRef();
  
  useFrame(() => {
    if (meshRef.current) {
      meshRef.current.rotation.y += 0.001;
    }
  });
  
  return (
    <group ref={meshRef} position={position}>
      {field && field.map((row, i) => 
        row.map((temp, j) => {
          const normalizedTemp = (temp - 1000) / 1000;
          const color = new THREE.Color().setHSL(
            (1 - normalizedTemp) * 0.6, 
            1, 
            0.5
          );
          
          return (
            <Sphere 
              key={`${i}-${j}`}
              args={[0.08]} 
              position={[
                (i - field.length / 2) * 0.3,
                (j - row.length / 2) * 0.3,
                0
              ]}
            >
              <meshStandardMaterial 
                color={color}
                emissive={color}
                emissiveIntensity={normalizedTemp * 0.5}
                transparent
                opacity={0.6}
              />
            </Sphere>
          );
        })
      )}
    </group>
  );
};

// Компонент: Формовочная линия с анимацией
const FormingLine = ({ speed, moldTemp, glass }) => {
  const beltRef = useRef();
  const glassRef = useRef();
  const [beltOffset, setBeltOffset] = useState(0);
  
  useFrame((state) => {
    if (beltRef.current) {
      const delta = state.clock.getDelta();
      setBeltOffset((prev) => (prev + speed * delta * 0.05) % 10);
      beltRef.current.position.x = -4 + beltOffset;
    }
    
    // Анимация стекла
    if (glassRef.current && glass) {
      const pulse = Math.sin(state.clock.elapsedTime * 3) * 0.02;
      glassRef.current.scale.y = 1 + pulse;
    }
  });
  
  return (
    <group position={[4, 0.5, 0]}>
      {/* Основание конвейера */}
      <DreiBox position={[0, -0.3, 0]} args={[10, 0.4, 2]} castShadow receiveShadow>
        <meshStandardMaterial 
          color={MIREA_COLORS.surfaceLight}
          metalness={0.7}
          roughness={0.3}
        />
      </DreiBox>
      
      {/* Движущаяся лента */}
      <DreiBox 
        ref={beltRef}
        position={[0, 0, 0]} 
        args={[1.5, 0.05, 1.8]} 
        castShadow
      >
        <meshStandardMaterial 
          color={MIREA_COLORS.primary}
          metalness={0.6}
          roughness={0.4}
        />
      </DreiBox>
      
      {/* Формы для стекла */}
      {[...Array(4)].map((_, i) => {
        const tempColor = moldTemp > 350 ? MIREA_COLORS.error : MIREA_COLORS.info;
        const intensity = moldTemp > 350 ? 0.4 : 0.1;
        
        return (
          <group key={i} position={[-4 + i * 2.5, 0.5, 0]}>
            <Cylinder args={[0.6, 0.6, 0.8]} castShadow>
              <meshStandardMaterial 
                color={tempColor}
                emissive={tempColor}
                emissiveIntensity={intensity}
                metalness={0.5}
                roughness={0.5}
              />
            </Cylinder>
            
            {/* Glass piece */}
            {glass && i === 1 && (
              <DreiBox 
                ref={glassRef}
                args={[1, 0.3, 1]} 
                position={[0, 0.5, 0]}
                castShadow
              >
                <meshPhysicalMaterial 
                  color={MIREA_COLORS.accent}
                  transparent
                  opacity={0.7}
                  metalness={0.1}
                  roughness={0.1}
                  transmission={0.9}
                  thickness={0.5}
                />
              </DreiBox>
            )}
            
            <Text
              position={[0, 1.5, 0]}
              color={MIREA_COLORS.textSecondary}
              fontSize={0.25}
              anchorX="center"
            >
              {moldTemp.toFixed(0)}°C
            </Text>
          </group>
        );
      })}
      
      {/* Speed indicator */}
      <Text
        position={[0, -1, 0]}
        color={MIREA_COLORS.accent}
        fontSize={0.35}
        fontWeight={700}
        anchorX="center"
      >
        {speed.toFixed(0)} м/мин
      </Text>
    </group>
  );
};

// Компонент: Дефекты в 3D
const DefectParticles = ({ defects, showDefects }) => {
  const particlesRef = useRef();
  const particles = useMemo(() => {
    const defectTypes = Object.entries(defects);
    const totalDefects = Object.values(defects).reduce((sum, val) => sum + val, 0);
    const count = Math.min(100, Math.floor(totalDefects * 200));
    
    return Array.from({ length: count }, (_, i) => {
      const angle = (i / count) * Math.PI * 2;
      const radius = 2 + Math.random() * 4;
      const x = Math.cos(angle) * radius;
      const z = Math.sin(angle) * radius;
      const y = Math.random() * 3;
      
      // Select defect type based on probabilities
      let selectedType = 'crack';
      let cumulative = 0;
      for (const [type, prob] of defectTypes) {
        cumulative += prob;
        if (Math.random() < cumulative / totalDefects) {
          selectedType = type;
          break;
        }
      }
      
      const colorMap = {
        crack: MIREA_COLORS.error,
        bubble: MIREA_COLORS.warning,
        chip: MIREA_COLORS.secondary,
        cloudiness: '#AAAAAA',
        deformation: '#9C27B0',
        stress: '#E91E63'
      };
      
      return {
        position: [x, y, z],
        color: colorMap[selectedType] || '#FFFFFF',
        type: selectedType,
        size: 0.04 + Math.random() * 0.06
      };
    });
  }, [defects]);
  
  useFrame((state) => {
    if (particlesRef.current && showDefects) {
      particlesRef.current.rotation.y += 0.003;
    }
  });
  
  if (!showDefects) return null;
  
  return (
    <group ref={particlesRef} position={[0, 2, 0]}>
      {particles.map((particle, i) => (
        <Sphere 
          key={i} 
          position={particle.position} 
          args={[particle.size]} 
          castShadow
        >
          <meshStandardMaterial 
            color={particle.color} 
            emissive={particle.color} 
            emissiveIntensity={0.5}
            transparent
            opacity={0.8}
          />
        </Sphere>
      ))}
    </group>
  );
};

// Компонент: Освещение и фон
