                border: `1px solid rgba(${color === 'primary' ? '0, 102, 255' : color === 'success' ? '0, 230, 118' : '255, 215, 0'}, 0.3)`,
                color: color === 'primary' ? MIREA_2025_COLORS.primaryLight :
                    color === 'success' ? MIREA_2025_COLORS.success : MIREA_2025_COLORS.warning,
