// DigitalTwinEnhanced.tsx
import React, { useRef, useState, useEffect, useCallback } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { 
  OrbitControls, Text, Box as DreiBox, Sphere, Cylinder, Plane,
  Line, Html
} from '@react-three/drei';
import * as THREE from 'three';
import { 
  Box, Chip, Paper, Typography, Slider, Button, ButtonGroup, 
  Grid, Card, CardContent, Switch, FormControlLabel, Divider,
  TextField, Stack, Tabs, Tab, LinearProgress, IconButton,
  Tooltip, CircularProgress, Alert, Snackbar, MenuItem, Select,
  FormControl, InputLabel, Avatar, Badge, List, ListItem,
  ListItemText, ListItemIcon, Accordion, AccordionSummary,
  AccordionDetails
} from '@mui/material';
import { 
  PlayArrow, Pause, Refresh, CompareArrows, TrendingUp, Science,
  Thermostat, Speed, Whatshot, BlurCircular, Grain, Warning,
  Info, Settings, Timeline, BubbleChart, ShowChart, BarChart,
  Download, Upload, Cloud, Memory, Hub, Security,
  ExpandMore, CameraAlt, Visibility, VisibilityOff,
  ZoomIn, ZoomOut, RotateLeft, RotateRight, GridOn,
  HeatPump, WaterDrop, ScatterPlot, Polyline, Gradient,
  Colorize, ArrowForward, ArrowBack, CheckCircle,
  ErrorOutline, Lightbulb, Analytics, Build,
  Speed as SpeedIcon, DeviceThermostat, Compress,
  FilterVintage, Gradient as GradientIcon, Waves,
  Timeline as TimelineIcon, StackedLineChart
} from '@mui/icons-material';
import { systemApi, knowledgeGraphApi } from '../services/api';
import { useWebSocketStream } from '../hooks/useWebSocketStream';

// –¶–≤–µ—Ç–æ–≤–∞—è –ø–∞–ª–∏—Ç—Ä–∞ –†–¢–£ –ú–ò–†–≠–ê —Å —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω—ã–º–∏ –≤–∞—Ä–∏–∞–Ω—Ç–∞–º–∏
const MIREA_COLORS = {
  primary: '#0066CC',
  primaryLight: '#4D94FF',
  primaryDark: '#004799',
  secondary: '#FF6B35',
  secondaryLight: '#FF9B68',
  secondaryDark: '#CC4800',
  success: '#00C853',
  successLight: '#5EFC82',
  successDark: '#009624',
  warning: '#FFA726',
  warningLight: '#FFD95B',
  warningDark: '#C77800',
  error: '#EF5350',
  errorLight: '#FF867C',
  errorDark: '#B61827',
  info: '#29B6F6',
  infoLight: '#73E8FF',
  infoDark: '#0086C3',
  background: '#0A1929',
  backgroundLight: '#1A2332',
  backgroundLighter: '#2A3342',
  text: '#FFFFFF',
  textSecondary: '#B0BEC5',
  textDisabled: '#546E7A',
  divider: '#37474F'
};

// –ì—Ä–∞–¥–∏–µ–Ω—Ç—ã –¥–ª—è –∫—Ä–∞—Å–∏–≤–æ–≥–æ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏—è
const GRADIENTS = {
  primary: `linear-gradient(135deg, ${MIREA_COLORS.primary}, ${MIREA_COLORS.info})`,
  secondary: `linear-gradient(135deg, ${MIREA_COLORS.secondary}, ${MIREA_COLORS.warning})`,
  success: `linear-gradient(135deg, ${MIREA_COLORS.success}, ${MIREA_COLORS.infoLight})`,
  warning: `linear-gradient(135deg, ${MIREA_COLORS.warning}, ${MIREA_COLORS.error})`,
  background: `linear-gradient(180deg, ${MIREA_COLORS.background}, ${MIREA_COLORS.backgroundLight})`
};

// –¢–∏–ø—ã –¥–µ—Ñ–µ–∫—Ç–æ–≤ —Å —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω–æ–π –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π
const DEFECT_TYPES = {
  crack: { 
    name: '–¢—Ä–µ—â–∏–Ω–∞', 
    color: MIREA_COLORS.error,
    icon: '‚ö°',
    description: '–õ–∏–Ω–µ–π–Ω—ã–π —Ä–∞–∑—Ä—ã–≤ –≤ –º–∞—Ç–µ—Ä–∏–∞–ª–µ',
    causes: ['–¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω—ã–π —à–æ–∫', '–ú–µ—Ö–∞–Ω–∏—á–µ—Å–∫–æ–µ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–µ', '–ë—ã—Å—Ç—Ä–æ–µ –æ—Ö–ª–∞–∂–¥–µ–Ω–∏–µ']
  },
  bubble: { 
    name: '–ü—É–∑—ã—Ä—å', 
    color: MIREA_COLORS.warning,
    icon: 'ü´ß',
    description: '–ì–∞–∑–æ–≤—ã–µ –≤–∫–ª—é—á–µ–Ω–∏—è –≤ —Å—Ç–µ–∫–ª–µ',
    causes: ['–î–µ–≥–∞–∑–∞—Ü–∏—è', '–ù–∏–∑–∫–∞—è —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ –ø–ª–∞–≤–ª–µ–Ω–∏—è', '–ö–æ–Ω—Ç–∞–º–∏–Ω–∞—Ü–∏—è']
  },
  chip: { 
    name: '–°–∫–æ–ª', 
    color: MIREA_COLORS.info,
    icon: 'üî™',
    description: '–û—Ç–∫–æ–ª–æ–≤—à–∏–µ—Å—è —Ñ—Ä–∞–≥–º–µ–Ω—Ç—ã –∫—Ä–æ–º–∫–∏',
    causes: ['–ú–µ—Ö–∞–Ω–∏—á–µ—Å–∫–∏–π —É–¥–∞—Ä', '–í–Ω—É—Ç—Ä–µ–Ω–Ω–µ–µ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–µ', '–ù–∏–∑–∫–∞—è –ø—Ä–æ—á–Ω–æ—Å—Ç—å']
  },
  deformation: { 
    name: '–î–µ—Ñ–æ—Ä–º–∞—Ü–∏—è', 
    color: MIREA_COLORS.secondary,
    icon: 'üåÄ',
    description: '–ò—Å–∫–∞–∂–µ–Ω–∏–µ —Ñ–æ—Ä–º—ã –∏–∑–¥–µ–ª–∏—è',
    causes: ['–ù–µ—Ä–∞–≤–Ω–æ–º–µ—Ä–Ω–æ–µ –æ—Ö–ª–∞–∂–¥–µ–Ω–∏–µ', '–°–ª–∏—à–∫–æ–º –≤—ã—Å–æ–∫–∞—è —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ —Ñ–æ—Ä–º–æ–≤–∞–Ω–∏—è']
  },
  cloudiness: { 
    name: '–ü–æ–º—É—Ç–Ω–µ–Ω–∏–µ', 
    color: MIREA_COLORS.textSecondary,
    icon: '‚òÅÔ∏è',
    description: '–ü–æ—Ç–µ—Ä—è –ø—Ä–æ–∑—Ä–∞—á–Ω–æ—Å—Ç–∏',
    causes: ['–ö—Ä–∏—Å—Ç–∞–ª–ª–∏–∑–∞—Ü–∏—è', '–ù–µ–ø–æ–ª–Ω–æ–µ –ø–ª–∞–≤–ª–µ–Ω–∏–µ', '–ó–∞–≥—Ä—è–∑–Ω–µ–Ω–∏—è']
  },
  stress: { 
    name: '–ù–∞–ø—Ä—è–∂–µ–Ω–∏–µ', 
    color: MIREA_COLORS.primary,
    icon: '‚öñÔ∏è',
    description: '–í–Ω—É—Ç—Ä–µ–Ω–Ω–∏–µ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏—è –≤ –º–∞—Ç–µ—Ä–∏–∞–ª–µ',
    causes: ['–¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω—ã–π –≥—Ä–∞–¥–∏–µ–Ω—Ç', '–ë—ã—Å—Ç—Ä–æ–µ –æ—Ö–ª–∞–∂–¥–µ–Ω–∏–µ']
  }
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω–æ–≥–æ –ø–æ–ª—è
const TemperatureField = ({ temperatureData, dimensions }: { 
  temperatureData: number[][]; 
  dimensions: { width: number; height: number; depth: number };
}) => {
  const meshRef = useRef<THREE.InstancedMesh>(null);
  const { width, height, depth } = dimensions;
  const count = temperatureData.length * temperatureData[0]?.length || 0;
  
  useEffect(() => {
    if (!meshRef.current) return;
    
    const tempArray = temperatureData.flat();
    const minTemp = Math.min(...tempArray);
    const maxTemp = Math.max(...tempArray);
    
    const matrix = new THREE.Matrix4();
    const color = new THREE.Color();
    
    for (let i = 0; i < count; i++) {
      const x = (i % temperatureData[0].length) * (width / temperatureData[0].length);
      const z = Math.floor(i / temperatureData[0].length) * (depth / temperatureData.length);
      const temp = tempArray[i];
      
      // –ù–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä—ã –¥–ª—è —Ü–≤–µ—Ç–∞
      const normalized = (temp - minTemp) / (maxTemp - minTemp);
      
      // –¶–≤–µ—Ç–æ–≤–∞—è –≥—Ä–∞–¥–∞—Ü–∏—è –æ—Ç —Å–∏–Ω–µ–≥–æ –∫ –∫—Ä–∞—Å–Ω–æ–º—É
      color.setRGB(
        normalized, // –∫—Ä–∞—Å–Ω—ã–π
        Math.max(0, 0.5 - Math.abs(normalized - 0.5)), // –∑–µ–ª–µ–Ω—ã–π
        1 - normalized // —Å–∏–Ω–∏–π
      );
      
      matrix.setPosition(x - width/2, 0, z - depth/2);
      meshRef.current.setMatrixAt(i, matrix);
      meshRef.current.setColorAt(i, color);
    }
    
    if (meshRef.current.instanceMatrix) meshRef.current.instanceMatrix.needsUpdate = true;
    if (meshRef.current.instanceColor) meshRef.current.instanceColor.needsUpdate = true;
  }, [temperatureData, dimensions, count]);
  
  return (
    <instancedMesh ref={meshRef} args={[undefined, undefined, count]}>
      <boxGeometry args={[width / temperatureData[0].length, 0.1, depth / temperatureData.length]} />
      <meshStandardMaterial />
    </instancedMesh>
  );
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ —Ç–µ—á–µ–Ω–∏—è —Å—Ç–µ–∫–ª–æ–º–∞—Å—Å—ã
const GlassFlow = ({ 
  viscosity, 
  temperature, 
  flowRate 
}: { 
  viscosity: number; 
  temperature: number; 
  flowRate: number;
}) => {
  const groupRef = useRef<THREE.Group>(null);
  const particlesRef = useRef<THREE.Points>(null);
  const [particles, setParticles] = useState<Float32Array>();
  
  useEffect(() => {
    // –°–æ–∑–¥–∞–Ω–∏–µ —á–∞—Å—Ç–∏—Ü –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ –ø–æ—Ç–æ–∫–∞
    const count = 1000;
    const positions = new Float32Array(count * 3);
    
    for (let i = 0; i < count; i++) {
      positions[i * 3] = (Math.random() - 0.5) * 10;
      positions[i * 3 + 1] = Math.random() * 2;
      positions[i * 3 + 2] = (Math.random() - 0.5) * 3;
    }
    
    setParticles(positions);
  }, []);
  
  useFrame((state) => {
    if (groupRef.current) {
      // –ê–Ω–∏–º–∞—Ü–∏—è —Ç–µ—á–µ–Ω–∏—è –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç –≤—è–∑–∫–æ—Å—Ç–∏ –∏ —Å–∫–æ—Ä–æ—Å—Ç–∏ –ø–æ—Ç–æ–∫–∞
      const speed = flowRate / (viscosity + 1) * 0.1;
      groupRef.current.position.x += Math.sin(state.clock.elapsedTime) * speed * 0.1;
      groupRef.current.rotation.y = Math.sin(state.clock.elapsedTime * 0.5) * 0.1;
    }
  });
  
  // –¶–≤–µ—Ç –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä—ã
  const tempColor = new THREE.Color();
  const normalizedTemp = (temperature - 1400) / 400;
  tempColor.setRGB(
    Math.min(1, normalizedTemp * 2),
    Math.max(0, 0.5 - Math.abs(normalizedTemp - 0.5)),
    Math.max(0, 1 - normalizedTemp * 1.5)
  );
  
  return (
    <group ref={groupRef}>
      {/* –û—Å–Ω–æ–≤–Ω–æ–π –ø–æ—Ç–æ–∫ —Å—Ç–µ–∫–ª–æ–º–∞—Å—Å—ã */}
      <DreiBox args={[8, 1, 3]} position={[0, 0.5, 0]}>
        <meshStandardMaterial 
          color={tempColor}
          transparent
          opacity={0.7}
          roughness={0.3}
          metalness={0.1}
        />
      </DreiBox>
      
      {/* –ß–∞—Å—Ç–∏—Ü—ã –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ —Ç–µ—á–µ–Ω–∏—è */}
      {particles && (
        <points ref={particlesRef}>
          <bufferGeometry>
            <bufferAttribute
              attach="attributes-position"
              count={particles.length / 3}
              array={particles}
              itemSize={3}
            />
          </bufferGeometry>
          <pointsMaterial
            size={0.05}
            color={tempColor}
            transparent
            opacity={0.6}
          />
        </points>
      )}
      
      {/* –í–∏—Ö—Ä–µ–≤—ã–µ –ø–æ—Ç–æ–∫–∏ */}
      {Array.from({ length: 3 }).map((_, i) => (
        <mesh key={i} position={[(i - 1) * 2, 0.6, 0]} rotation={[Math.PI / 2, 0, 0]}>
          <torusGeometry args={[0.3, 0.1, 16, 100]} />
          <meshStandardMaterial 
            color={MIREA_COLORS.info}
            emissive={MIREA_COLORS.info}
            emissiveIntensity={0.3}
            transparent
            opacity={0.5}
          />
        </mesh>
      ))}
    </group>
  );
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–π
const StressVisualization = ({ stressData }: { stressData: number[][] }) => {
  const linesRef = useRef<THREE.Group>(null);
  
  useFrame((state) => {
    if (linesRef.current) {
      linesRef.current.rotation.y += 0.001;
    }
  });
  
  return (
    <group ref={linesRef}>
      {stressData.map((row, i) => 
        row.map((stress, j) => {
          const intensity = Math.min(1, stress / 100);
          const length = 0.1 + intensity * 0.5;
          
          return (
            <Line
              key={`${i}-${j}`}
              points={[
                [j - row.length/2, 0, i - stressData.length/2],
                [j - row.length/2, length, i - stressData.length/2]
              ]}
              color={intensity > 0.7 ? MIREA_COLORS.error : MIREA_COLORS.warning}
              lineWidth={2}
            />
          );
        })
      )}
    </group>
  );
};

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –¥–µ—Ñ–µ–∫—Ç–æ–≤ —Å –ø–æ–¥—Ä–æ–±–Ω–æ–π –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π
const EnhancedDefectVisualization = ({ defects, position }: {
  defects: Record<string, number>;
  position: [number, number, number];
}) => {
  const groupRef = useRef<THREE.Group>(null);
  
  useFrame((state) => {
    if (groupRef.current) {
      groupRef.current.rotation.y += 0.005;
    }
  });
  
  return (
    <group ref={groupRef} position={position}>
      {Object.entries(defects).map(([type, severity], idx) => {
        const defectInfo = DEFECT_TYPES[type as keyof typeof DEFECT_TYPES];
        if (!defectInfo || severity < 0.1) return null;
        
        const angle = (idx / Object.keys(defects).length) * Math.PI * 2;
        const radius = 1 + severity * 2;
        const x = Math.cos(angle) * radius;
        const z = Math.sin(angle) * radius;
        const y = Math.sin(state.clock.elapsedTime * 2 + idx) * 0.5;
        
        return (
          <React.Fragment key={type}>
            <Sphere position={[x, y, z]} args={[0.05 + severity * 0.15]}>
              <meshStandardMaterial 
                color={defectInfo.color}
                emissive={defectInfo.color}
                emissiveIntensity={severity * 0.8}
                transparent
                opacity={0.7}
              />
            </Sphere>
            
            {/* –°–æ–µ–¥–∏–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –ª–∏–Ω–∏–∏ */}
            <Line
              points={[[0, 0, 0], [x, y, z]]}
              color={defectInfo.color}
              lineWidth={1}
              transparent
              opacity={0.3}
            />
            
            {/* –¢–µ–∫—Å—Ç —Å –æ–ø–∏—Å–∞–Ω–∏–µ–º –¥–µ—Ñ–µ–∫—Ç–∞ */}
            <Html position={[x, y + 0.3, z]} center>
              <Box sx={{
                bgcolor: 'rgba(0,0,0,0.8)',
                backdropFilter: 'blur(10px)',
                borderRadius: 1,
                p: 1,
                minWidth: 120,
                border: `1px solid ${defectInfo.color}`
              }}>
                <Typography variant="caption" sx={{ color: 'white', display: 'block' }}>
                  <strong>{defectInfo.icon} {defectInfo.name}</strong>
                </Typography>
                <Typography variant="caption" sx={{ color: MIREA_COLORS.textSecondary, display: 'block' }}>
                  –í–µ—Ä–æ—è—Ç–Ω–æ—Å—Ç—å: {(severity * 100).toFixed(1)}%
                </Typography>
              </Box>
            </Html>
          </React.Fragment>
        );
      })}
    </group>
  );
};

// –û—Å–Ω–æ–≤–Ω–∞—è 3D —Å—Ü–µ–Ω–∞ —Å —É–ª—É—á—à–µ–Ω–Ω–æ–π –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–µ–π
const EnhancedDigitalTwinScene = ({ 
  furnaceTemp, 
  moldTemp, 
  beltSpeed, 
  defects,
  viewMode = 'temperature',
  temperatureData,
  stressData,
  viscosity
}: { 
  furnaceTemp: number; 
  moldTemp: number; 
  beltSpeed: number; 
  defects: Record<string, number>;
  viewMode: string;
  temperatureData: number[][];
  stressData: number[][];
  viscosity: number;
}) => {
  const [showTemperatureField, setShowTemperatureField] = useState(true);
  const [showStressField, setShowStressField] = useState(false);
  
  return (
    <>
      {/* –û—Å–≤–µ—â–µ–Ω–∏–µ */}
      <ambientLight intensity={0.4} />
      <pointLight position={[10, 10, 10]} intensity={1.2} castShadow />
      <pointLight position={[-10, 5, -10]} intensity={0.6} color={MIREA_COLORS.info} />
      <spotLight
        position={[0, 15, 0]}
        angle={0.4}
        penumbra={1}
        intensity={1.5}
        castShadow
        shadow-mapSize-width={4096}
        shadow-mapSize-height={4096}
      />
      
      {/* –ü–æ–ª */}
      <Plane 
        rotation={[-Math.PI / 2, 0, 0]} 
        position={[0, -0.5, 0]} 
        args={[40, 40]} 
        receiveShadow
      >
        <meshStandardMaterial 
          color={MIREA_COLORS.backgroundLight}
          roughness={0.8}
          metalness={0.2}
        />
      </Plane>
      
      {/* –ü–µ—á—å —Å —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω—ã–º –ø–æ–ª–µ–º */}
      <group position={[-8, 1.5, 0]}>
        <DreiBox args={[4, 3, 4]} castShadow receiveShadow>
          <meshStandardMaterial 
            color={MIREA_COLORS.secondary}
            emissive={MIREA_COLORS.secondary}
            emissiveIntensity={Math.min(1, (furnaceTemp - 1400) / 200) * 0.3}
            roughness={0.4}
            metalness={0.7}
          />
        </DreiBox>
        
        {showTemperatureField && temperatureData && (
          <TemperatureField 
            temperatureData={temperatureData}
            dimensions={{ width: 3, height: 2, depth: 3 }}
          />
        )}
        
        <Text
          position={[0, 3.5, 0]}
          color="white"
          fontSize={0.5}
          anchorX="center"
          anchorY="middle"
          outlineWidth={0.1}
          outlineColor="black"
        >
          {furnaceTemp.toFixed(0)}¬∞C
        </Text>
      </group>
      
      {/* –ü–æ—Ç–æ–∫ —Å—Ç–µ–∫–ª–æ–º–∞—Å—Å—ã */}
      <GlassFlow 
        viscosity={viscosity}
        temperature={furnaceTemp}
        flowRate={beltSpeed}
      />
      
      {/* –õ–∏–Ω–∏—è —Ñ–æ—Ä–º–æ–≤–∞–Ω–∏—è */}
      <group position={[8, 0.5, 0]}>
        <DreiBox position={[0, 0, 0]} args={[10, 0.1, 2]} castShadow receiveShadow>
          <meshStandardMaterial color={MIREA_COLORS.info} />
        </DreiBox>
        
        {Array.from({ length: 3 }).map((_, i) => (
          <group key={i} position={[-4 + i * 4, 0.6, 0]}>
            <Cylinder args={[0.4, 0.4, 1]} castShadow>
              <meshStandardMaterial 
                color={moldTemp > 350 ? MIREA_COLORS.warning : MIREA_COLORS.info}
                emissive={moldTemp > 350 ? MIREA_COLORS.warning : MIREA_COLORS.info}
                emissiveIntensity={moldTemp > 350 ? 0.4 : 0.1}
              />
            </Cylinder>
            <Text
              position={[0, 1.8, 0]}
              color="white"
              fontSize={0.3}
              anchorX="center"
              anchorY="middle"
            >
              {moldTemp.toFixed(0)}¬∞C
            </Text>
          </group>
        ))}
      </group>
      
      {/* –í–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏—è –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–π */}
      {showStressField && stressData && (
        <StressVisualization stressData={stressData} />
      )}
      
      {/* –í–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏—è –¥–µ—Ñ–µ–∫—Ç–æ–≤ */}
      <EnhancedDefectVisualization 
        defects={defects}
        position={[0, 3, 0]}
      />
      
      {/* –û—Å–∏ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç */}
      <Line
        points={[[-10, 0, 0], [10, 0, 0]]}
        color={MIREA_COLORS.error}
        lineWidth={1}
      />
      <Line
        points={[[0, -1, 0], [0, 10, 0]]}
        color={MIREA_COLORS.success}
        lineWidth={1}
      />
      <Line
        points={[[0, 0, -10], [0, 0, 10]]}
        color={MIREA_COLORS.info}
        lineWidth={1}
      />
      
      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        maxPolarAngle={Math.PI / 2}
        minDistance={5}
        maxDistance={50}
      />
    </>
  );
};

// –ì–ª–∞–≤–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç —Ä–∞—Å—à–∏—Ä–µ–Ω–Ω–æ–≥–æ —Ü–∏—Ñ—Ä–æ–≤–æ–≥–æ –¥–≤–æ–π–Ω–∏–∫–∞
const DigitalTwinEnhanced = () => {
  // WebSocket —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ
  const { wsData, isConnected } = useWebSocketStream();
  
  // –°–æ—Å—Ç–æ—è–Ω–∏—è —Ä–µ–∞–ª—å–Ω–æ–≥–æ –ø—Ä–æ–∏–∑–≤–æ–¥—Å—Ç–≤–∞
  const [realState, setRealState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    defects: { crack: 0, bubble: 0, chip: 0, deformation: 0, cloudiness: 0, stress: 0 },
    viscosity: 1200,
    pressure: 45,
    quality: 95.2
  });
  
  // –ü—Ä–æ–≥–Ω–æ–∑–∏—Ä—É–µ–º–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
  const [predictedState, setPredictedState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    defects: { crack: 0, bubble: 0, chip: 0, deformation: 0, cloudiness: 0, stress: 0 },
    viscosity: 1200,
    quality: 95.2
  });
  
  // What-If —Å–æ—Å—Ç–æ—è–Ω–∏–µ
  const [whatIfState, setWhatIfState] = useState({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    coolingRate: 3.5,
    pressure: 45,
    annealingTemp: 580
  });
  
  // What-If —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã
  const [whatIfResults, setWhatIfResults] = useState({
    defects: { crack: 0, bubble: 0, chip: 0, deformation: 0, cloudiness: 0, stress: 0 },
    quality: 95.2,
    viscosity: 1200,
    stressLevel: 35
  });
  
  // UI —Å–æ—Å—Ç–æ—è–Ω–∏—è
  const [shadowMode, setShadowMode] = useState(true);
  const [whatIfMode, setWhatIfMode] = useState(false);
  const [isSimulating, setIsSimulating] = useState(false);
  const [viewMode, setViewMode] = useState('temperature');
  const [activeTab, setActiveTab] = useState(0);
  const [showTemperatureField, setShowTemperatureField] = useState(true);
  const [showStressField, setShowStressField] = useState(false);
  const [showDefects, setShowDefects] = useState(true);
  
  // –î–∞–Ω–Ω—ã–µ –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏
  const [temperatureData, setTemperatureData] = useState<number[][]>([]);
  const [stressData, setStressData] = useState<number[][]>([]);
  
  // –†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏ –∏ –ø—Ä–∏—á–∏–Ω—ã –¥–µ—Ñ–µ–∫—Ç–æ–≤
  const [recommendations, setRecommendations] = useState<any[]>([]);
  const [defectCauses, setDefectCauses] = useState<Record<string, any[]>>({});
  
  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –¥–∞–Ω–Ω—ã—Ö –¥–ª—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–∏
  useEffect(() => {
    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω–æ–≥–æ –ø–æ–ª—è
    const tempField = Array.from({ length: 20 }, (_, i) => 
      Array.from({ length: 10 }, (_, j) => {
        const centerDist = Math.sqrt(Math.pow(i - 10, 2) + Math.pow(j - 5, 2));
        return realState.furnaceTemp - 200 * (centerDist / 14);
      })
    );
    setTemperatureData(tempField);
    
    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –ø–æ–ª—è –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–π
    const stressField = Array.from({ length: 15 }, (_, i) =>
      Array.from({ length: 15 }, (_, j) => {
        const x = (i - 7.5) / 7.5;
        const y = (j - 7.5) / 7.5;
        return Math.exp(-(x * x + y * y)) * 50 + Math.random() * 20;
      })
    );
    setStressData(stressField);
  }, [realState]);
  
  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ —Ä–µ–∞–ª—å–Ω–æ–≥–æ —Å–æ—Å—Ç–æ—è–Ω–∏—è –∏–∑ WebSocket
  useEffect(() => {
    if (wsData.parameters) {
      const newState = {
        furnaceTemp: wsData.parameters.furnace?.temperature || 1520,
        moldTemp: wsData.parameters.forming?.mold_temp || 320,
        beltSpeed: wsData.parameters.forming?.speed || 150,
        defects: wsData.defectAggregation || { crack: 0, bubble: 0, chip: 0 },
        viscosity: 1200,
        pressure: wsData.parameters.forming?.pressure || 45,
        quality: wsData.qualityMetrics?.qualityRate || 95.2
      };
      setRealState(newState);
      
      // –°–∏–º—É–ª—è—Ü–∏—è –ø—Ä–æ–≥–Ω–æ–∑–∞
      setTimeout(() => {
        setPredictedState({
          ...newState,
          furnaceTemp: newState.furnaceTemp + (Math.random() - 0.5) * 10,
          moldTemp: newState.moldTemp + (Math.random() - 0.5) * 5,
          beltSpeed: newState.beltSpeed + (Math.random() - 0.5) * 3,
          quality: newState.quality + (Math.random() - 0.5) * 2
        });
      }, 1000);
    }
  }, [wsData]);
  
  // –ü–æ–ª—É—á–µ–Ω–∏–µ –ø—Ä–∏—á–∏–Ω –¥–µ—Ñ–µ–∫—Ç–æ–≤
  const fetchDefectCauses = async () => {
    const causes: Record<string, any[]> = {};
    for (const defectType of Object.keys(realState.defects)) {
      try {
        const response = await knowledgeGraphApi.getCausesOfDefect(defectType);
        causes[defectType] = response;
      } catch (error) {
        console.error(`Error fetching causes for ${defectType}:`, error);
      }
    }
    setDefectCauses(causes);
  };
  
  // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–π
  const fetchRecommendations = async () => {
    try {
      const response = await knowledgeGraphApi.getRecommendations('crack', {
        furnace_temperature: realState.furnaceTemp,
        belt_speed: realState.beltSpeed,
        mold_temperature: realState.moldTemp
      });
      setRecommendations(response);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };
  
  // –ó–∞–ø—É—Å–∫ What-If —Å–∏–º—É–ª—è—Ü–∏–∏
  const runWhatIfSimulation = async () => {
    setIsSimulating(true);
    
    // –§–∏–∑–∏—á–µ—Å–∫–∞—è –º–æ–¥–µ–ª—å —Ä–∞—Å—á–µ—Ç–∞ –¥–µ—Ñ–µ–∫—Ç–æ–≤
    const tempDeviation = Math.abs(whatIfState.furnaceTemp - 1520) / 50;
    const speedDeviation = Math.abs(whatIfState.beltSpeed - 150) / 20;
    const moldDeviation = Math.abs(whatIfState.moldTemp - 320) / 30;
    const coolingDeviation = Math.abs(whatIfState.coolingRate - 3.5) / 2;
    const pressureDeviation = Math.abs(whatIfState.pressure - 45) / 15;
    
    const crackProbability = Math.min(tempDeviation * 8 + speedDeviation * 3 + coolingDeviation * 5, 30);
    const bubbleProbability = Math.min(tempDeviation * 4 + moldDeviation * 6 + pressureDeviation * 3, 25);
    const chipProbability = Math.min(speedDeviation * 5 + moldDeviation * 3 + coolingDeviation * 2, 20);
    const deformationProbability = Math.min(tempDeviation * 3 + speedDeviation * 4 + moldDeviation * 2, 18);
    const cloudinessProbability = Math.min(tempDeviation * 5 + coolingDeviation * 4, 15);
    const stressProbability = Math.min(tempDeviation * 6 + coolingDeviation * 7, 35);
    
    const quality = Math.max(70, 95 - crackProbability - bubbleProbability * 0.7 - chipProbability * 0.5);
    const viscosity = 1200 * (1 + tempDeviation * 0.3 - speedDeviation * 0.2);
    
    setWhatIfResults({
      defects: {
        crack: Math.round(crackProbability),
        bubble: Math.round(bubbleProbability),
        chip: Math.round(chipProbability),
        deformation: Math.round(deformationProbability),
        cloudiness: Math.round(cloudinessProbability),
        stress: Math.round(stressProbability)
      },
      quality: Math.round(quality * 10) / 10,
      viscosity: Math.round(viscosity),
      stressLevel: Math.round(stressProbability)
    });
    
    // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–π –¥–ª—è –Ω–æ–≤—ã—Ö –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
    try {
      const response = await knowledgeGraphApi.getRecommendations('crack', {
        furnace_temperature: whatIfState.furnaceTemp,
        belt_speed: whatIfState.beltSpeed,
        mold_temperature: whatIfState.moldTemp,
        cooling_rate: whatIfState.coolingRate,
        pressure: whatIfState.pressure
      });
      setRecommendations(response);
    } catch (error) {
      console.error('Error fetching what-if recommendations:', error);
    }
    
    setTimeout(() => setIsSimulating(false), 1000);
  };
  
  // –°–±—Ä–æ—Å What-If –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
  const resetWhatIf = () => {
    setWhatIfState({
      furnaceTemp: realState.furnaceTemp,
      moldTemp: realState.moldTemp,
      beltSpeed: realState.beltSpeed,
      coolingRate: 3.5,
      pressure: 45,
      annealingTemp: 580
    });
  };
  
  // –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –æ—Ç–æ–±—Ä–∞–∂–∞–µ–º–æ–≥–æ —Å–æ—Å—Ç–æ—è–Ω–∏—è
  const displayState = whatIfMode ? 
    { 
      ...whatIfState, 
      defects: whatIfResults.defects,
      viscosity: whatIfResults.viscosity,
      quality: whatIfResults.quality
    } : 
    (shadowMode ? predictedState : realState);
  
  return (
    <Box sx={{ width: '100%', height: '100vh', overflow: 'hidden' }}>
      {/* –í–µ—Ä—Ö–Ω—è—è –ø–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
      <Paper elevation={4} sx={{ 
        p: 2, 
        mb: 2, 
        background: GRADIENTS.background,
        borderRadius: 2,
        border: `1px solid ${MIREA_COLORS.divider}`
      }}>
        <Grid container alignItems="center" spacing={2}>
          <Grid item xs={12} md={6}>
            <Stack direction="row" spacing={2} alignItems="center">
              <Avatar sx={{ bgcolor: MIREA_COLORS.primary, width: 40, height: 40 }}>
                <Build />
              </Avatar>
              <Box>
                <Typography variant="h6" sx={{ 
                  color: MIREA_COLORS.text,
                  fontWeight: 700,
                  background: GRADIENTS.primary,
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent'
                }}>
                  –¶–∏—Ñ—Ä–æ–≤–æ–π –¥–≤–æ–π–Ω–∏–∫ –ø—Ä–æ–∏–∑–≤–æ–¥—Å—Ç–≤–∞ —Å—Ç–µ–∫–ª–∞
                </Typography>
                <Typography variant="caption" sx={{ color: MIREA_COLORS.textSecondary }}>
                  –†–¢–£ –ú–ò–†–≠–ê ‚Ä¢ –ò–Ω—Ç–µ–ª–ª–µ–∫—Ç—É–∞–ª—å–Ω–∞—è —Å–∏—Å—Ç–µ–º–∞ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –∫–∞—á–µ—Å—Ç–≤–æ–º
                </Typography>
              </Box>
            </Stack>
          </Grid>
          
          <Grid item xs={12} md={6}>
            <Stack direction="row" spacing={1} justifyContent="flex-end" alignItems="center">
              <Chip
                icon={<Hub />}
                label={isConnected ? '–ü–æ–¥–∫–ª—é—á–µ–Ω–æ' : '–û—Ç–∫–ª—é—á–µ–Ω–æ'}
                color={isConnected ? 'success' : 'error'}
                sx={{ fontWeight: 700 }}
              />
              
              <ButtonGroup variant="contained" size="small">
                <Button
                  onClick={() => {
                    setShadowMode(true);
                    setWhatIfMode(false);
                  }}
                  sx={{ 
                    bgcolor: shadowMode ? MIREA_COLORS.primary : 'inherit',
                    fontWeight: 700
                  }}
                >
                  <CompareArrows sx={{ mr: 1 }} />
                  Shadow Mode
                </Button>
                <Button
                  onClick={() => {
                    setWhatIfMode(true);
                    setShadowMode(false);
                  }}
                  sx={{ 
                    bgcolor: whatIfMode ? MIREA_COLORS.secondary : 'inherit',
                    fontWeight: 700
                  }}
                >
                  <Science sx={{ mr: 1 }} />
                  What-If
                </Button>
              </ButtonGroup>
              
              <IconButton onClick={() => fetchRecommendations()} sx={{ color: MIREA_COLORS.info }}>
                <Lightbulb />
              </IconButton>
            </Stack>
          </Grid>
        </Grid>
      </Paper>
      
      <Grid container spacing={2} sx={{ height: 'calc(100vh - 120px)' }}>
        {/* –û—Å–Ω–æ–≤–Ω–∞—è 3D –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏—è */}
        <Grid item xs={12} lg={whatIfMode ? 8 : 9}>
          <Paper elevation={4} sx={{ 
            height: '100%',
            position: 'relative',
            overflow: 'hidden',
            border: `2px solid ${whatIfMode ? MIREA_COLORS.secondary : MIREA_COLORS.primary}`,
            borderRadius: 2,
            background: GRADIENTS.background
          }}>
            <Canvas
              shadows
              camera={{ position: [15, 8, 15], fov: 50 }}
              style={{ background: 'transparent' }}
            >
              <EnhancedDigitalTwinScene 
                furnaceTemp={displayState.furnaceTemp}
                moldTemp={displayState.moldTemp}
                beltSpeed={displayState.beltSpeed}
                defects={displayState.defects}
                viewMode={viewMode}
                temperatureData={temperatureData}
                stressData={stressData}
                viscosity={displayState.viscosity}
              />
            </Canvas>
            
            {/* –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –≤–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏–µ–π */}
            <Paper elevation={4} sx={{
              position: 'absolute',
              top: 16,
              right: 16,
              background: 'rgba(0, 0, 0, 0.7)',
              backdropFilter: 'blur(10px)',
              p: 2,
              borderRadius: 2,
              border: `1px solid ${MIREA_COLORS.divider}`
            }}>
              <Stack spacing={1}>
                <FormControlLabel
                  control={
                    <Switch 
                      checked={showTemperatureField}
                      onChange={(e) => setShowTemperatureField(e.target.checked)}
                      size="small"
                      color="primary"
                    />
                  }
                  label={
                    <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
                      <Thermostat fontSize="small" sx={{ mr: 0.5 }} />
                      –¢–µ–º–ø. –ø–æ–ª–µ
                    </Typography>
                  }
                />
                <FormControlLabel
                  control={
                    <Switch 
                      checked={showStressField}
                      onChange={(e) => setShowStressField(e.target.checked)}
                      size="small"
                      color="warning"
                    />
                  }
                  label={
                    <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
                      <Compress fontSize="small" sx={{ mr: 0.5 }} />
                      –ù–∞–ø—Ä—è–∂–µ–Ω–∏—è
                    </Typography>
                  }
                />
                <FormControlLabel
                  control={
                    <Switch 
                      checked={showDefects}
                      onChange={(e) => setShowDefects(e.target.checked)}
                      size="small"
                      color="error"
                    />
                  }
                  label={
                    <Typography variant="caption" sx={{ color: MIREA_COLORS.text }}>
                      <Warning fontSize="small" sx={{ mr: 0.5 }} />
                      –î–µ—Ñ–µ–∫—Ç—ã
                    </Typography>
                  }
                />
              </Stack>
            </Paper>
            
            {/* –ü–∞–Ω–µ–ª—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –∫–∞—á–µ—Å—Ç–≤–µ */}
            <Paper elevation={4} sx={{
              position: 'absolute',
              bottom: 16,
              left: 16,
              background: 'rgba(0, 0, 0, 0.8)',
              backdropFilter: 'blur(10px)',
              p: 2,
              borderRadius: 2,
              border: `1px solid ${MIREA_COLORS.divider}`,
              minWidth: 200
            }}>
              <Typography variant="caption" sx={{ 
                color: MIREA_COLORS.text, 
                fontWeight: 700,
                display: 'block',
                mb: 1
              }}>
                üìä –ö–∞—á–µ—Å—Ç–≤–æ –ø—Ä–æ–¥—É–∫—Ü–∏–∏
              </Typography>
              
              <Stack spacing={1}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Typography variant="caption" sx={{ color: MIREA_COLORS.textSecondary }}>
                    –¢–µ–∫—É—â–µ–µ:
                  </Typography>
                  <Chip
                    label={`${displayState.quality}%`}
                    size="small"
                    sx={{
                      bgcolor: displayState.quality > 90 ? MIREA_COLORS.success + '20' : 
                              displayState.quality > 80 ? MIREA_COLORS.warning + '20' : 
                              MIREA_COLORS.error + '20',
                      color: displayState.quality > 90 ? MIREA_COLORS.success : 
                             displayState.quality > 80 ? MIREA_COLORS.warning : 
                             MIREA_COLORS.error,
                      fontWeight: 700
                    }}
                  />
                </Box>
                
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Typography variant="caption" sx={{ color: MIREA_COLORS.textSecondary }}>
                    –í—è–∑–∫–æ—Å—Ç—å:
                  </Typography>
                  <Typography variant="caption" sx={{ 
                    color: displayState.viscosity > 1500 ? MIREA_COLORS.warning : 
                           displayState.viscosity < 800 ? MIREA_COLORS.error : 
                           MIREA_COLORS.success,
                    fontWeight: 700
                  }}>
                    {displayState.viscosity} –ü–∞¬∑—Å
                  </Typography>
                </Box>
                
                <LinearProgress 
                  variant="determinate" 
                  value={displayState.quality} 
                  sx={{ 
                    height: 6,
                    borderRadius: 1,
                    bgcolor: MIREA_COLORS.backgroundLighter,
                    '& .MuiLinearProgress-bar': {
                      bgcolor: displayState.quality > 90 ? MIREA_COLORS.success : 
                               displayState.quality > 80 ? MIREA_COLORS.warning : 
                               MIREA_COLORS.error
                    }
                  }}
                />
              </Stack>
            </Paper>
            
            {/* –ü–∞–Ω–µ–ª—å —Ä–µ–∂–∏–º–∞ —Ä–∞–±–æ—Ç—ã */}
            {shadowMode && (
              <Paper elevation={4} sx={{
                position: 'absolute',
                top: 16,
                left: 16,
                background: 'rgba(0, 102, 255, 0.15)',
                backdropFilter: 'blur(10px)',
                p: 2,
                borderRadius: 2,
                border: `1px solid ${MIREA_COLORS.primary}`,
                maxWidth: 300
              }}>
                <Typography variant="caption" sx={{ 
                  color: MIREA_COLORS.text, 
                  fontWeight: 700,
                  display: 'block',
