// @ts-nocheck
import React, { useRef, useState, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Text, Box as DreiBox, Sphere, Cylinder, Plane, Line, Html } from '@react-three/drei';
import * as THREE from 'three';
import { 
  Box, Chip, Paper, Typography, Slider, Button, 
  Grid, Card, CardContent, Switch, FormControlLabel, Divider,
  TextField, Stack, LinearProgress, Tooltip, IconButton,
  Tabs, Tab, Badge, Avatar, List, ListItem, ListItemText,
  Accordion, AccordionSummary, AccordionDetails
} from '@mui/material';
import { 
  PlayArrow, Pause, Refresh, CompareArrows, TrendingUp, Science,
  Warning, Error, CheckCircle, Thermostat, Speed, Opacity,
  Timeline, BubbleChart, Landscape, Settings, DataArray,
  ExpandMore, WaterDrop, Report, HeatPump, Factory,
  LineAxis, ScatterPlot, ShowChart, Animation, LiveTv,
  Dashboard, Analytics, Psychology, AutoFixHigh, PrecisionManufacturing
} from '@mui/icons-material';
import { systemApi, knowledgeGraphApi } from '../services/api';
import { useWebSocketStream } from '../hooks/useWebSocketStream';

// Цветовая палитра РТУ МИРЭА
const MIREA_COLORS = {
  primary: '#0066CC',
  secondary: '#FF6B35',
  success: '#00C853',
  warning: '#FFA726',
  error: '#EF5350',
  info: '#29B6F6',
  background: '#0A1929',
  backgroundLight: '#1A2332',
  text: '#FFFFFF',
  glassHot: '#FF3300',
  glassWarm: '#FF9900',
  glassCool: '#0099FF',
  glassCold: '#0066CC'
};

// Интерфейсы
interface ProductionState {
  furnaceTemp: number;
  moldTemp: number;
  beltSpeed: number;
  viscosity: number;
  flowRate: number;
  defects: Record<string, number>;
  quality: number;
  efficiency?: number;
  energyConsumption?: number;
  pressure?: number;
  humidity?: number;
  coolingRate?: number;
}

interface WhatIfState {
  furnaceTemp: number;
  moldTemp: number;
  beltSpeed: number;
  viscosity: number;
  flowRate: number;
  pressure?: number;
  coolingRate?: number;
}

interface SimulationResults {
  predictedDefects: Record<string, number>;
  quality: number;
  efficiency: number;
  energyImpact: number;
  riskLevel: string;
  recommendations: string[];
  confidence: number;
}

// Компонент печи
const EnhancedFurnace = ({ temperature, meltLevel = 0.85, efficiency = 0.92 }) => {
  const furnaceRef = useRef<THREE.Group>(null);
  const [heatWave, setHeatWave] = useState(0);
  
  useFrame((state) => {
    if (furnaceRef.current) {
      const pulse = Math.sin(state.clock.elapsedTime * 2) * 0.5 + 0.5;
      setHeatWave(pulse);
      furnaceRef.current.rotation.y = Math.sin(state.clock.elapsedTime * 0.1) * 0.1;
    }
  });

  const getTemperatureColor = (temp: number) => {
    if (temp > 1650) return '#ff3300';
    if (temp > 1550) return '#ff6600';
    if (temp > 1450) return '#ff9900';
    if (temp > 1350) return '#ffcc00';
    return '#ffeda6';
  };

  const furnaceColor = getTemperatureColor(temperature);

  return (
    <group ref={furnaceRef} position={[-10, 2, 0]}>
      <DreiBox args={[8, 5, 6]} position={[0, 2.5, 0]} castShadow receiveShadow>
        <meshStandardMaterial 
          color="#2c2c2c"
          metalness={0.9}
          roughness={0.3}
          emissive="#444444"
          emissiveIntensity={0.1}
        />
      </DreiBox>

      <Sphere args={[4.5]} position={[0, 2.5, 0]}>
        <meshBasicMaterial
          color={furnaceColor}
          transparent
          opacity={0.15 + heatWave * 0.1}
          side={THREE.BackSide}
        />
      </Sphere>

      <DreiBox args={[7, 4, 5]} position={[0, 2.5, 0]}>
        <meshStandardMaterial
          color="#1a1a1a"
          metalness={0.8}
          roughness={0.4}
        />
      </DreiBox>

      <DreiBox 
        args={[6.5, meltLevel * 3.5, 4.5]} 
        position={[0, 1 + meltLevel * 2, 0]}
        castShadow
      >
        <meshStandardMaterial
          color={furnaceColor}
          emissive={furnaceColor}
          emissiveIntensity={0.3 + heatWave * 0.2}
          transparent
          opacity={0.85}
          roughness={0.2}
          metalness={0.7}
        />
      </DreiBox>

      {Array.from({ length: 4 }).map((_, i) => (
        <Cylinder
          key={i}
          position={[-3 + i * 2, 5.5, 2]}
          args={[0.3, 0.3, 1]}
          castShadow
        >
          <meshStandardMaterial
            color="#ff3300"
            emissive="#ff3300"
            emissiveIntensity={0.9}
          />
        </Cylinder>
      ))}

      <Html position={[0, 7, 0]} center>
        <Card sx={{
          bgcolor: 'rgba(0, 0, 0, 0.95)',
          backdropFilter: 'blur(20px)',
          border: `2px solid ${temperature > 1600 ? '#ff3300' : '#ff9900'}`,
          minWidth: 200,
          transform: 'scale(0.8)',
        }}>
          <CardContent sx={{ p: 2 }}>
            <Stack spacing={1} alignItems="center">
              <Typography variant="h6" sx={{ color: '#fff', fontWeight: 700 }}>
                {temperature.toFixed(0)}°C
              </Typography>
              <Typography variant="caption" sx={{ color: '#aaa' }}>
                Эффективность: {(efficiency * 100).toFixed(1)}%
              </Typography>
              <LinearProgress 
                variant="determinate" 
                value={Math.min(100, (temperature - 1300) / 5)}
                sx={{
                  width: '100%',
                  height: 6,
                  borderRadius: 3,
                  bgcolor: 'rgba(255,255,255,0.1)',
                  '& .MuiLinearProgress-bar': {
                    bgcolor: furnaceColor,
                  }
                }}
              />
            </Stack>
          </CardContent>
        </Card>
      </Html>
    </group>
  );
};

// Линия формования
const SmartFormingLine = ({ speed, moldTemp, quality = 0.95, defectLevel = 0.1 }) => {
  const beltRef = useRef<THREE.Group>(null);
  const [glassProducts, setGlassProducts] = useState<
    Array<{id: number, position: number, quality: number, stage: number}>
  >([]);
  const [beltOffset, setBeltOffset] = useState(0);

  useFrame(() => {
    if (beltRef.current) {
      const newOffset = (beltOffset + speed * 0.005) % 12;
      setBeltOffset(newOffset);
      beltRef.current.position.z = newOffset;

      setGlassProducts(prev => 
        prev.map(product => ({
          ...product,
          position: (product.position + speed * 0.005) % 12,
          stage: Math.min(4, product.stage + speed * 0.0002)
        })).filter(p => p.stage < 4.5)
      );

      if (Math.random() < 0.03 && glassProducts.length < 8) {
        const qualityScore = quality * (1 - Math.random() * defectLevel);
        setGlassProducts(prev => [...prev, {
          id: Date.now(),
          position: 0,
          quality: qualityScore,
          stage: 0
        }]);
      }
    }
  });

  const getGlassProductColor = (quality: number) => {
    if (quality > 0.95) return '#00ff88';
    if (quality > 0.9) return '#00ccff';
    if (quality > 0.85) return '#ffaa00';
    return '#ff4444';
  };

  return (
    <group position={[10, 1, 0]}>
      <DreiBox args={[12, 0.2, 2]} position={[0, 0.1, 0]} receiveShadow>
        <meshStandardMaterial color="#333333" metalness={0.6} roughness={0.4} />
      </DreiBox>

      <group ref={beltRef}>
        <DreiBox args={[11.5, 0.05, 1.8]} position={[0, 0.15, 0]} castShadow>
          <meshStandardMaterial 
            color="#555555"
            emissive="#666666"
            emissiveIntensity={0.1}
          />
        </DreiBox>
      </group>

      {Array.from({ length: 4 }).map((_, i) => (
        <group key={i} position={[-5 + i * 3.5, 1, 0]}>
          <Cylinder args={[0.6, 0.6, 0.3]} castShadow>
            <meshStandardMaterial
              color={moldTemp > 350 ? '#ff4444' : '#4488ff'}
              emissive={moldTemp > 350 ? '#ff4444' : '#4488ff'}
              emissiveIntensity={0.3}
            />
          </Cylinder>
          <Text
            position={[0, 1.2, 0]}
            fontSize={0.25}
            color="white"
            anchorX="center"
          >
            {moldTemp}°C
          </Text>
        </group>
      ))}

      {glassProducts.map((product) => (
        <group key={product.id} position={[product.position - 6, 0.4, 0]}>
          <DreiBox args={[0.8, 0.4 + product.stage * 0.2, 0.8]} castShadow>
            <meshStandardMaterial
              color={getGlassProductColor(product.quality)}
              emissive={getGlassProductColor(product.quality)}
              emissiveIntensity={0.2}
              transparent
              opacity={0.9}
              roughness={0.1}
              metalness={0.8}
            />
          </DreiBox>
          {product.quality < 0.9 && (
            <Text
              position={[0, 0.8, 0]}
              fontSize={0.2}
              color="#ff4444"
              anchorX="center"
            >
              ⚠
            </Text>
          )}
        </group>
      ))}

      <Html position={[0, 2.5, 0]} center>
        <Card sx={{
          bgcolor: 'rgba(0, 0, 0, 0.9)',
          backdropFilter: 'blur(15px)',
          border: '2px solid #4488ff',
          minWidth: 180,
        }}>
          <CardContent sx={{ p: 1.5 }}>
            <Stack spacing={1} alignItems="center">
              <Typography variant="body1" sx={{ color: '#fff', fontWeight: 600 }}>
                {speed} м/мин
              </Typography>
              <Typography variant="caption" sx={{ color: '#aaa' }}>
                Качество: {(quality * 100).toFixed(1)}%
              </Typography>
              <LinearProgress 
                variant="determinate" 
                value={quality * 100}
                sx={{
                  width: '100%',
                  height: 4,
                  borderRadius: 2,
                  bgcolor: 'rgba(255,255,255,0.1)',
                  '& .MuiLinearProgress-bar': {
                    bgcolor: quality > 0.95 ? '#00ff88' : 
                            quality > 0.9 ? '#00ccff' : 
                            quality > 0.85 ? '#ffaa00' : '#ff4444'
                  }
                }}
              />
            </Stack>
          </CardContent>
        </Card>
      </Html>
    </group>
  );
};

// Визуализация дефектов
const PhysicsDefects = ({ defects, temperature }: { defects: Record<string, number>, temperature: number }) => {
  const defectsRef = useRef<THREE.Group>(null);
  const [defectParticles, setDefectParticles] = useState<
    Array<{
      id: number;
      type: string;
      position: [number, number, number];
      velocity: [number, number, number];
      size: number;
      lifetime: number;
    }>
  >([]);

  useEffect(() => {
    const particles: typeof defectParticles = [];
    Object.entries(defects).forEach(([type, severity]) => {
      const count = Math.floor(severity * 15);
      for (let i = 0; i < count; i++) {
        particles.push({
          id: Date.now() + Math.random(),
          type,
          position: [
            (Math.random() - 0.5) * 20,
            1 + Math.random() * 4,
            (Math.random() - 0.5) * 15
          ] as [number, number, number],
          velocity: [
            (Math.random() - 0.5) * 0.2,
            Math.random() * 0.1,
            (Math.random() - 0.5) * 0.2
          ] as [number, number, number],
          size: 0.1 + severity * 0.3,
          lifetime: 100
        });
      }
    });
    setDefectParticles(particles);
  }, [defects]);

  useFrame(() => {
    setDefectParticles(prev => 
      prev.map(p => ({
        ...p,
        position: [
          p.position[0] + p.velocity[0],
          Math.max(0.5, p.position[1] + p.velocity[1]),
          p.position[2] + p.velocity[2]
        ],
        velocity: [
          p.velocity[0] * 0.98,
          p.velocity[1] - 0.005,
          p.velocity[2] * 0.98
        ],
        lifetime: p.lifetime - 1
      })).filter(p => p.lifetime > 0 && p.position[1] > 0.5)
    );

    if (defectsRef.current) {
      defectsRef.current.rotation.y += 0.002;
    }
  });

  const getDefectConfig = (type: string) => {
    const configs: Record<string, { color: string, icon: string }> = {
      crack: { color: '#ff4444', icon: '⚡' },
      bubble: { color: '#ffaa00', icon: '○' },
      chip: { color: '#4488ff', icon: '✖' },
      cloudiness: { color: '#cccccc', icon: '☁' },
      deformation: { color: '#aa44ff', icon: '≈' },
      stress: { color: '#ff44aa', icon: '⚠' }
    };
    return configs[type] || { color: '#ffffff', icon: '?' };
  };

  return (
    <group ref={defectsRef}>
      {defectParticles.map((particle) => {
        const config = getDefectConfig(particle.type);
        return (
          <group key={particle.id} position={particle.position}>
            <Sphere args={[particle.size]}>
              <meshBasicMaterial
                color={config.color}
                emissive={config.color}
                emissiveIntensity={0.5}
                transparent
                opacity={particle.lifetime / 100}
              />
            </Sphere>
            <Text
              position={[0, particle.size + 0.1, 0]}
              fontSize={particle.size * 2}
              color={config.color}
              anchorX="center"
              anchorY="middle"
            >
              {config.icon}
            </Text>
          </group>
        );
      })}

      {temperature > 1550 && (
        <Sphere args={[8]} position={[0, 3, 0]}>
          <meshBasicMaterial
            color="#ff3300"
            transparent
            opacity={0.1}
            side={THREE.BackSide}
          />
        </Sphere>
      )}

      <Html position={[-15, 5, 0]}>
        <Card sx={{
          bgcolor: 'rgba(0, 0, 0, 0.95)',
          backdropFilter: 'blur(25px)',
          border: '2px solid #ff4444',
          minWidth: 280,
          maxWidth: 320,
        }}>
          <CardContent>
            <Stack spacing={2}>
              <Stack direction="row" spacing={1} alignItems="center">
                <Warning sx={{ color: '#ff4444' }} />
                <Typography variant="h6" sx={{ color: '#fff', fontWeight: 700 }}>
                  Карта дефектов
                </Typography>
              </Stack>
              
              <Divider sx={{ borderColor: 'rgba(255,255,255,0.1)' }} />

              {Object.entries(defects).map(([type, severity]) => {
                const config = getDefectConfig(type);
                return (
                  <Stack key={type} spacing={1} sx={{ mb: 1 }}>
                    <Stack direction="row" justifyContent="space-between" alignItems="center">
                      <Stack direction="row" spacing={1} alignItems="center">
                        <Box sx={{
                          width: 12,
                          height: 12,
                          borderRadius: '50%',
                          bgcolor: config.color,
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center'
                        }}>
                          <Typography variant="caption" sx={{ color: '#000', fontSize: 8 }}>
                            {config.icon}
                          </Typography>
                        </Box>
                        <Typography variant="body2" sx={{ color: '#fff', textTransform: 'capitalize' }}>
                          {type}
                        </Typography>
                      </Stack>
                      <Typography variant="body2" sx={{ color: config.color, fontWeight: 600 }}>
                        {(severity * 100).toFixed(1)}%
                      </Typography>
                    </Stack>
                    <LinearProgress
                      variant="determinate"
                      value={severity * 100}
                      sx={{
                        height: 6,
                        borderRadius: 3,
                        bgcolor: 'rgba(255,255,255,0.1)',
                        '& .MuiLinearProgress-bar': {
                          bgcolor: config.color,
                          borderRadius: 3,
                        }
                      }}
                    />
                  </Stack>
                );
              })}
            </Stack>
          </CardContent>
        </Card>
      </Html>
    </group>
  );
};

// Система течения стекла
const GlassFlowSystem = ({ viscosity, temperature, flowRate = 1.0 }: { viscosity: number, temperature: number, flowRate: number }) => {
  const flowRef = useRef<THREE.Group>(null);
  const [streamlines, setStreamlines] = useState<
    Array<{
      id: number;
      points: [number, number, number][];
      age: number;
      width: number;
    }>
  >([]);

  useFrame(() => {
    if (temperature > 1400) {
      if (Math.random() < flowRate * 0.08) {
        const points = Array.from({ length: 5 }, (_, i) => [
          -8 + (i * 4),
          1.5 + Math.sin(i) * 0.3,
          (Math.random() - 0.5) * 2
        ] as [number, number, number]);
        
        setStreamlines(prev => [...prev.slice(-15), {
          id: Date.now() + Math.random(),
          points,
          age: 0,
          width: 0.05 + Math.random() * 0.1
        }]);
      }

      setStreamlines(prev => 
        prev.map(line => ({
          ...line,
          points: line.points.map(([x, y, z]) => [
            x + 0.15 * flowRate,
            y + (Math.random() - 0.5) * 0.02,
            z + (Math.random() - 0.5) * 0.01
          ]) as [number, number, number][],
          age: line.age + 0.02,
          width: line.width * 0.99
        })).filter(line => line.age < 1)
      );
    }
  });

  const getFlowColor = () => {
    if (temperature > 1600) return '#ff3300';
    if (temperature > 1500) return '#ff6600';
    if (temperature > 1400) return '#ff9900';
    return '#ffcc00';
  };

  return (
    <group ref={flowRef}>
      {streamlines.map((line) => (
        <Line
          key={line.id}
          points={line.points}
          color={getFlowColor()}
          lineWidth={line.width}
          transparent
          opacity={1 - line.age}
        />
      ))}

      <Html position={[-8, 3.5, 0]}>
        <Card sx={{
          bgcolor: 'rgba(0, 0, 0, 0.85)',
          backdropFilter: 'blur(15px)',
          border: '1px solid #00ccff',
        }}>
          <CardContent sx={{ p: 1.5 }}>
            <Stack spacing={0.5} alignItems="center">
              <WaterDrop sx={{ color: '#00ccff', fontSize: 18 }} />
              <Typography variant="body2" sx={{ color: '#fff', fontWeight: 600 }}>
                Вязкость
              </Typography>
              <Typography variant="h6" sx={{ color: '#00ccff', fontWeight: 700 }}>
                {(viscosity / 1000).toFixed(1)} кПа·с
              </Typography>
              <Typography variant="caption" sx={{ color: '#aaa' }}>
                {viscosity > 1500 ? 'Высокая' : viscosity > 1000 ? 'Средняя' : 'Низкая'}
              </Typography>
            </Stack>
          </CardContent>
        </Card>
      </Html>
    </group>
  );
};

// Главная сцена
const RevolutionScene = ({ 
  furnaceTemp, 
  moldTemp, 
  beltSpeed, 
  defects,
  viscosity = 1200,
  flowRate = 1.0,
  quality = 95.5,
  showPhysics = true
}: {
  furnaceTemp: number;
  moldTemp: number;
  beltSpeed: number;
  defects: Record<string, number>;
  viscosity: number;
  flowRate: number;
  quality: number;
  showPhysics: boolean;
}) => {
  const sceneRef = useRef<THREE.Group>(null);

  useFrame((state) => {
    if (sceneRef.current) {
      sceneRef.current.rotation.y = Math.sin(state.clock.elapsedTime * 0.05) * 0.02;
    }
  });

  return (
    <group ref={sceneRef}>
      <ambientLight intensity={0.5} color="#3344aa" />
      <pointLight position={[20, 15, 10]} intensity={1.2} color="#4488ff" castShadow />
      <pointLight position={[-20, 10, -10]} intensity={0.7} color="#ff4488" />
      <spotLight
        position={[0, 25, 0]}
        angle={0.5}
        penumbra={1}
        intensity={1.5}
        castShadow
        shadow-mapSize-width={4096}
        shadow-mapSize-height={4096}
        color="#ffffff"
      />

      <Plane 
        rotation={[-Math.PI / 2, 0, 0]} 
        position={[0, -0.5, 0]} 
        args={[50, 30]} 
        receiveShadow
      >
        <meshStandardMaterial 
          color="#1a1a2e"
          roughness={0.8}
          metalness={0.2}
          emissive="#0a0a1a"
          emissiveIntensity={0.3}
        />
      </Plane>

      <DreiBox position={[0, 8, -15]} args={[40, 12, 1]} receiveShadow>
        <meshStandardMaterial color="#0f3460" metalness={0.3} roughness={0.7} />
      </DreiBox>

      <EnhancedFurnace 
        temperature={furnaceTemp} 
        meltLevel={0.85}
        efficiency={quality / 100}
      />
      
      <SmartFormingLine 
        speed={beltSpeed}
        moldTemp={moldTemp}
        quality={quality / 100}
        defectLevel={Object.values(defects).reduce((a, b) => a + b, 0)}
      />

      {showPhysics && (
        <>
          <GlassFlowSystem 
            temperature={furnaceTemp}
            viscosity={viscosity}
            flowRate={flowRate}
          />
          <PhysicsDefects defects={defects} temperature={furnaceTemp} />
        </>
      )}

      <Html position={[0, 12, -10]} center>
        <Stack spacing={2} alignItems="center">
          <Typography variant="h3" sx={{
            color: '#fff',
            fontWeight: 900,
            textShadow: '0 0 20px rgba(0, 102, 204, 0.7)',
            background: 'linear-gradient(90deg, #0066CC, #FF6B35)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            textAlign: 'center',
            lineHeight: 1.2
          }}>
            ЦИФРОВОЙ ДВОЙНИК
            <br />
            <Typography variant="h5" component="span" sx={{ 
              color: '#aaa', 
              fontWeight: 400,
              display: 'block',
              mt: 1
            }}>
              ИНТЕЛЛЕКТУАЛЬНОЕ ПРОИЗВОДСТВО СТЕКЛА
            </Typography>
          </Typography>
          
          <Stack direction="row" spacing={2}>
            <Chip
              label="РТУ МИРЭА"
              sx={{
                bgcolor: 'rgba(0, 102, 204, 0.2)',
                color: '#0066CC',
                border: '1px solid #0066CC',
                fontWeight: 700
              }}
            />
            <Chip
              label="AI-АНАЛИТИКА"
              sx={{
                bgcolor: 'rgba(255, 107, 53, 0.2)',
                color: '#FF6B35',
                border: '1px solid #FF6B35',
                fontWeight: 700
              }}
            />
          </Stack>
        </Stack>
      </Html>

      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        maxPolarAngle={Math.PI / 1.8}
        minDistance={8}
        maxDistance={40}
        enableDamping
        dampingFactor={0.05}
      />
    </group>
  );
};

// Главный компонент Digital Twin
const DigitalTwin3D = () => {
  const { wsData, isConnected } = useWebSocketStream();
  
  const [productionState, setProductionState] = useState<ProductionState>({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    viscosity: 1200,
    flowRate: 1.0,
    pressure: 45,
    humidity: 35,
    coolingRate: 3.5,
    defects: { 
      crack: 0.12, 
      bubble: 0.08, 
      chip: 0.05, 
      cloudiness: 0.03, 
      deformation: 0.06,
      stress: 0.04
    },
    quality: 95.5,
    efficiency: 92.3,
    energyConsumption: 85.7
  });

  const [whatIfState, setWhatIfState] = useState<WhatIfState>({
    furnaceTemp: 1520,
    moldTemp: 320,
    beltSpeed: 150,
    viscosity: 1200,
    flowRate: 1.0,
    pressure: 45,
    coolingRate: 3.5
  });

  const [simulationResults, setSimulationResults] = useState<SimulationResults>({
    predictedDefects: { crack: 0.12, bubble: 0.08, chip: 0.05, cloudiness: 0.03, deformation: 0.06 },
    quality: 95.5,
    efficiency: 92.3,
    energyImpact: 0,
    riskLevel: 'LOW',
    recommendations: [],
    confidence: 0.87
  });

  const [activeMode, setActiveMode] = useState<'realtime' | 'shadow' | 'whatif'>('realtime');
  const [showPhysics, setShowPhysics] = useState(true);
  const [isSimulating, setIsSimulating] = useState(false);
  const [activePanel, setActivePanel] = useState(0);

  useEffect(() => {
    if (wsData.parameters) {
      setProductionState(prev => ({
        ...prev,
        furnaceTemp: wsData.parameters?.furnace?.temperature || prev.furnaceTemp,
        moldTemp: wsData.parameters?.forming?.mold_temp || prev.moldTemp,
        beltSpeed: wsData.parameters?.forming?.speed || prev.beltSpeed,
        pressure: wsData.parameters?.furnace?.pressure || prev.pressure,
        quality: wsData.qualityMetrics?.qualityRate || prev.quality
      }));
    }
  }, [wsData]);

  const runWhatIfSimulation = async () => {
    setIsSimulating(true);
    
    try {
      const tempFactor = Math.abs(whatIfState.furnaceTemp - 1520) / 100;
      const speedFactor = Math.abs(whatIfState.beltSpeed - 150) / 50;
      const pressureFactor = Math.abs((whatIfState.pressure || 45) - 45) / 20;
      
      const newDefects = {
        crack: Math.min(0.25, 0.08 + tempFactor * 0.5 + pressureFactor * 0.3),
        bubble: Math.min(0.2, 0.05 + tempFactor * 0.4),
        chip: Math.min(0.15, 0.04 + speedFactor * 0.6),
        cloudiness: Math.min(0.18, 0.03 + tempFactor * 0.7),
        deformation: Math.min(0.22, 0.05 + speedFactor * 0.5 + pressureFactor * 0.4)
      };

      const qualityScore = 100 - Object.values(newDefects).reduce((a, b) => a + b, 0) * 250;
      const efficiencyScore = 95 - Math.abs(whatIfState.furnaceTemp - 1520) * 0.01;
      const energyImpact = Math.abs(whatIfState.furnaceTemp - 1520) * 0.5;

      let riskLevel = 'LOW';
      const totalRisk = Object.values(newDefects).reduce((a, b) => a + b, 0);
      if (totalRisk > 0.4) riskLevel = 'HIGH';
      else if (totalRisk > 0.2) riskLevel = 'MEDIUM';

      const recommendations = [];
      if (whatIfState.furnaceTemp > 1600) 
        recommendations.push('Уменьшить температуру печи на 30°C');
      if (whatIfState.beltSpeed > 170) 
        recommendations.push('Снизить скорость линии до 160 м/мин');
      if ((whatIfState.pressure || 45) > 50) 
        recommendations.push('Скорректировать давление формования');
      if ((whatIfState.coolingRate || 3.5) > 5) 
        recommendations.push('Увеличить время отжига');

      setSimulationResults({
        predictedDefects: newDefects,
        quality: Math.max(70, qualityScore),
        efficiency: Math.max(80, efficiencyScore),
        energyImpact,
        riskLevel,
        recommendations,
        confidence: 0.85 + Math.random() * 0.1
      });

    } catch (error) {
      console.error('Simulation error:', error);
    } finally {
      setIsSimulating(false);
    }
  };

  const displayState = activeMode === 'whatif' ? 
    { ...whatIfState, defects: simulationResults.predictedDefects, quality: simulationResults.quality } : 
    productionState;

  const renderMetricsDashboard = () => (
    <Grid container spacing={2} sx={{ mb: 3 }}>
      <Grid item xs={12} md={4}>
        <Card sx={{ 
          bgcolor: 'rgba(255, 107, 53, 0.15)', 
          border: '2px solid #FF6B35',
          height: '100%'
        }}>
          <CardContent>
            <Stack spacing={2}>
              <Stack direction="row" spacing={1} alignItems="center">
                <Typography variant="h6" sx={{ color: '#fff', fontWeight: 700 }}>
                  Температура
                </Typography>
              </Stack>
              <Typography variant="h2" sx={{ color: '#FF6B35', fontWeight: 900 }}>
                {displayState.furnaceTemp}°C
              </Typography>
              <LinearProgress 
                variant="determinate" 
                value={Math.min(100, (displayState.furnaceTemp - 1300) / 5)}
                sx={{
                  height: 8,
                  borderRadius: 4,
                  bgcolor: 'rgba(255,255,255,0.1)',
                  '& .MuiLinearProgress-bar': {
                    bgcolor: '#FF6B35',
                    backgroundImage: 'linear-gradient(90deg, #FF6B35, #FF8E53)'
                  }
                }}
              />
              <Stack direction="row" justifyContent="space-between">
                <Typography variant="caption" sx={{ color: '#aaa' }}>
                  Норма: 1520°C
                </Typography>
                <Typography variant="caption" sx={{ 
                  color: displayState.furnaceTemp > 1550 ? '#ff4444' : '#00ff88',
                  fontWeight: 600
                }}>
                  {displayState.furnaceTemp > 1550 ? 'Высокая' : 'Нормальная'}
                </Typography>
              </Stack>
            </Stack>
          </CardContent>
        </Card>
      </Grid>

      <Grid item xs={12} md={4}>
        <Card sx={{ 
          bgcolor: 'rgba(0, 200, 83, 0.15)', 
          border: '2px solid #00C853',
          height: '100%'
        }}>
          <CardContent>
            <Stack spacing={2}>
              <Stack direction="row" spacing={1} alignItems="center">
                <CheckCircle sx={{ color: '#00C853' }} />
                <Typography variant="h6" sx={{ color: '#fff', fontWeight: 700 }}>
                  Качество
                </Typography>
              </Stack>
              <Typography variant="h2" sx={{ color: '#00C853', fontWeight: 900 }}>
                {displayState.quality.toFixed(1)}%
              </Typography>
              <LinearProgress 
                variant="determinate" 
                value={displayState.quality}
                sx={{
                  height: 8,
                  borderRadius: 4,
                  bgcolor: 'rgba(255,255,255,0.1)',
                  '& .MuiLinearProgress-bar': {
                    bgcolor: '#00C853',
                    backgroundImage: 'linear-gradient(90deg, #00C853, #00E676)'
                  }
                }}
              />
              <Stack direction="row" justifyContent="space-between">
                <Typography variant="caption" sx={{ color: '#aaa' }}>
                  Цель: {'>'}95%
                </Typography>
                <CheckCircle sx={{ 
                  color: displayState.quality > 95 ? '#00C853' : '#FFA726',
                  fontSize: 20 
                }} />
              </Stack>
            </Stack>
          </CardContent>
        </Card>
      </Grid>

      <Grid item xs={12} md={4}>
        <Card sx={{ 
          bgcolor: 'rgba(41, 182, 246, 0.15)', 
          border: '2px solid #29B6F6',
          height: '100%'
        }}>
          <CardContent>
            <Stack spacing={2}>
              <Stack direction="row" spacing={1} alignItems="center">
                <Speed sx={{ color: '#29B6F6' }} />
                <Typography variant="h6" sx={{ color: '#fff', fontWeight: 700 }}>
                  Производительность
                </Typography>
              </Stack>
              <Typography variant="h2" sx={{ color: '#29B6F6', fontWeight: 900 }}>
                {displayState.beltSpeed} м/мин
              </Typography>
              <LinearProgress 
                variant="determinate" 
                value={displayState.beltSpeed / 2}
                sx={{
                  height: 8,
                  borderRadius: 4,
                  bgcolor: 'rgba(255,255,255,0.1)',
                  '& .MuiLinearProgress-bar': {
                    bgcolor: '#29B6F6',
                    backgroundImage: 'linear-gradient(90deg, #29B6F6, #4FC3F7)'
                  }
                }}
              />
              <Stack direction="row" justifyContent="space-between">
                <Typography variant="caption" sx={{ color: '#aaa' }}>
                  Эффективность: {productionState.efficiency?.toFixed(1)}%
                </Typography>
                <Typography variant="caption" sx={{ color: '#29B6F6', fontWeight: 600 }}>
                  {productionState.energyConsumption?.toFixed(1)} кВт·ч
                </Typography>
              </Stack>
            </Stack>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );

  return (
    <Box sx={{ 
      width: '100vw', 
      height: '100vh', 
      bgcolor: '#0A1929',
      overflow: 'hidden',
      position: 'relative',
    }}>
      <Paper sx={{ 
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bgcolor: 'rgba(10, 25, 41, 0.95)',
        backdropFilter: 'blur(20px)',
        borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
        zIndex: 1000,
        p: 2
      }}>
        <Grid container alignItems="center" spacing={3}>
          <Grid item xs={12} md={6}>
            <Stack direction="row" spacing={2} alignItems="center">
              <Avatar sx={{ 
                bgcolor: '#0066CC',
                width: 40,
                height: 40
              }}>
                <Factory />
              </Avatar>
              <Stack>
                <Typography variant="h5" sx={{ 
                  color: '#fff',
                  fontWeight: 900,
                  background: 'linear-gradient(90deg, #0066CC, #FF6B35)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent'
                }}>
                  GlassFlow AI
                </Typography>
                <Typography variant="caption" sx={{ color: '#aaa' }}>
                  Интеллектуальный цифровой двойник производства стекла
                </Typography>
              </Stack>
            </Stack>
          </Grid>
          
          <Grid item xs={12} md={6}>
            <Stack direction="row" spacing={2} justifyContent="flex-end" alignItems="center">
              <ButtonGroup variant="contained" size="large">
                <Button
                  startIcon={<LiveTv />}
                  onClick={() => setActiveMode('realtime')}
                  sx={{ 
                    bgcolor: activeMode === 'realtime' ? '#0066CC' : 'transparent',
                    '&:hover': { bgcolor: activeMode === 'realtime' ? '#0066CC' : 'rgba(255,255,255,0.1)' }
                  }}
                >
                  Режим реального времени
                </Button>
                <Button
                  startIcon={<CompareArrows />}
                  onClick={() => setActiveMode('shadow')}
                  sx={{ 
                    bgcolor: activeMode === 'shadow' ? '#FF6B35' : 'transparent',
                    '&:hover': { bgcolor: activeMode === 'shadow' ? '#FF6B35' : 'rgba(255,255,255,0.1)' }
                  }}
                >
                  Shadow Mode
                </Button>
                <Button
                  startIcon={<Science />}
                  onClick={() => setActiveMode('whatif')}
                  sx={{ 
                    bgcolor: activeMode === 'whatif' ? '#00C853' : 'transparent',
                    '&:hover': { bgcolor: activeMode === 'whatif' ? '#00C853' : 'rgba(255,255,255,0.1)' }
                  }}
                >
                  What-If Анализ
                </Button>
              </ButtonGroup>
              
              <Tooltip title="Физическая симуляция">
                <IconButton 
                  onClick={() => setShowPhysics(!showPhysics)}
                  sx={{ 
                    color: showPhysics ? '#29B6F6' : '#666',
                    border: `1px solid ${showPhysics ? '#29B6F6' : '#444'}`
                  }}
                >
                  <Animation />
                </IconButton>
              </Tooltip>
              
              <Chip
                label={isConnected ? 'Подключено' : 'Не подключено'}
                color={isConnected ? 'success' : 'error'}
                sx={{ fontWeight: 700 }}
              />
            </Stack>
          </Grid>
        </Grid>
      </Paper>

      <Box sx={{ 
        height: 'calc(100vh - 80px)', 
        mt: '80px',
        display: 'flex',
        flexDirection: 'column'
      }}>
        <Paper sx={{ 
          flex: 1,
          position: 'relative',
          overflow: 'hidden',
          border: `3px solid ${activeMode === 'realtime' ? '#0066CC' : activeMode === 'shadow' ? '#FF6B35' : '#00C853'}`,
          borderRadius: 2,
          m: 2,
          bgcolor: '#0a0a1a'
        }}>
          <Canvas
            shadows
            camera={{ position: [25, 12, 15], fov: 50 }}
            style={{ background: 'linear-gradient(135deg, #0A1929 0%, #1a1a2e 100%)' }}
          >
            <RevolutionScene 
              furnaceTemp={displayState.furnaceTemp}
              moldTemp={displayState.moldTemp}
              beltSpeed={displayState.beltSpeed}
              defects={displayState.defects}
              viscosity={displayState.viscosity}
              flowRate={displayState.flowRate}
              quality={displayState.quality}
              showPhysics={showPhysics}
            />
          </Canvas>
        </Paper>

        <Paper sx={{ 
          m: 2, 
          p: 3,
          bgcolor: 'rgba(16, 20, 30, 0.9)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          borderRadius: 2
        }}>
          <Tabs 
            value={activePanel} 
            onChange={(_, v) => setActivePanel(v)}
            sx={{ 
              mb: 3,
              '& .MuiTab-root': { color: '#aaa' },
              '& .Mui-selected': { color: '#fff' },
              '& .MuiTabs-indicator': { bgcolor: '#0066CC' }
            }}
          >
            <Tab label="Панель управления" icon={<Dashboard />} />
            <Tab label="What-If Анализ" icon={<Psychology />} />
            <Tab label="Дефекты и качество" icon={<Warning />} />
            <Tab label="Физические параметры" icon={<Thermostat />} />
          </Tabs>

          {activePanel === 0 && renderMetricsDashboard()}

          {activePanel === 1 && (
            <Grid container spacing={3}>
              <Grid item xs={12} md={8}>
                <Card sx={{ bgcolor: 'rgba(0, 0, 0, 0.5)', p: 3 }}>
                  <Typography variant="h6" sx={{ color: '#fff', mb: 3, display: 'flex', alignItems: 'center' }}>
                    <AutoFixHigh sx={{ mr: 1, color: '#00C853' }} />
                    What-If Симулятор
                  </Typography>
                  
                  <Grid container spacing={3}>
                    <Grid item xs={12} md={6}>
                      <Stack spacing={3}>
                        <Box>
                          <Typography variant="body2" sx={{ color: '#fff', mb: 1 }}>Температура печи</Typography>
                          <Slider
                            value={whatIfState.furnaceTemp}
                            onChange={(_, v) => setWhatIfState({...whatIfState, furnaceTemp: v as number})}
                            min={1400}
                            max={1700}
                            marks={[
                              { value: 1400, label: '1400°C' },
                              { value: 1520, label: 'Оптимум' },
                              { value: 1700, label: '1700°C' }
                            ]}
                            sx={{ color: '#FF6B35' }}
                          />
                          <Typography variant="body2" sx={{ color: '#aaa', mt: 1 }}>
                            Текущее: {whatIfState.furnaceTemp}°C
                          </Typography>
                        </Box>

                        <Box>
                          <Typography variant="body2" sx={{ color: '#fff', mb: 1 }}>Скорость линии</Typography>
                          <Slider
                            value={whatIfState.beltSpeed}
                            onChange={(_, v) => setWhatIfState({...whatIfState, beltSpeed: v as number})}
                            min={100}
                            max={200}
                            marks={[
                              { value: 100, label: '100' },
                              { value: 150, label: 'Оптимум' },
                              { value: 200, label: '200' }
                            ]}
                            sx={{ color: '#29B6F6' }}
                          />
                          <Typography variant="body2" sx={{ color: '#aaa', mt: 1 }}>
                            Текущее: {whatIfState.beltSpeed} м/мин
                          </Typography>
                        </Box>
                      </Stack>
                    </Grid>

                    <Grid item xs={12} md={6}>
                      <Stack spacing={3}>
                        <Box>
                          <Typography variant="body2" sx={{ color: '#fff', mb: 1 }}>Давление формования</Typography>
                          <Slider
                            value={whatIfState.pressure || 45}
                            onChange={(_, v) => setWhatIfState({...whatIfState, pressure: v as number})}
                            min={30}
                            max={60}
                            marks={[
                              { value: 30, label: '30' },
                              { value: 45, label: 'Оптимум' },
                              { value: 60, label: '60' }
                            ]}
                            sx={{ color: '#FFD700' }}
                          />
                        </Box>

                        <Box>
                          <Typography variant="body2" sx={{ color: '#fff', mb: 1 }}>Скорость охлаждения</Typography>
                          <Slider
                            value={whatIfState.coolingRate || 3.5}
                            onChange={(_, v) => setWhatIfState({...whatIfState, coolingRate: v as number})}
                            min={1}
                            max={8}
                            step={0.5}
                            marks={[
                              { value: 1, label: '1' },
                              { value: 3.5, label: 'Оптимум' },
                              { value: 8, label: '8' }
                            ]}
                            sx={{ color: '#00CCFF' }}
                          />
                        </Box>
                      </Stack>
                    </Grid>
                  </Grid>

                  <Stack direction="row" spacing={2} sx={{ mt: 3 }}>
                    <Button
                      variant="contained"
                      startIcon={<PlayArrow />}
                      onClick={runWhatIfSimulation}
                      disabled={isSimulating}
                      sx={{
                        bgcolor: '#00C853',
                        '&:hover': { bgcolor: '#00E676' },
                        flex: 1
                      }}
                    >
                      {isSimulating ? 'Симуляция...' : 'Запустить симуляцию'}
                    </Button>
                    <Button
                      variant="outlined"
                      startIcon={<Refresh />}
                      onClick={() => setWhatIfState({
                        furnaceTemp: 1520,
                        moldTemp: 320,
                        beltSpeed: 150,
                        viscosity: 1200,
                        flowRate: 1.0,
                        pressure: 45,
                        coolingRate: 3.5
                      })}
                      sx={{
                        borderColor: '#fff',
                        color: '#fff'
                      }}
                    >
                      Сбросить
                    </Button>
                  </Stack>
                </Card>
              </Grid>

              <Grid item xs={12} md={4}>
                <Card sx={{ bgcolor: 'rgba(0, 0, 0, 0.5)', p: 3, height: '100%' }}>
                  <Typography variant="h6" sx={{ color: '#fff', mb: 3, display: 'flex', alignItems: 'center' }}>
                    <Analytics sx={{ mr: 1, color: '#FFD700' }} />
                    Результаты симуляции
                  </Typography>

                  {simulationResults.recommendations.length > 0 ? (
                    <Stack spacing={2}>
                      <Typography variant="body2" sx={{ color: simulationResults.riskLevel === 'HIGH' ? '#ff4444' : 
                                                           simulationResults.riskLevel === 'MEDIUM' ? '#ffaa00' : '#00ff88',
                                                           fontWeight: 700 }}>
                        Уровень риска: {simulationResults.riskLevel}
                      </Typography>
                      <Typography variant="caption" sx={{ color: '#aaa' }}>
                        Достоверность: {(simulationResults.confidence * 100).toFixed(1)}%
                      </Typography>

                      <Stack spacing={1}>
                        <Typography variant="body2" sx={{ color: '#fff' }}>
                          Прогноз качества:
                        </Typography>
                        <LinearProgress 
                          variant="determinate" 
                          value={simulationResults.quality}
                          sx={{
                            height: 10,
                            borderRadius: 5,
                            bgcolor: 'rgba(255,255,255,0.1)',
                            '& .MuiLinearProgress-bar': {
                              bgcolor: simulationResults.quality > 95 ? '#00C853' : 
                                      simulationResults.quality > 90 ? '#FFA726' : '#EF5350',
                              borderRadius: 5
                            }
                          }}
                        />
                        <Typography variant="body2" sx={{ 
                          color: simulationResults.quality > 95 ? '#00C853' : 
                                simulationResults.quality > 90 ? '#FFA726' : '#EF5350',
                          textAlign: 'center',
                          fontWeight: 600
                        }}>
                          {simulationResults.quality.toFixed(1)}%
                        </Typography>
                      </Stack>

                      <Divider sx={{ borderColor: 'rgba(255,255,255,0.1)' }} />

                      <Typography variant="body2" sx={{ color: '#fff', fontWeight: 600 }}>
                        Рекомендации:
                      </Typography>
                      <List dense>
                        {simulationResults.recommendations.map((rec, i) => (
                          <ListItem key={i} sx={{ py: 0.5 }}>
                            <ListItemText 
                              primary={
                                <Typography variant="caption" sx={{ color: '#aaa' }}>
                                  • {rec}
                                </Typography>
                              }
                            />
                          </ListItem>
                        ))}
                      </List>
                    </Stack>
                  ) : (
                    <Stack spacing={2} alignItems="center" justifyContent="center" sx={{ height: '100%', py: 4 }}>
                      <Science sx={{ fontSize: 48, color: '#666' }} />
                      <Typography variant="body2" sx={{ color: '#666', textAlign: 'center' }}>
                        Запустите симуляцию для получения прогноза
                      </Typography>
                    </Stack>
                  )}
                </Card>
              </Grid>
            </Grid>
          )}

          {activePanel === 2 && (
            <Card sx={{ bgcolor: 'rgba(0, 0, 0, 0.5)', p: 3 }}>
              <Typography variant="h6" sx={{ color: '#fff', mb: 3 }}>
                Анализ дефектов
              </Typography>
              <Grid container spacing={2}>
                {Object.entries(displayState.defects).map(([type, value]) => (
                  <Grid item xs={6} md={4} key={type}>
                    <Card sx={{ 
                      bgcolor: 'rgba(255,255,255,0.05)', 
                      border: '1px solid rgba(255,255,255,0.1)',
                      p: 2
                    }}>
                      <Stack spacing={1} alignItems="center">
                        <Typography variant="body2" sx={{ color: '#fff', textTransform: 'capitalize' }}>
                          {type}
                        </Typography>
                        <Typography variant="h4" sx={{ 
                          color: value > 0.15 ? '#ff4444' : 
                                 value > 0.1 ? '#ffaa00' : '#00ff88',
                          fontWeight: 700
                        }}>
                          {(value * 100).toFixed(1)}%
                        </Typography>
                        <LinearProgress 
                          variant="determinate" 
                          value={value * 100}
                          sx={{
                            width: '100%',
                            height: 8,
                            borderRadius: 4,
                            bgcolor: 'rgba(255,255,255,0.1)',
                            '& .MuiLinearProgress-bar': {
                              bgcolor: value > 0.15 ? '#ff4444' : 
                                       value > 0.1 ? '#ffaa00' : '#00ff88'
                            }
                          }}
                        />
                      </Stack>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Card>
          )}

          {activePanel === 3 && (
            <Card sx={{ bgcolor: 'rgba(0, 0, 0, 0.5)', p: 3 }}>
              <Typography variant="h6" sx={{ color: '#fff', mb: 3 }}>
                Физические параметры
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={6} md={3}>
                  <Card sx={{ bgcolor: 'rgba(255,107,53,0.1)', p: 2 }}>
                    <Typography variant="caption" sx={{ color: '#aaa' }}>Вязкость</Typography>
                    <Typography variant="h5" sx={{ color: '#FF6B35', fontWeight: 700 }}>
                      {(displayState.viscosity / 1000).toFixed(1)} кПа·с
                    </Typography>
                  </Card>
                </Grid>
                <Grid item xs={6} md={3}>
                  <Card sx={{ bgcolor: 'rgba(0,200,83,0.1)', p: 2 }}>
                    <Typography variant="caption" sx={{ color: '#aaa' }}>Давление</Typography>
                    <Typography variant="h5" sx={{ color: '#00C853', fontWeight: 700 }}>
                      {displayState.pressure} кПа
                    </Typography>
                  </Card>
                </Grid>
                <Grid item xs={6} md={3}>
                  <Card sx={{ bgcolor: 'rgba(41,182,246,0.1)', p: 2 }}>
                    <Typography variant="caption" sx={{ color: '#aaa' }}>Влажность</Typography>
                    <Typography variant="h5" sx={{ color: '#29B6F6', fontWeight: 700 }}>
                      {displayState.humidity}%
                    </Typography>
                  </Card>
                </Grid>
                <Grid item xs={6} md={3}>
                  <Card sx={{ bgcolor: 'rgba(255,167,38,0.1)', p: 2 }}>
                    <Typography variant="caption" sx={{ color: '#aaa' }}>Охлаждение</Typography>
                    <Typography variant="h5" sx={{ color: '#FFA726', fontWeight: 700 }}>
                      {displayState.coolingRate}°C/мин
                    </Typography>
                  </Card>
                </Grid>
              </Grid>
            </Card>
          )}
        </Paper>
      </Box>
    </Box>
  );
};

export default DigitalTwin3D;
